(window.webpackJsonp=window.webpackJsonp||[]).push([[966],{1321:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1844-han-la-hoc-tro-ta"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1844-han-la-hoc-tro-ta"}},[n._v("#")]),n._v(" Chương 1844: Hắn là học trò ta")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1844: Hắn là học trò ta"),t("br"),t("br"),n._v("Thần Dung Hoàng thương thế nặng, không chỉ cơ sở hủy, linh hồn đều đã bắt đầu tán loạn, vốn là không có ý định tiếp tục sống sót, nằm mơ đều không có nghĩ đến, thiếu gia vậy mà đem loại này cơ hội quý giá, ban tặng hắn, làm cho đối phương cứu người!"),t("br"),t("br"),n._v("Nếu như nói trước đó, thừa nhận đối phương là thiếu gia, là bởi vì Linh Thần nguyên nhân, mà bây giờ thật tâm phục uống xuống!"),t("br"),t("br"),n._v("Vị thiếu gia này, làm việc mặc dù có đôi khi không quá đáng tin cậy, cũng không coi trọng quy tắc, nhưng làm người, có điểm mấu chốt của mình, dù là khó khăn đi nữa, cũng sẽ không từ bỏ kiên trì!"),t("br"),t("br"),n._v("Chỉ sợ đây mới là hắn chân chính nhân cách mị lực, để vô số đệ tử, vô số thú sủng, cam tâm tình nguyện theo sau lưng, đến chết dứt khoát chân chính nguyên nhân."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Lưu Dương nắm đấm căng thẳng."),t("br"),t("br"),n._v("Đặt mình vào hoàn cảnh người khác, đổi lại là hắn, cũng sẽ không dễ dàng như thế từ bỏ Thần Linh ban cho cơ hội, cần đến cứu người... Cho nên, lão sư liền là lão sư, mới có thể để cho người phục sát đất."),t("br"),t("br"),n._v("“Có như vậy chủ nhân, là của ta chuyện may mắn!”"),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh cũng hốc mắt đỏ lên."),t("br"),t("br"),n._v("Làm Thánh thú, sợ nhất liền là nhờ vả không phải người, chủ nhân vì bản thân tính mạng, tại thú sủng không quan tâm... Cùng nhau đi tới, bản thân vị chủ nhân này, không những không có làm như vậy, ngược lại khắp nơi thay nó suy nghĩ, nhận như vậy người vì chủ, dù là thật vì đó chết rồi, cũng cam tâm tình nguyện!"),t("br"),t("br"),n._v("Ý tưởng giống nhau, xuất hiện tại Mặc Linh Cổ Thánh trong óc, đám người nhìn về phía một màn trước mắt, đều có suy nghĩ, thần thái phức tạp."),t("br"),t("br"),n._v("“Thiếu gia, ta đã không cứu nổi, khỏi cần vì ta lãng phí cơ hội...”"),t("br"),t("br"),n._v("Thân thể run rẩy, Thần Dung Hoàng giãy dụa lấy lắc đầu."),t("br"),t("br"),n._v("Thần Linh bằng lòng ra tay, đối tu luyện giả tới nói, nắm giữ dạng gì tác dụng cùng hiệu quả, hắn thân thể hấp hối, không đáng vì đó lãng phí cơ hội."),t("br"),t("br"),n._v("“Có phải hay không lãng phí, thiếu gia ta tự có quyết định!”"),t("br"),t("br"),n._v("Ngẩng đầu nhìn về phía trên không bóng người, Trương Huyền nói: “Thế nào? Linh Thần đã dặn dò ngươi, muốn xuất thủ giúp ta, ta nói lên điều yêu cầu thứ nhất, ngươi sẽ không phải liền không làm được đi!”"),t("br"),t("br"),n._v("“Hắn...”"),t("br"),t("br"),n._v("Bóng người cúi đầu nhìn một cái Thần Dung Hoàng, nhíu nhíu mày: “Ngươi xác định, là yêu cầu này?”"),t("br"),t("br"),n._v("“Xác định!”"),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Cái kia tốt...”"),t("br"),t("br"),n._v("Bóng người dừng lại một chút: “Tính mạng hắn bị hao tổn nghiêm trọng, trong này lực lượng thiếu thốn thế giới, muốn sống sót, gần như không có khả năng, trừ phi... Mang về Thượng Thương! Bất quá, không gian bích lũy chắc chắn, ta muốn dẫn người tới, cũng cần nhận quy tắc phản phệ... Như vậy đi, ta có thể đem linh hồn của hắn mang đi, nghĩ biện pháp cứu chữa, còn thân thể, liền không có biện pháp gì!”"),t("br"),t("br"),n._v("“Linh hồn cũng được!” Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Thân là vu hồn sư trong lòng của hắn rõ ràng, chỉ cần hồn phách vẫn còn, chẳng khác nào sinh mệnh vẫn còn, nhiều nhất tiêu tốn chút công phu, cọ rửa luyện chế thân thể là có thể."),t("br"),t("br"),n._v("“Tốt a!”"),t("br"),t("br"),n._v("Bóng người không nói thêm lời, tay ngọc nhẹ nhàng vồ một cái."),t("br"),t("br"),n._v("Ngồi tại nguyên chỗ không nhúc nhích Thần Dung Hoàng, đầu lâu nghiêng một cái, cắt đứt hô hấp, linh hồn ngay sau đó xuất hiện tại đối phương lòng bàn tay, bị một cỗ đặc thù lực lượng phong ấn."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Làm xong những này, bóng người hướng quang mang trên mới chậm rãi bay đi, thân ảnh càng lúc càng mờ nhạt, tựa như lúc nào cũng sẽ lần nữa phá vỡ không gian bích lũy, biến mất ở cái thế giới này."),t("br"),t("br"),n._v("Đột nhiên, ngừng lại, đầu lâu hơi nghiêng: “Ta rốt cuộc minh bạch, Linh Thần vì sao lại đối ngươi ưa thích không rời (*)... Bất quá, muốn tìm nàng, ngàn khó vạn ngăn trở, còn hi vọng ngươi có thể kiên trì...”"),t("br"),t("br"),n._v("Nói xong, càng lúc càng mờ nhạt, “Bành!” Một tiếng, biến mất tại nguyên chỗ, cũng không thấy nữa tung tích."),t("br"),t("br"),n._v("“Ta hiểu rồi...”"),t("br"),t("br"),n._v("Nhìn thấy trước mắt mất đi bóng người, Trương Huyền nắm đấm xiết chặt."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Quang mang tán đi, tế tự kết thúc."),t("br"),t("br"),n._v("Trương Huyền đứng tại chỗ, thất vọng mất mát."),t("br"),t("br"),n._v("Mặc dù lần này vẫn như cũ không được đến Lạc Nhược Hi tin tức chính xác, dĩ nhiên đã rõ ràng, muốn tới chân chính cùng một chỗ, quả thực khó khăn tầng tầng, mà chủ yếu liền là trước đột phá, phá toái hư không!"),t("br"),n._v("Chỉ có đạt tới loại cảnh giới này, mới có cơ hội, đi vào cao hơn thế giới, đi vào Thượng Thương, đi tìm trong lòng người yêu."),t("br"),t("br"),n._v("“Chủ nhân...”"),t("br"),t("br"),n._v("“Hội trưởng!”"),t("br"),t("br"),n._v("Hỏa Tư Cổ Thánh cùng Mặc Linh Cổ Thánh đi tới trước mặt."),t("br"),t("br"),n._v("Trương Huyền quay đầu."),t("br"),t("br"),n._v("“Dị Linh tộc ba đại Hoàng giả, tất cả đều tiêu vẫn, hiện tại toàn bộ hoàng thất, rắn mất đầu, vị này Dương Lưu, mặc dù được xác nhận là Dung Hoàng, nhưng tu vi cùng kinh nghiệm đều thật to không đủ, ta nhìn không bằng...”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh mắt sáng lên, trong mắt sát khí đằng đằng."),t("br"),t("br"),n._v("Vu hồn nhất mạch, nằm gai nếm mật, tiềm phục tại dị linh nhất tộc, nguyện vọng lớn nhất liền là đem hắn hủy diệt, hiện tại, thời cơ cuối cùng đã tới, bằng vào hội trưởng thực lực cùng danh vọng, ra lệnh một tiếng, cướp lấy, dễ như trở bàn tay!"),t("br"),t("br"),n._v("Không có trả lời hai người lời nói, Trương Huyền nhẹ nhàng cười một tiếng: “Lưu Dương!”"),t("br"),t("br"),n._v("Lưu Dương đi tới trước mặt, đầu gối mềm nhũn quỳ mọp xuống đất: “Đồ nhi Lưu Dương gặp qua ân sư!”"),t("br"),t("br"),n._v("“Đồ nhi? Ân sư?”"),t("br"),t("br"),n._v("Liếc mắt nhìn nhau, hai đại cổ thánh tất cả đều ngẩn ngơ."),t("br"),t("br"),n._v("Lúc nào thiếu gia (hội trưởng) đem vị này tân nhiệm Thần Dung Hoàng, thu làm đệ tử?"),t("br"),t("br"),n._v("“Tại hạ là ân sư thân truyền đệ tử, hơn một năm trước liền bái sư...”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Lưu Dương trong cơ thể công pháp khôi phục bình thường, trên người sát lục chi khí biến mất, thân cao cũng dần dần hạ thấp, rất nhanh biến thành lúc đầu bộ dáng."),t("br"),t("br"),n._v("“Ngươi, ngươi là nhân tộc?”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh trợn mắt lên."),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Lưu Dương gật đầu."),t("br"),t("br"),n._v("Tại đây không có người ngoài, khỏi cần tại tiếp tục giấu diếm."),t("br"),t("br"),n._v("Mặc Linh cùng Hỏa Tư Cổ Thánh tất cả đều bối rối."),t("br"),t("br"),n._v("Thần Dung Hoàng đủ loại đề phòng, tốn sức tâm huyết tìm đến truyền nhân, lại là hội trưởng (chủ nhân) học sinh, cái này chẳng phải tương đương, khỏi cần cướp đoạt, dị linh nhất mạch, đã rơi vào trong tay?"),t("br"),t("br"),n._v("“Tốt, Lưu Dương là của ta thân truyền, vừa mới được xác định Dung Hoàng, khẳng định có không ít phản kháng thanh âm, các ngươi hai cái giúp một tay giúp đỡ, trong một tháng, ngừng lại phản loạn, nhất thống dị linh nhất tộc, thiết lập hắn là duy nhất dị linh chi Hoàng!”"),t("br"),t("br"),n._v("Không nói thêm lời, Trương Huyền dặn dò."),t("br"),t("br"),n._v("Linh Hoàng, Tinh Hoàng mặc dù chết rồi, nhưng bọn hắn thuộc hạ, còn có không ít, các nơi còn có không ít vương giả, cũng cần tiến hành thống nhất, Lưu Dương vừa mới tiếp nhận truyền thừa, cơ sở nông cạn, tự nhiên còn cần hai vị này nâng đỡ."),t("br"),t("br"),n._v("Có hai người bọn họ tại, dị linh nhất mạch, lại không người có thể phản kháng, Lưu Dương trở thành chân chính khống chế thiên hạ dị linh chi Hoàng, chỉ là vấn đề thời gian."),t("br"),t("br"),n._v("“Chủ nhân (hội trưởng) yên tâm, chúng ta toàn lực giúp đỡ tại một bên, giúp một tay bình định!”"),t("br"),t("br"),n._v("Mặc Linh Cổ Thánh cùng Hỏa Tư Cổ Thánh đồng thời gật đầu."),t("br"),t("br"),n._v("“Đi đi, ta muốn bế quan một đoạn thời gian, các ngươi bình định náo động, chân chính khống chế dị linh nhất tộc về sau, tới này tìm ta là được!”"),t("br"),t("br"),n._v("Trương Huyền xua tay."),t("br"),t("br"),n._v("Đám người đồng thời gật đầu."),t("br"),t("br"),n._v("Đem ba đại Hoàng giả thế lực tụ tập cùng một chỗ, khẳng định khó khăn tầng tầng, có điều, bằng vào chém giết “Thần Linh” uy thế, cùng Thần Dung Hoàng trước khi chết uỷ thác, Lưu Dương thành công cũng không khó."),t("br"),t("br"),n._v("Nhìn bọn họ rời đi, Trương Huyền yên tĩnh tại đợi tại nguyên chỗ không biết qua bao lâu, thân thể nhoáng một cái, đi vào không gian gấp."),t("br"),t("br"),n._v("Hiện tại có nhàn rỗi, vừa vặn nhìn một chút có thể hay không đem vị kia “Thần Linh” thi thể, luyện chế thành Vô Hồn Kim Nhân!"),t("br"),t("br"),n._v("Thuận tiện, đem xương đùi cùng chân xương, cho Ngoan Nhân luyện hóa, hơn nữa nghĩ biện pháp để hắn lột xác ra cơ bắp, bản thân cũng tốt hiếu học tập quan sát, tranh thủ sớm ngày đạt tới loại cảnh giới này, từ đó phá toái hư không!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1855"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1857"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);