(window.webpackJsonp=window.webpackJsonp||[]).push([[968],{1323:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1846-thich-khach"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1846-thich-khach"}},[n._v("#")]),n._v(" Chương 1846: Thích khách")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1846: Thích khách"),t("br"),t("br"),n._v("Khắp nơi chư hầu, các đại vương tộc, ngày hôm nay tất cả đều tề tụ tại Dị Linh vương thành, chỉ có một cái mục đích, ăn mừng một đời mới Dung Hoàng kế vị, khắp nơi đều là cảnh tượng nhiệt náo."),t("br"),t("br"),n._v("Trên đường phố rộng rãi, thỉnh thoảng đi qua một vị khí tức tĩnh mịch siêu cấp cường giả, Thánh vực cửu trọng đứng tại phía trước, đều kìm lòng không được run lẩy bẩy, hiển nhiên đã đạt đến Đại Thánh cấp bậc."),t("br"),t("br"),n._v("Đổi lại bình thường, loại thực lực này, ngang dọc thiên địa, hầu như không ai cản nổi, mà giờ khắc này, ở đây, cũng không dám láo xược, tân nhiệm Dung Hoàng, bình định tứ hải, thống nhất Linh tộc, vô số cường giả thần phục, vạn tộc triều bái, ai cũng không biết, cái nào khách sạn, hoặc là cửa hàng bên trong, liền có khả năng ngồi một vị người người kính sợ Cổ Thánh."),t("br"),t("br"),n._v("“Muốn nói tới vị tân nhiệm Dung Hoàng, thật đúng là một vị Thần Nhân, đời trước Dung Hoàng, bị Linh Hoàng, Tinh Hoàng thông đồng nhân tộc gây thương tích, hắn đột nhiên xuất hiện, không chỉ đánh bại âm mưu của đối phương, càng đem Thượng Thương tới Tiên sứ đều một lần đánh giết, đặt vững vô địch cơ sở...”"),t("br"),t("br"),n._v("Trên đường phố, một vị người kể chuyện, tay cầm quạt xếp, sinh động như thật."),t("br"),t("br"),n._v("“Lời nói này không đúng sao? Một tháng trước vương thành đại chiến, ta nhìn rõ ràng, Dung Hoàng lúc ấy chẳng qua Đại Thánh cấp bậc, căn bản không thể nào nhúng tay, là một cái không biết tên Cổ Thánh ra tay, mới đưa Tiên sứ chém giết!”"),t("br"),t("br"),n._v("Một vị người qua đường nghi ngờ nói."),t("br"),t("br"),n._v("Đại chiến mới đi qua một tháng kế tiếp mà thôi, không ít người tận mắt nhìn thấy, cùng người kể chuyện nói tới, hoàn toàn khác biệt."),t("br"),t("br"),n._v("Cái trước, một mực tại tuyên dương mới Dung Hoàng công tích, mà trên thực tế, nhìn thấy cái kia một trận chiến đấu người, tất cả đều rõ ràng, lớn nhất công thần, cũng không phải là hắn, mà là một cái trước kia chưa hề xuất hiện qua Cổ Thánh."),t("br"),t("br"),n._v("“Vị kia không biết tên Cổ Thánh, ngươi cũng đã biết là ai?” Thấy hắn nghi ngờ, người kể chuyện cười nhìn lại."),t("br"),t("br"),n._v("“Ta... Không biết!”"),t("br"),t("br"),n._v("Người qua đường lắc đầu, chung quanh những người khác cũng đều từng cái khắp khuôn mặt là mê man."),t("br"),t("br"),n._v("“Người kia chưa nói qua tên của mình, từ ngày đó về sau, càng không xuất hiện qua, chẳng lẽ ngươi biết là ai?”"),t("br"),t("br"),n._v("Một người hỏi."),t("br"),t("br"),n._v("Mọi người đồng loạt nhìn tới."),t("br"),t("br"),n._v("Toàn bộ vương thành đều đối vị kia cao thủ thần bí, tràn đầy nghi ngờ, đều muốn biết, là từ đâu đột nhiên xuất hiện."),t("br"),t("br"),n._v("“Ta đương nhiên biết!”"),t("br"),t("br"),n._v("Nhìn thấy mọi người khẩu vị bị treo lên, người kể chuyện cười cười, vuốt râu, ngón tay không ngừng gõ phía trước cài lại chiêng đồng."),t("br"),t("br"),n._v("“Mau nói đi!”"),t("br"),t("br"),n._v("Một cái đầu mang mũ rộng vành mập mạp thân ảnh, cổ tay rung lên, mấy cái đại dung tệ rơi vào trong đó, đập ra “Leng keng!” Thanh âm."),t("br"),t("br"),n._v("Nhìn thấy có tiền, người kể chuyện ánh mắt sáng lên, vội vàng đem đại dung tệ thu vào ống tay áo, lúc này mới lần nữa lắc lư một cái quạt xếp, nhẹ nhàng cười một tiếng, hạ giọng: “Nghe nói, vị này thần bí Cổ Thánh, là Dung Hoàng bệ hạ thân truyền ân sư, một vị ẩn sĩ cao nhân, Dung Hoàng bệ hạ, chính là tại bọn họ bên dưới tu luyện, mới có thành tựu như thế, đột nhiên xuất hiện, một lần bình định thiên hạ! Không chỉ có như vậy, nghe nói, vị này còn thuần phục Hỏa Tư Cổ Thánh, để hắn trở thành thú sủng, càng làm cho Mặc Linh Cổ Thánh trở nên thần phục, mới để cho bệ hạ, đạt được hai người ủng hộ...”"),t("br"),t("br"),n._v("“Dung Hoàng bệ hạ lão sư?”"),t("br"),t("br"),n._v("“Cái này khoác lác a? Dung Hoàng bệ hạ đạt được đời trước Dung Hoàng truyền thừa, chẳng lẽ hắn ân sư, không phải đời trước Dung Hoàng?”"),t("br"),t("br"),n._v("Có người nghi ngờ."),t("br"),t("br"),n._v("“Dĩ nhiên không phải, đời trước Dung Hoàng, là rất cường đại, để vô số người bội phục, nhưng còn chưa đủ làm mới Dung Hoàng lão sư tư cách! Ta còn nghe nói... Lão Dung Hoàng, nhìn thấy hắn vị lão sư này, đều muốn khom người xưng hô một tiếng thiếu gia!”"),t("br"),t("br"),n._v("Người kể chuyện nói."),t("br"),t("br"),n._v("“Lão Dung Hoàng xưng hô thiếu gia? Vượt thổi càng lợi hại đi! Chẳng lẽ, Dung Hoàng vị lão sư này, là Thần Linh?”"),t("br"),t("br"),n._v("Đầu đội mũ rộng vành mập mạp thân ảnh cười nhạo."),t("br"),t("br"),n._v("“Đương nhiên là Thần Linh, nếu như không phải Thần Linh, làm sao có thể đem Thượng Thương tới Tiên sứ ung dung chém giết?” Người kể chuyện hừ lạnh: “Vài vạn năm đến, loại trừ nhân tộc Khổng sư, tộc ta Ngoan Nhân tiền bối, các ngươi nghe nói qua ai, có loại lực lượng này?”"),t("br"),t("br"),n._v("Tất cả mọi người yên lặng."),t("br"),n._v(" Thượng Thương Tiên sứ, chỉ nghe tên, liền biết là vô địch biểu tượng, dựa vào bản thân lực lượng chém giết, vài vạn năm đến, cũng chỉ có trong truyền thuyết hai vị."),t("br"),t("br"),n._v("Hiện tại đột nhiên toát ra Cổ Thánh, tuỳ tiện đem hắn giết chết, muốn nói không phải Thần Linh, đều bị người không thể tin tưởng."),t("br"),t("br"),n._v("“Dung Hoàng bệ hạ, là Thần Linh thân truyền, lúc này mới có thể khắc kế đại thống, vâng mệnh trời, để vạn tộc tới chúc, đây là ta Linh tộc nhất mạch chuyện may mắn, cũng là chúng ta đáng giá kiêu ngạo sự tình!”"),t("br"),t("br"),n._v("Người kể chuyện càng nói càng kích động."),t("br"),t("br"),n._v("“Đúng vậy a, có thể cùng Dung Hoàng cùng thế hệ, là vinh hạnh của ta!”"),t("br"),t("br"),n._v("“Chúng ta Linh tộc, về sau sẽ càng ngày càng cường đại...”"),t("br"),t("br"),n._v("“Trời giúp tộc ta...”"),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Mọi người tất cả đều tràn đầy kích động."),t("br"),t("br"),n._v("“Hừ! Cái gì Thần Linh, ta nhìn chính là mê hoặc nhân tâm, cố ý làm cái gì thần quyền thiên bẩm, tới lừa gạt...”"),t("br"),t("br"),n._v("Đầu đội áo choàng mập mạp khóe miệng cong lên, đang muốn tiếp tục nói chuyện, liền cảm thấy sau lưng xiết chặt, bị người kéo thoáng cái."),t("br"),t("br"),n._v("Lông mày nhíu một cái, quay đầu nhìn lại, là cái người mặc áo tơi cao gầy nữ tử: “Trở về!”"),t("br"),t("br"),n._v("“Ta là đi ra hỏi thăm tin tức, thật không phải là tìm đồ ăn...”"),t("br"),t("br"),n._v("Sửng sốt một chút, mập mạp nhét vào trong miệng một cái cục thịt, đang muốn nói cái gì, liền nghe đến nữ tử thanh âm bên trong mang theo băng lãnh: “Sư tỷ để cho ta gọi ngươi trở về, không nghe mà nói, có thể tiếp tục đợi ở đây!”"),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Run rụt lại, mập mạp tựa như lập tức đổi khuôn mặt: “Ha ha, ngươi nói gì vậy, sư tỷ dặn dò, ta nào dám không theo, đi mau, đừng để sư tỷ chờ sốt ruột...”"),t("br"),t("br"),n._v("Chẳng muốn nói nhảm, nữ tử đi đầu dẫn đường, nhị nhân chuyển mấy lần cong, đi tới một cái tiểu viện, đẩy cửa đi vào."),t("br"),t("br"),n._v("Chỉ chốc lát đi tới một cái phòng kín mít, bốn phía đều bị dùng thủ đoạn đặc thù phong ấn, thanh âm thậm chí thần thức đều không thể truyền lại."),t("br"),t("br"),n._v("Trong phòng ngồi ngay thẳng mấy người, nhìn hình dáng lại không phải Dị Linh tộc, mà là nhân tộc."),t("br"),t("br"),n._v("“Ha ha, ta ra ngoài hỏi thăm tin tức...”"),t("br"),t("br"),n._v("Nhìn thấy ở chính giữa, lông mày nhăn lại nữ tử, trong ánh mắt mang theo tiêu sát, mập mạp khó xử vò đầu."),t("br"),t("br"),n._v("“Ngươi thăm dò được tin tức gì? Không ngại nói một chút...”"),t("br"),t("br"),n._v("Một bên thanh niên mỉm cười, phía sau hắn một thanh trường thương, nghiêng dựa vào trên vách tường, thương mang mơ hồ, mang theo hàn quang."),t("br"),t("br"),n._v("“Cái này... Tân nhiệm Dung Hoàng, quả thực là một nhân tài, không chỉ đã bình định thiên hạ, còn làm ra cái thần quyền thiên bẩm mánh lới, để người kể chuyện tuyên dương khắp chốn, hiện tại hầu như toàn bộ vương thành người, thậm chí toàn bộ Dị Linh tộc, đều cho rằng hắn là Thần Linh phái xuống cứu tộc nhân thiên mệnh chi tử, cả đám đều bội phục cực kỳ!”"),t("br"),t("br"),n._v("Suy nghĩ một chút, mập mạp nói."),t("br"),t("br"),n._v("“Chuyện này ta cũng nghe nói, vị này tân nhiệm Dung Hoàng là một nhân tài, Dị Linh nhất mạch, tại dưới sự hướng dẫn của hắn, nhất định càng thêm huy hoàng, nhân tộc sợ rằng sẽ rơi vào nguy cơ!”"),t("br"),t("br"),n._v("Thanh niên lông mày cũng nhíu lại, quay đầu nhìn về phía trung tâm nữ tử: “Sư tỷ, chúng ta bây giờ nên làm như thế nào?”"),t("br"),t("br"),n._v("“Vị này tân nhiệm Dung Hoàng vừa mới kế vị, muốn làm cho tất cả mọi người quy tâm, tự nhiên cần đủ loại tuyên dương, làm ra thanh thế như vậy, cũng là bình thường!”"),t("br"),t("br"),n._v("Trung tâm nữ tử, ngón tay gõ bàn, đôi mi thanh tú nhíu lên, một lát sau: “Bất quá, Trịnh Dương nói rất đúng, mặc cho đi xuống, Linh tộc triệt để nhất thống, nhân tộc quả thực muốn rơi vào nguy hiểm, vì lẽ đó... Chúng ta muốn trước ra tay vì mạnh! Thừa dịp hắn kế vị đại điển, xuất hiện hỗn loạn thời điểm, đem ám sát!”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1857"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1859"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);