(window.webpackJsonp=window.webpackJsonp||[]).push([[969],{1322:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1847-moi-nguoi-ke-hoach"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1847-moi-nguoi-ke-hoach"}},[n._v("#")]),n._v(" Chương 1847: Mọi người kế hoạch")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Ám sát?”"),t("br"),t("br"),n._v("Mọi người tất cả đều vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("Kế vị đại điển, hàng trăm cặp mắt đổ dồn vào bên dưới động thủ, tương đương với đem sinh tử tại bất chấp, làm không cẩn thận liền sẽ toàn quân bị diệt."),t("br"),t("br"),n._v("“Tại sao muốn lựa chọn lúc này?”"),t("br"),t("br"),n._v("Mập mạp nhìn qua, có chút không hiểu: “Đổi lại lúc khác đánh lén, chẳng phải phần thắng lớn hơn một chút?”"),t("br"),t("br"),n._v("Kế vị đại điển, là toàn bộ Dị Linh tộc, phòng ngự nghiêm mật nhất thời điểm, lúc này động thủ, thật phù hợp?"),t("br"),t("br"),n._v("“Có ba điểm chỗ tốt!”"),t("br"),t("br"),n._v("Lần này nói chuyện không phải trung tâm sư tỷ, mà là vừa rồi dẫn hắn tới nữ tử: “Thứ nhất, kế nhiệm đại điển là Dị Linh một tộc huy hoàng nhất trang nghiêm thời điểm, lúc này thành công, đối phương thần quyền thiên bẩm nói dối liền sẽ bị vạch trần, Dị Linh tộc nhân vừa mới ngưng tụ nhân tâm, lại không hội tụ khả năng, thậm chí còn có thể dẫn đến khắp nơi náo động, triệt để rơi vào loạn cục!”"),t("br"),t("br"),n._v("Mập mạp gật gật đầu."),t("br"),t("br"),n._v("Đối phương đủ loại tuyên truyền bên dưới, vừa mới leo lên Thần đàn, liền bị giết, quả thực lực ảnh hưởng lớn nhất, có thể nhất để cho người ta cảm thấy khủng hoảng."),t("br"),t("br"),n._v("“Thứ hai, Dị Linh một tộc, vài vạn năm đến, đều là Tam Hoàng chấp sự, cùng khống chế thiên hạ, hiện tại biến thành một người, khẳng định mang theo cực lớn tranh luận, cứ việc đối phương nắm giữ nghiền ép tất cả thủ đoạn cùng lực lượng, có thể... Để cho người ta khó mà tin tưởng và nghe theo, vì lẽ đó, mới có đủ loại người kể chuyện xuất hiện!”"),t("br"),t("br"),n._v("Nữ tử nói tiếp: “Những người này như vậy tuyên dương, không phải là không bởi vì vị này mới Linh Hoàng, sợ trấn áp không biết cục diện? Lúc này động thủ, thoạt nhìn nguy cơ trùng trùng, có khả năng ngược lại là an toàn nhất!”"),t("br"),t("br"),n._v("“An toàn?” Mập mạp không hiểu."),t("br"),t("br"),n._v("“Những này nội tâm không phục Dị Linh tộc nhân, kế nhiệm đại điển thời điểm, khẳng định cũng tại, thấy có người đối tân nhiệm Linh Hoàng ra tay, làm không cẩn thận không những không biết hỗ trợ, còn vô cùng có khả năng làm loạn! Như vậy trải qua, ngược lại so đánh lén, càng thêm dễ dàng!”"),t("br"),t("br"),n._v("Nữ tử nói."),t("br"),t("br"),n._v("“Cái này...” Mập mạp nói không ra lời."),t("br"),t("br"),n._v("Muốn nói đối với nhân tâm lý giải, hắn cùng vị sư tỷ này kém không biết bao xa."),t("br"),t("br"),n._v("Như vậy tính toán, quả thực có khả năng."),t("br"),t("br"),n._v("Tinh Hoàng, Linh Hoàng nhất mạch người, coi như thủ lĩnh chết rồi, bị người dùng võ lực nhất thống, ở sâu trong nội tâm, khẳng định cũng không cách nào thần phục... Giờ phút này có người tới làm loạn, để hắn không cách nào kế thừa đại vị, bọn họ tự nhiên vui hưởng hắn thành."),t("br"),t("br"),n._v("“Còn thứ ba, ta tới nói đi!”"),t("br"),t("br"),n._v("Trung tâm sư tỷ, trên người tản mát ra vắng lặng khí tức, để cho người ta nhìn lên một cái, như rớt vào hầm băng: “Buổi sáng hôm nay, ta chịu trách nhiệm quan sát Mặc Linh Cổ Thánh cùng Hỏa Tư Cổ Thánh trước sáng sớm liền từ vương thành vội vàng rời đi, mất đi tung tích. Hai bọn họ là Linh tộc hiện nay người mạnh nhất, chỉ cần không tại, chúng ta cơ hội càng lớn hơn!”"),t("br"),t("br"),n._v("“Kế nhiệm đại điển đến, hai người này làm thủ hộ giả, vì sao đột nhiên rời đi?”"),t("br"),t("br"),n._v("Mập mạp kỳ lạ."),t("br"),t("br"),n._v("“Nguyên nhân cụ thể ta cũng không rõ ràng, có điều, có lẽ hẳn là đi trấn áp cái khác phản loạn, để kế vị đại điển càng thêm thuận lợi...”"),t("br"),t("br"),n._v("Hừ một tiếng, sư tỷ tiếp tục nói: “Hơn nữa, kế nhiệm đại điển, ta đặc biệt hỏi thăm, có một cái tế tự tiên tổ phân đoạn, cái này phân đoạn bên trong, tân nhiệm Linh Hoàng muốn lắng nghe tổ tông dạy bảo, thần thức không thể lan tràn ra phía ngoài, cũng không thể vận chuyển chân khí... Là thực lực thời khắc yếu đuối nhất, chỉ cần động tác nhanh, hoàn toàn có thể một lần thành công!”"),t("br"),t("br"),n._v("“Đã như vậy... Sư tỷ an bài là được, ta tất cả tất cả nghe theo ngươi!”"),t("br"),t("br"),n._v("Nghe xong phân tích, mập mạp gật đầu."),t("br"),n._v(" “Vậy là tốt rồi, đã xác định, chúng ta liền chuẩn bị động thủ!”"),t("br"),t("br"),n._v("Sư tỷ đứng dậy hai tay chắp sau lưng: “Ta biết các ngươi nghĩ cái gì... Lão sư bị Chư Tử bách gia bức bách, nói hắn cùng Dị Linh chi thần có liên quan... Vì rửa sạch khuất nhục, đoạn tuyệt tại Danh Sư đường... Chúng ta đều biết đây chỉ là che giấu tai mắt người thủ đoạn, trên thực tế là đi tới Dị Linh một tộc! Nhưng... Thương thế hắn quá nặng, trong thời gian ngắn như vậy khẳng định khó khôi phục, hiện tại mới Dung Hoàng kế vị, Dị Linh tộc nhất thống, chỉ ở sớm tối... Về thời gian đã tới không kịp tiếp tục tìm kiếm! Chúng ta phải dùng tốc độ nhanh nhất, đem vị này tân nhiệm Hoàng giả chém giết!”"),t("br"),t("br"),n._v("“Chỉ có như vậy, mới có thể rửa sạch lão sư nhận khuất nhục, để Danh Sư đường cùng người trong thiên hạ đều biết, lão sư một lòng cùng Dị Linh chống lại, chưa bao giờ thay đổi! Cũng có thể để chúng ta thanh danh truyền bá ra, để lão sư tìm tới chúng ta.”"),t("br"),t("br"),n._v("Đã không biết lão sư ở đâu, vậy chúng ta liền đứng tại chói mắt nhất địa phương, để lão sư thấy được."),t("br"),t("br"),n._v("“Chúng ta rõ ràng!”"),t("br"),t("br"),n._v("Mấy cái này, chính là Trương Huyền sáu đại thân truyền."),t("br"),t("br"),n._v("Triệu Nhã, Vương Dĩnh, Trịnh Dương, Ngụy Như Yên, Viên Đào cùng Lộ Xung."),t("br"),t("br"),n._v("Lão sư đoạn tuyệt tại Danh Sư đường, đã qua đã hơn hai tháng, sợ lão sư xảy ra chuyện, một tháng trước, bọn họ cũng giống như những người khác, lẫn vào nơi này, khắp nơi tìm hiểu, chỉ bất quá, thủy chung không được đến lão sư tin tức."),t("br"),t("br"),n._v("Giờ phút này rõ ràng mới Dung Hoàng sắp kế vị, lại không động thủ, đã tới không kịp, lúc này mới tập hợp ở đây, thảo luận biện pháp này."),t("br"),t("br"),n._v("“Đã rõ ràng, vậy bây giờ liền kiểm lại một chút thực lực!”"),t("br"),t("br"),n._v("Triệu Nhã gật gật đầu, nhìn qua: “Lão sư rời đi về sau, Trịnh Dương đem bản thân nhốt vào Xuân Thu điện bên trong tu luyện, hiện tại đã đạt đến Cổ Thánh một tầng, huyết mạch kéo dài đỉnh phong, sức chiến đấu so với nhị trọng Chư Tử bách gia đều không hề yếu, điểm ấy nói không sai chứ!”"),t("br"),t("br"),n._v("“Vâng!” Trịnh Dương lên tiếng."),t("br"),t("br"),n._v("Lão sư ở trước mặt tự sát, cả người hắn đều sắp muốn điên rồi, đem bản thân nhốt vào Xuân Thu điện, điên cuồng tu luyện, bản thân cố gắng thêm vào thiên phú và cơ duyên, vậy mà một lần thành công, đột phá Cổ Thánh cấp bậc, lại thêm tiếp cận trong điện, tiếp cận một năm củng cố, sớm đã đạt đến huyết mạch kéo dài đỉnh phong, bất cứ lúc nào cũng sẽ đột phá!"),t("br"),t("br"),n._v("Sức chiến đấu càng là mạnh mẽ vô song, so với bình thường nhị trọng cường giả đều không hề yếu."),t("br"),t("br"),n._v("“Ngươi cùng ta chủ động xuất kích!” Nói xong, Triệu Nhã nhìn về phía cách đó không xa một cô gái khác: “Ngụy Như Yên cũng đột phá Cổ Thánh cấp bậc, tuy là không có Lộ Xung sức chiến đấu mạnh như vậy, nhưng cũng không kém, đặc biệt là dùng độc, càng thêm xuất thần nhập hóa, Chư Tử bách gia cường giả gặp lên, cũng sẽ tuỳ tiện bị độc chết!”"),t("br"),t("br"),n._v("Cô gái này một mực không lên tiếng, thần tình lạnh nhạt."),t("br"),t("br"),n._v("Chính là Trương Huyền vị kia nắm giữ Tiên Thiên độc thể học sinh, Ngụy Như Yên."),t("br"),t("br"),n._v("Tuy là không nói lời nào, nhưng người người đều biết, chân chính chiến đấu, trong mấy người tuyệt đối là kinh khủng nhất."),t("br"),t("br"),n._v("“Ừm, ta chịu trách nhiệm kiềm chế những người khác, ai dám xông lại, trực tiếp hạ độc chết, thực tế không được, độc diệt toàn thành...”"),t("br"),t("br"),n._v("Ngụy Như Yên trên mặt không có chút nào sóng lớn, đặc biệt là nói đến độc diệt toàn thành, trên mặt biểu lộ cùng hạ độc chết một con kiến đồng dạng, không có bất kỳ cái gì cảm xúc bên trên chấn động."),t("br"),t("br"),n._v("Nàng là Tiên Thiên độc thể, được đến lão sư truyền thừa cùng Bồ Đề quả, tâm cảnh đột phá bên dưới, cũng cùng một tháng trước, thành công đột phá đến Cổ Thánh cấp bậc, cứ việc không bằng Trịnh Dương cường đại như vậy, nhưng phối hợp ra bên trong thân thể khí độc, tuyệt đối là quần công tai nạn."),t("br"),t("br"),n._v("Chỉ cần dám xông lại, nhiều hơn nữa người đều chống lại không được."),t("br"),t("br"),n._v("“Không sai, ngươi chịu trách nhiệm kiềm chế, còn Viên Đào, chịu trách nhiệm phòng ngự... Đúng, của ngươi lần thứ hai Cổ Thánh kiếp, đến cùng lúc nào có thể tới?”"),t("br"),t("br"),n._v("Triệu Nhã quay đầu nhìn về phía cách đó không xa mập mạp, nhíu nhíu mày."),t("br"),t("br"),n._v("Cái tên này lần trước tại Khổng miếu, xung kích qua một lần Cổ Thánh cấp bậc, cưỡng ép bị áp chế xuống dưới, lần thứ hai, đến bây giờ cũng không có xuất hiện, nói cách khác... Tiềm lực của hắn tuy là rất lớn, nhưng đến hiện nay, vẫn như cũ không có đột phá Cổ Thánh!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1858"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1860"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);