(window.webpackJsonp=window.webpackJsonp||[]).push([[974],{1329:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1851-truong-huyen-day-hoc-tro"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1851-truong-huyen-day-hoc-tro"}},[n._v("#")]),n._v(" Chương 1851: Trương Huyền dạy học trò")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1851: Trương Huyền dạy học trò"),t("br"),t("br"),n._v("“Nói đi, ta bình thường thế nào giáo dục các ngươi?”"),t("br"),t("br"),n._v("Nhìn phía trước quỳ thành một hàng mọi người, Trương Huyền nhịn không được lửa giận trong lòng."),t("br"),t("br"),n._v("Tu vi đạt tới Cổ Thánh, muốn tiến thêm một bước, cần đối lực lượng, tâm tính, tu vi... Không ngừng rèn luyện, sức chiến đấu đạt tới vô cùng thống nhất, mới có cơ hội xung kích phá toái hư không, đi vào vị diện cao hơn."),t("br"),t("br"),n._v("Đám người kia, không có chút nào áp chế, trực tiếp trùng kích Cổ Thánh, mấu chốt nhất là, sáu người liên thủ đánh lâu như vậy, đều suýt chút nữa không có vượt qua Lưu Dương... Làm cho cái gì ngoạn ý?"),t("br"),t("br"),n._v("Cái này mẹ nó cũng gọi phối hợp?"),t("br"),t("br"),n._v("Ta liền xem như dạy một đám heo, cũng không đến mức đần như vậy a?"),t("br"),t("br"),n._v("“Lão sư dạy dỗ chúng ta, trung nghĩa trong lòng...” Trịnh Dương nhỏ giọng."),t("br"),t("br"),n._v("“Đánh rắm! Ta nói chính là phối hợp, có phải hay không cho rằng các ngươi hiện tại cũng trở thành Cổ Thánh, đuôi có thể vểnh đến trên trời?”"),t("br"),t("br"),n._v("Trương Huyền xoay đầu lại, nhìn về phía cách đó không xa Lộ Xung: “Ngươi tu vi cao nhất, dùng ngươi công kích mạnh nhất tới đối phó ta!”"),t("br"),t("br"),n._v("“Ta?” Lộ Xung ngẩn ngơ: “Lão sư mới Bất Hủ cảnh đại viên mãn...”"),t("br"),t("br"),n._v("Hắn đều tích huyết trùng sinh cảnh giới, lão sư mới Bất Hủ cảnh đại viên mãn, để hắn ra tay, Một khi đem lão sư tổn thương..."),t("br"),t("br"),n._v("“Làm sao? Cho rằng đã có thể xuất sư? Xem thường lão sư?”"),t("br"),t("br"),n._v("Trương Huyền sầm mặt lại."),t("br"),t("br"),n._v("“Không dám...” Lộ Xung liền vội vàng lắc đầu."),t("br"),t("br"),n._v("“Không dám cũng đừng nói nhảm, động thủ!”"),t("br"),t("br"),n._v("Lông mày giương lên, Trương Huyền quát tháo."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Lộ Xung cắn răng một cái, vu hồn từ mi tâm nhảy ra, thẳng tắp hướng Trương Huyền lao đến."),t("br"),t("br"),n._v("Sóng khí cuồn cuộn, lực lượng tựa như cuốn lên cuồng phong."),t("br"),t("br"),n._v("Triệu Nhã đám người, toàn bộ đồng loạt nhìn qua, tiện tay đều sẽ ra tay, sợ lão sư bị thương."),t("br"),t("br"),n._v("Lão sư năng lực cùng thiên phú, bọn họ bội phục, nhưng Bất Hủ cảnh cùng tích huyết trùng sinh, chênh lệch thực tế quá xa, ngộ nhỡ bởi vậy để hắn bị thương, thật sự trăm chết không đền được tội."),t("br"),t("br"),n._v("Mọi người ở đây vì lão sư mà lo lắng thời điểm, Một mực không nhúc nhích lão sư, lật bàn tay một cái, nhìn cũng không nhìn, đánh ra."),t("br"),t("br"),n._v("Lạch cạch!"),t("br"),t("br"),n._v("Lộ Xung còn không có phản ứng lại, to lớn vu hồn liền kề sát mặt đất, giống như là bị cự thạch ngàn cân ép lại, khẽ động cũng không thể động."),t("br"),t("br"),n._v("“Đây là ngươi cái gọi là tích huyết trùng sinh?” Trương Huyền lông mày nâng lên."),t("br"),t("br"),n._v("“Ta...” Lộ Xung vẻ mặt đỏ bừng, nói không ra lời."),t("br"),t("br"),n._v("Những người khác cũng từng cái, mở to hai mắt nhìn, khắp khuôn mặt là khó tin."),t("br"),t("br"),n._v("Lộ Xung thực lực, trong lòng bọn họ vô cùng rõ ràng, trong mấy người mạnh nhất, nhưng tại lão sư trong tay, thậm chí ngay cả một bàn tay đều không có nhận lấy."),t("br"),t("br"),n._v("Lão sư thật chỉ là Bất Hủ cảnh đại viên mãn, liền nửa bước Cổ Thánh cũng không phải?"),t("br"),t("br"),n._v("“Tốt, đừng nói nhảm, cùng lên đi, tựa như vừa rồi hợp kích Thần Dung Hoàng chiêu số!”"),t("br"),t("br"),n._v("Bàn tay buông lỏng, thả ra đối Lộ Xung áp chế, Trương Huyền khẽ nói."),t("br"),t("br"),n._v("“Vâng...”"),t("br"),t("br"),n._v("Triệu Nhã cùng Trịnh Dương đám người liếc mắt nhìn nhau, đồng thời gật gật đầu."),t("br"),t("br"),n._v("Nếu như lúc trước còn lo lắng lão sư thực lực thấp, bây giờ thấy hắn vừa ra tay, liền đem Lộ Xung đánh, không có chút nào sức phản kháng, trong lòng lại không lo nghĩ."),t("br"),t("br"),n._v("Lão sư vẫn là trước kia lão sư kia, chỉ điểm bọn họ tu vi, để cho bọn họ trưởng thành, không có chút nào thay đổi."),t("br"),t("br"),n._v("Sáu người lần nữa liên hợp cùng một chỗ, khí tức ngưng tụ cùng một chỗ, tựa như giang hải dòng nước xiết."),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Triệu Nhã đi đầu ra tay, kiếm khí ngang dọc, sức mạnh mạnh mẽ như là gió lốc bạo cuốn, giữa trời mà xuống, còn lại năm vị sư đệ sư muội, vừa vặn phối hợp, để công kích của nàng trở nên càng hung hiểm hơn."),t("br"),t("br"),n._v("Đưa thân vào loại công kích này bên trong, Trương Huyền phảng phất là một cái, đứng sừng sững trong hải dương hải đăng, bất cứ lúc nào cũng sẽ bị cuồng bạo sóng biển nuốt mất."),t("br"),t("br"),n._v("Do người ngoài nhìn tới, cái này hoàn toàn là một hồi không công bằng quyết đấu."),t("br"),t("br"),n._v("Trên mặt không có bất kỳ cái gì biểu lộ, Trương Huyền đột nhiên hướng về phía trước."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Bầu trời phong, dừng lại, sóng lớn trở nên ngoan ngoãn, Triệu Nhã, Trịnh Dương đám người đồng loạt lui về phía sau mấy bước, một cái cá thể nội khí máu sôi trào, khuôn mặt trắng bệch."),t("br"),t("br"),n._v("“Đây là các ngươi cái thứ nhất thiếu hụt!”"),t("br"),t("br"),n._v("Trương Huyền chậm rãi hướng về phía trước, nhìn quanh một vòng: “Tiếp tục!”"),t("br"),t("br"),n._v("Mọi người lần nữa xông tới, còn chưa tới trước mặt, kèm theo Trương Huyền cong ngón tay búng một cái, lần nữa đồng loạt lui về phía sau, lần này bị thương nặng hơn."),t("br"),t("br"),n._v("“Tiếp tục!”"),t("br"),t("br"),n._v("Mọi người ở đây có chút không kiên trì nổi thời điểm, nghe được lão sư sinh khí thanh âm bỗng nhiên vang lên."),t("br"),t("br"),n._v("Cắn cắn răng, đành phải tiếp tục hướng phía trước."),t("br"),t("br"),n._v("Thình thịch thình thịch!"),t("br"),t("br"),n._v("Mỗi lần tiến lên không được hai cái hô hấp liền sẽ bị ung dung đánh trở về, mỗi người thương thế càng ngày càng nặng, đều sắp sắp không kiên trì được nữa."),t("br"),t("br"),n._v("Liên tục tiến công mấy chục lần, Trương Huyền lúc này mới ngừng lại, hai tay khua, từng đạo chân khí từ đầu ngón tay bắn ra, đi vào mỗi cái học sinh trong cơ thể, đồng thời, một cái bình ngọc bay về phía trên không, vô số tinh thuần huyết dịch lực lượng, quán dũng xuống."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Hấp thu những này, tích huyết trùng sinh cảnh giới Cổ Thánh chi huyết, thương thế của mọi người, khôi phục nhanh chóng, dựa theo tình huống bình thường, tu vi cũng nhanh nhanh tăng trưởng, nhưng chẳng biết tại sao, không những không có tăng trưởng, còn bắt đầu dần dần hạ thấp."),t("br"),t("br"),n._v("Trịnh Dương Chư Tử bách gia sơ kỳ thực lực, không được mười phút đồng hồ, lần nữa trở lại huyết mạch kéo dài đỉnh phong, thậm chí còn có tiếp tục rớt xuống xu thế."),t("br"),t("br"),n._v("Triệu Nhã cũng từ Cổ Thánh một tầng đỉnh phong biến thành trung kỳ..."),t("br"),t("br"),n._v("Cấp bậc tuy là hạ thấp, nhưng mà mọi người ngạc nhiên phát hiện, sức chiến đấu nhưng tăng lên rất nhiều, đặc biệt là Lộ Xung, trước hắn, đã đạt đến tích huyết trùng sinh sơ kỳ, đi qua lão sư rèn luyện, lần nữa trở lại Chư Tử bách gia cảnh giới, tu vi hạ thấp gần tới một cái đại cấp bậc, sức chiến đấu nhưng so trước đó càng thêm khủng bố."),t("br"),t("br"),n._v("Đặc biệt là hồn thể, thậm chí có thực chất hóa xu thế, Một khi hiện hình, tích huyết trùng sinh cường giả, đều khó mà phát hiện, hiện tại hình thái, chỉ là cái vu hồn."),t("br"),t("br"),n._v("“Tu luyện cũng không phải là tiến bộ càng nhanh càng tốt, cũng không phải nhiều người liền có thể phát huy ra lực lượng càng thêm cường đại, nếu như không đem cơ sở đánh vững chắc, đi được lại xa, cũng chỉ là lâu đài trên không.”"),t("br"),t("br"),n._v("Thấy mọi người dường như rõ ràng thứ gì, Trương Huyền lúc này mới hai tay chắp sau lưng, trong ánh mắt mang theo thâm ý."),t("br"),t("br"),n._v("Giáo thư dục nhân, là cho đối phương chỉ rõ một đầu càng tốt, càng thích hợp con đường của mình, bản thân những học sinh này, đã bước vào ngưỡng cửa này, nhưng mà thoát khỏi chỉ điểm của mình, vẫn là rất dễ dàng rơi vào lạc lối."),t("br"),t("br"),n._v("Giờ phút này, cũng không phải là giảng giải tu vi, cũng không phải để hắn, dựa theo con đường của mình tiến lên, mà là để cho bọn họ biết, tu luyện không phải một sớm một chiều, một lần liền thành sự tình."),t("br"),t("br"),n._v("Cần phải có đầy đủ kiên nhẫn cùng tâm cảnh."),t("br"),t("br"),n._v("Liền giống như bản thân, vây ở Bất Hủ cảnh đại viên mãn tiếp cận một tháng, đồng dạng không có xiết đột phá."),t("br"),t("br"),n._v("“Lão sư đối với các ngươi kỳ vọng, cũng không phải là Cổ Thánh, mà là muốn đi đến cao hơn, càng xa!”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua, ánh mắt ngưng trọng: “Nếu như ngay cả phá toái hư không cảnh giới đều không đạt tới, làm sao nói một mực đi theo? Đặc biệt là Lộ Xung, ngươi có nhiều như vậy hồn lực ủng hộ, lại càng dễ tích lũy, nhưng dùng tại tăng cao tu vi bên trên, thực lực phù phiếm lợi hại như thế, cũng là ta trước kia truyền thụ cho? Như ngươi loại này tình huống, Một khi đạt tới tích huyết trùng sinh đỉnh phong sau, muốn phá vỡ thế giới hàng rào, xung kích cảnh giới cao hơn, sẽ bị thiên kiếp tại chỗ đánh cho không còn sót lại một chút cặn!”"),t("br"),t("br"),n._v("Mọi người tất cả đều trên mặt vẻ xấu hổ."),t("br"),t("br"),n._v("Còn tưởng rằng toàn bộ có chỗ đột phá, lần nữa gặp được sư phụ sẽ có được khen ngợi, không nghĩ tới sẽ bị quát tháo."),t("br"),t("br"),n._v("Chẳng qua đi qua chuyện này, cũng biết lão sư một phen dụng tâm lương khổ, không trải qua một phen hàn triệt cốt, nào có Mai Hương xông vào mũi tới."),t("br"),t("br"),n._v("Chúng sinh tâm tính, đều tại lúc này, có biến hóa mới.."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1862"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1864"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);