(window.webpackJsonp=window.webpackJsonp||[]).push([[978],{1333:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1855-mac-hieu-muon-ty-thi"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1855-mac-hieu-muon-ty-thi"}},[n._v("#")]),n._v(" Chương 1855: Mạc Hiểu muốn tỷ thí")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1855: Mạc Hiểu muốn tỷ thí"),t("br"),t("br"),n._v("Xuất hiện ở trước mặt hắn, không phải người khác, chính là vị kia cùng hắn cùng nhau lớn lên hảo huynh đệ Mạc Hiểu!"),t("br"),t("br"),n._v("Lúc trước hai người đồng thời lời thề son sắt đi tới Hồng Thiên học viện, đều muốn dự thi Vương Siêu lão sư môn hạ, trở thành học viên của hắn, cuối cùng người sau thành công, mà hắn bởi vì phát huy không tốt, thi rớt, bị Trương Huyền thu làm học sinh."),t("br"),t("br"),n._v("Thoáng một cái hơn một năm, khả năng hai người ai cũng không nghĩ tới hôm nay cục diện."),t("br"),t("br"),n._v("Có thể người thành công, vẫn tại Thiên Huyền vương quốc, đối với toàn bộ đại lục tới nói, bừa bãi vô danh; Mà không thành công, ngược lại thành tựu Cổ Thánh, Chiến Sư đường đường chủ, thiên hạ có thể vượt qua, lại không mấy người."),t("br"),t("br"),n._v("Nhân sinh gặp gỡ chi khác biệt, để cho người ta thổn thức."),t("br"),t("br"),n._v("“Ngươi là...”"),t("br"),t("br"),n._v("Thấy đối phương hô lên tên của mình, còn rất quen thuộc nhẫm bộ dạng, Mạc Hiểu nhíu nhíu mày."),t("br"),t("br"),n._v("Thân là Cổ Thánh cường giả, không nguyện ý để cho người ta nhận ra, tu vi không đạt tới tương đồng cấp bậc, căn bản nhìn không ra đến cùng đến cùng dáng dấp loại nào hình dáng, vì lẽ đó, mặc dù thân là cùng nhau lớn lên đồng bọn, cũng không nhận ra được."),t("br"),t("br"),n._v("“Ta... Trịnh Dương!”"),t("br"),t("br"),n._v("Khí tức trên thân tiêu tán, lộ ra vốn dung mạo."),t("br"),t("br"),n._v("“Dương tử?”"),t("br"),t("br"),n._v("Thân thể chấn động, Mạc Hiểu hốc mắt nhất thời đỏ lên."),t("br"),t("br"),n._v("Hai người từ trần truồng liền cùng nhau chơi đùa, cùng một chỗ luyện thương, đã hơn một năm không thấy, còn tưởng rằng, đời này đều không thể tại gặp nhau, không nghĩ tới nhanh như vậy lần nữa gặp lên."),t("br"),t("br"),n._v("“Ngươi không phải cùng... Trương sư cùng rời đi sao? Làm sao...”"),t("br"),t("br"),n._v("Hưng phấn sau đó, hơi nghi hoặc một chút."),t("br"),t("br"),n._v("Đối phương lão sư, Trương Huyền, nghe nói thành công thi đậu danh sư, oai phong lẫm liệt, còn chuyện về sau, Thiên Huyền vương quốc thực tế quá bế tắc, biết liền không nhiều lắm."),t("br"),t("br"),n._v("“Vừa vặn có thời gian, trở lại thăm một chút ngươi cùng cái khác vài bằng hữu!”"),t("br"),t("br"),n._v("Cười cười, Trịnh Dương cũng không có quá nhiều giải thích."),t("br"),t("br"),n._v("Nói thật, cũng không dám giải thích, cùng đối phương nói, hắn hiện tại đã là Chiến Sư đường đường chủ, Cổ Thánh cường giả..."),t("br"),t("br"),n._v("Chiến Sư đường hiểu không? Cổ Thánh hiểu không?"),t("br"),t("br"),n._v("Nói cũng là nói vô ích!"),t("br"),t("br"),n._v("“Quá tốt rồi, ngươi đi theo Trương sư lâu như vậy, dựa theo đạo lý, hẳn là đạt tới võ giả bát trọng Tông Sư cảnh đi! Rất lâu không cùng ngươi luận bàn công phu, ta gần nhất thế nhưng là học được không ít lợi hại thương pháp, hơn nữa tu vi, cũng đã đạt đến võ giả thất trọng Thông Huyền cảnh đỉnh phong, bất cứ lúc nào cũng sẽ đột phá! Để cho ta nhìn một chút ngươi một năm này tiến bộ thế nào!”"),t("br"),t("br"),n._v("Cổ tay rung lên, trường thương từ trên lưng rơi vào lòng bàn tay, Mạc Hiểu trong nháy mắt chiến ý như hồng."),t("br"),t("br"),n._v("Từ nhỏ đánh quen thuộc, hơn một năm qua, không có người luận bàn, đã sớm ngứa tay khó nhịn, giờ phút này nhìn thấy hảo hữu, cái kia còn kiềm chế được."),t("br"),t("br"),n._v("“Tông Sư cảnh, Thông Huyền cảnh đỉnh phong? Thương pháp?”"),t("br"),t("br"),n._v("Trịnh Dương dở khóc dở cười: “Thôi được rồi...”"),t("br"),t("br"),n._v("Tuy là tuổi không lớn lắm, nhưng mà hai cái này cảnh giới, đã cách xa nhau thực tế quá xa, đều có chút nhớ không rõ."),t("br"),t("br"),n._v("“Làm sao? Sợ ta làm bị thương ngươi? Không cần quá lo lắng, ta đối lực lượng khống chế rất tốt, ngươi không tiếp nổi, sẽ tùy thời dừng lại, chắc chắn sẽ không có vấn đề...”"),t("br"),t("br"),n._v("Trường thương run lên, Mạc Hiểu cười ha ha một tiếng."),t("br"),t("br"),n._v("“Cái này...” Trịnh Dương khó xử."),t("br"),t("br"),n._v("Đường đường Cổ Thánh, Chiến Sư đường đường chủ, cùng một cái Thông Huyền cảnh võ giả tỷ thí... Đánh như thế nào?"),t("br"),t("br"),n._v("“Làm sao? Xem thường huynh đệ, vẫn là sợ thắng không nổi ta, trên mặt không dễ nhìn?” Thấy hắn do dự, Mạc Hiểu nhíu nhíu mày."),t("br"),t("br"),n._v("“Dĩ nhiên không phải...”"),t("br"),t("br"),n._v("Thấy đối phương đều sẽ nói đến nước này, Trịnh Dương lại không cách nào cự tuyệt, bất đắc dĩ lắc đầu, cũng cổ tay rung lên, tùy tiện lấy ra một thanh cấp bậc kém nhất trường thương: “Ở đâu so?”"),t("br"),t("br"),n._v("“Đều có không gian giới chỉ, không tệ, nhìn tới Trương sư không xử bạc với ngươi...”"),t("br"),n._v("Thấy hắn lòng bàn tay bất ngờ xuất hiện trường thương, Mạc Hiểu trong mắt tràn đầy hâm mộ, ngay sau đó gật gật đầu, tiện tay một chỉ: “Lão sư lập tức sẽ ở đây giảng bài, chúng ta qua bên kia, nơi đó có cái tỷ thí đài...”"),t("br"),t("br"),n._v("“Liền không gian giới chỉ đều không có gặp qua...”"),t("br"),t("br"),n._v("Gặp ngày xưa hảo hữu cái bộ dáng này, Trịnh Dương đầy mặt cười khổ, đành phải gật đầu đồng ý: “Đi qua đi!”"),t("br"),t("br"),n._v("Nói xong, đồng loạt hướng cách đó không xa đài cao đi đến."),t("br"),t("br"),n._v("“Chúng ta cũng nhìn một chút!”"),t("br"),t("br"),n._v("Dù sao hiện tại giảng bài không có bắt đầu, Trương Huyền mấy người cũng rảnh đến nhàm chán, cùng một chỗ đi theo."),t("br"),t("br"),n._v("Vừa rồi nữ hài, gặp mấy cái này lừa đảo vậy mà cùng sư huynh nhận thức, chần chờ một chút, cũng theo sau."),t("br"),t("br"),n._v("Hai người nhảy lên đài cao, mỗi người cầm trong tay trường thương, lập tức hấp dẫn vô số người chú ý, vừa rồi chuẩn bị nghe giảng bài mọi người, lập tức đồng loạt xoay người lại."),t("br"),t("br"),n._v("Trịnh Dương gặp nhiều người như vậy nhìn qua, lần nữa che đậy khí tức cùng dung mạo, để cho người ta nhận không ra."),t("br"),t("br"),n._v("“Mạc Hiểu thế nhưng là học viện nổi danh nhất thương pháp thiên tài, một thân tu vi, lão sư, trưởng lão cũng không là đối thủ, cái tên này ở đâu xuất hiện, cùng hắn so?”"),t("br"),t("br"),n._v("“Không biết, chờ lấy nhìn bị đánh chính là!”"),t("br"),t("br"),n._v("“Này ngược lại là...”"),t("br"),t("br"),n._v("Mọi người tất cả đều một mặt vui vẻ."),t("br"),t("br"),n._v("Một năm qua này, Mạc Hiểu tại Lục Tầm lão sư bồi dưỡng bên dưới, đột nhiên tăng mạnh, vô luận tu vi hay là đối thương pháp lý giải, đều sắp muốn đạt tới cảnh giới tông sư, loại này cấp bậc, toàn bộ Hồng Thiên học viện, đều không có mấy người có thể vượt qua, huống chi cái này không biết từ nơi nào xuất hiện thiếu niên."),t("br"),t("br"),n._v("Nhìn thấy Mạc Hiểu vậy mà tại hắn giảng bài tiền khiêu chiến, Lục Tầm cười khổ lắc đầu, nhìn quanh một vòng: “Tốt, đã bọn họ muốn tỷ thí, vừa vặn ta liền lấy các nàng chiến đấu làm đề, cho mọi người giảng một chút thương pháp trong thực chiến vận dụng cùng kỹ xảo!”"),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("“Liền ưa thích nghe Lục lão sư truyền thụ thực chiến kỹ xảo!”"),t("br"),t("br"),n._v("“Đây chính là có thể tăng lên sức chiến đấu biện pháp tốt nhất, lần này không uổng công...”"),t("br"),t("br"),n._v("Nghe được Lục Tầm lão sư lại muốn truyền thụ thương pháp thi triển vận dụng, phía dưới một hồi xôn xao, mọi người tất cả đều hưng phấn mà tràn đầy kích động, đồng loạt hướng cách đó không xa đài cao nhìn sang."),t("br"),t("br"),n._v("“Ha ha, tới đi!”"),t("br"),t("br"),n._v("Gặp đưa tới như vậy lớn chú ý, lão sư cũng tự thân nhìn qua, Mạc Hiểu trong mắt chiến ý như hồng, trường thương run lên, phát ra nghẹn ngào thanh âm, thẳng tắp hướng Trịnh Dương nhanh đâm mà tới."),t("br"),t("br"),n._v("Ô ô ô ô!"),t("br"),t("br"),n._v("Bởi vì tốc độ quá nhanh, thương trên không trung đập ra phá không tiếng gió."),t("br"),t("br"),n._v("“Mạc Hiểu chiêu này không tệ, một chiêu ra, mà không dừng lại, thương pháp chú ý chính là trôi chảy, không chút nào dây dưa dài dòng, một khi làm ra quyết định, liền Lôi Đình Vạn Quân, cho người ta một loại thế không thể đỡ cảm giác... Mạc Hiểu chiêu này, bình thường tông sư, đều không đỡ được, hắn đối diện thiếu niên, đoán chừng phải xui xẻo...”"),t("br"),t("br"),n._v("Thoả mãn gật gật đầu, Lục Tầm thanh âm vang vang."),t("br"),t("br"),n._v("Trịnh Dương đầy mặt rầu rĩ."),t("br"),t("br"),n._v("Thương pháp này tốc độ, thực tế quá chậm, cùng ốc sên không có cái gì khác nhau, hơn nữa lực lượng quá yếu, tùy tiện chặn, đều rất dễ dàng làm bị thương bằng hữu, để hắn mười phần làm khó."),t("br"),t("br"),n._v("Ngăn cản không phải, không ngăn cản cũng không quá dễ... Thoạt nhìn thật là khó chịu ah!"),t("br"),t("br"),n._v("“Ta nói không sai a, đối thủ của hắn đã hoảng hốt lo sợ, không biết như thế nào cho phải!”"),t("br"),t("br"),n._v("Nhìn thấy đối diện Trịnh Dương, cũng chưa hề đụng tới, Lục Tầm thoả mãn gật gật đầu: “Ta truyền thụ cho thương pháp, chú ý chính là cái khí thế, như vậy mới có uy lực, vì rèn luyện Mạc Hiểu, ta từng dẫn hắn đi dưới thác nước, đối kháng hồng lưu, kiên trì ba tháng, mới có hôm nay hiệu quả, không cần nhìn, đối thủ của hắn, lập tức liền sẽ nhận thua, thua, thua, thua...”"),t("br"),t("br"),n._v("Lời còn chưa dứt, cảm thấy bờ môi không khỏi khống chế, run rẩy."),t("br"),t("br"),n._v("Thít chặt trong con mắt cho thấy như vậy một hình ảnh: Mạc Hiểu trường thương, đâm vào Trịnh Dương trên ánh mắt, sau đó..."),t("br"),t("br"),n._v("Từng đoạn từng đoạn vỡ vụn."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1866"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1868"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);