(window.webpackJsonp=window.webpackJsonp||[]).push([[98],{451:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1065-hong-diep-vuong-hoc-mau"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1065-hong-diep-vuong-hoc-mau"}},[n._v("#")]),n._v(" Chương 1065: Hồng Diệp Vương hộc máu")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1065: Hồng Diệp Vương hộc máu"),t("br"),t("br"),n._v("“Thì ra là thế...”"),t("br"),t("br"),n._v("Nghe được giải thích, Trương Huyền bừng tỉnh hiểu ra."),t("br"),t("br"),n._v("Khó trách!"),t("br"),t("br"),n._v("Trước đó, hắn liền suy đoán, bám vào tại Giang Nguyên trong cơ thể Thiên Diệp Vương thực lực có chênh lệch chút ít thấp, nguyên lai cái tên này chân thân bị vây ở di tích chỗ sâu nhất, làm những chuyện này, chỉ là một đạo ít ỏi ý niệm."),t("br"),t("br"),n._v("“Nguyên thần mặc dù có thể đơn độc sống sót, đây là một đạo ý niệm, không có khả năng vĩnh cửu tồn tại... Đoán chừng lúc ấy chúng ta bắt lấy Thổ Diệp Vương thời điểm, cái tên này cũng tại, chỉ là chúng ta không phát hiện thôi...”"),t("br"),t("br"),n._v("Trong lòng hơi động, nghĩ đến một loại khả năng."),t("br"),t("br"),n._v("Nguyên thần là có thể đơn độc sống sót, nhưng một đạo ý niệm liền không cường đại như vậy năng lực."),t("br"),t("br"),n._v("Cái này Thiên Diệp Vương có thể là giấu ở Thổ Diệp Vương trên người lẫn vào Tĩnh Viễn thành Độc điện."),t("br"),t("br"),n._v("Chẳng qua là lúc đó không nghĩ tới cái này gốc, cũng không phải là phát hiện cái gì."),t("br"),t("br"),n._v("Về sau cái tên này, phát hiện bản thân chỉ là nhận được một cái giới thiệu, cũng không phải là bản đồ, chẳng biết lúc nào chui vào Giang Nguyên trong cơ thể, ngụy trang đi theo nhóm người mình sau lưng."),t("br"),t("br"),n._v("Hi vọng từ trên người bọn họ biết bản đồ bí mật."),t("br"),t("br"),n._v("Nếu như không phải nguyên nhân này, khẳng định đã sớm ra tay đem bọn hắn đều giết."),t("br"),t("br"),n._v("“Ngươi biết bản đồ tin tức, thứ này đến cùng ở đâu?”"),t("br"),t("br"),n._v("Thấy hắn trầm tư, Hồng Diệp Vương không nghi ngờ gì, nhịn không được hỏi."),t("br"),t("br"),n._v("“Bản đồ hẳn là tại vị kia kêu Trương Huyền gia hỏa trong tay!” Trương Huyền nhẹ gật đầu."),t("br"),t("br"),n._v("“Trương Huyền?” Hồng Diệp Vương lắc đầu: “Đoạn đường này Thiên Diệp Vương cẩn thận quan sát, cái tên này mặc dù thủ đoạn rất nhiều, cũng không ít xuất kỳ bất ý ý nghĩ, nhưng... Cũng không biết bản đồ tin tức, nếu là biết, cũng không biết bị vây ở trong thiên cung, đến bây giờ đều không ra được!”"),t("br"),t("br"),n._v("Cái kia Trương Huyền, mặc dù thoạt nhìn rất lợi hại, trên thực tế chỉ là một chút khôn vặt, tiểu kỹ xảo thôi, hẳn là không có địa đồ."),t("br"),t("br"),n._v("Nếu không, căn bản không cần trải qua nhiều như vậy nguy hiểm, đã sớm trực tiếp đi vào di tích chỗ sâu."),t("br"),t("br"),n._v("“Hắn đích xác không biết bản đồ tin tức, nhưng mà bản đồ nhưng thật trong tay hắn!” Trương Huyền gật đầu."),t("br"),t("br"),n._v("“Ồ?” Hồng Diệp Vương không hiểu."),t("br"),t("br"),n._v("“Cái tên này từng nhận được Ngô Dương Tử truyền thừa, trong đó có bản đồ, nhưng mà hắn không được đến Thiên Diệp Vương cướp đi dùng Cổ Thần ngữ viết di tích giới thiệu... Cho nên, căn bản xem không hiểu, vốn không rõ ràng, cái này bản đồ cùng cái này di tích liên quan đến, đương nhiên cũng liền không lấy ra!”"),t("br"),t("br"),n._v("Trương Huyền giải thích nói."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Hồng Diệp Vương sững sờ."),t("br"),t("br"),n._v("Điểm ấy ngược lại là hắn không nghĩ tới."),t("br"),t("br"),n._v("“Tin tức này ngươi từ nơi nào lấy được?” Dừng lại một chút, Hồng Diệp Vương vội nói."),t("br"),t("br"),n._v("“Những ngày gần đây, ta lẫn vào nhân loại, chuyên môn đi Hồng Viễn danh sư học viện, đem chuyện này hỏi thăm rõ rõ ràng ràng, tuyệt sẽ không sai!” Trương Huyền nói."),t("br"),t("br"),n._v("“Nếu như là như vậy, ngược lại là thật có khả năng...” Hồng Diệp Vương con mắt tỏa ánh sáng: “Nếu như tin tức này là thật, đợi sau khi đi ra ngoài, ta sẽ báo lên Thanh Điền Hoàng, cho ngươi ngợi khen! Được rồi, ngươi trước tiên ở cái này chờ lấy, ta phá vỡ cái tên này bố trí trận pháp, đem hắn bắt tới...”"),t("br"),t("br"),n._v("“Trước không vội... Ta mặc dù biết, bản đồ có thể sẽ tại vị này Trương Huyền trong tay, nhưng mà hắn để ở nơi đâu, có hay không mang ở trên người cũng không xác định, dù sao nơi này nguy hiểm, hắn lại là danh sư học viện viện trưởng, một cái nghiên cứu không hiểu bản đồ, ở lại học viện cũng rất bình thường...” Vội vàng đánh gãy cử động của hắn, Trương Huyền tiếp tục mở miệng."),t("br"),t("br"),n._v("“Ừm?” Hồng Diệp Vương nhìn tới."),t("br"),t("br"),n._v("Đối phương nói không sai, nếu như không biết cái này bản đồ có liên quan với đó, biết rõ đi vào di tích, cửu tử nhất sinh, rất có thể sẽ đem hắn ở lại học viện, thật muốn như vậy liền phiền toái."),t("br"),t("br"),n._v("“Ta nhìn không bằng như vậy, Thiên Diệp Vương không phải nhận được cái kia dùng Cổ Thần ngữ viết di tích giới thiệu ư? Không bằng chúng ta lấy trước đi ra, coi đây là mồi nhử, đem hắn dụ dỗ đi ra...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("“Dụ dỗ đi ra? Làm thế nào?” Hồng Diệp Vương cau mày."),t("br"),t("br"),n._v("Nếu là có thể làm được, bọn họ khẳng định trước đó liền làm, cũng không trở thành chờ tới bây giờ."),t("br"),t("br"),n._v("“Thánh Nhân quý tộc, có thể thông qua huyết mạch đặc tính, tìm tới thất lạc nhiều năm người thân cốt nhục, hai thứ đồ này đã đều là Cổ Thần ngữ viết, hoàn toàn có thể lợi dụng điểm giống nhau, lẫn nhau thu hút... Những ngày này ta tại danh sư học viện, học được loại phương pháp này, ngươi chỉ cần đem bản này di tích giới thiệu cho ta, ta liền có thể cam đoan, đem bản đồ vị trí cụ thể tìm ra...”"),t("br"),t("br"),n._v("Trương Huyền gật đầu nói."),t("br"),t("br"),n._v("“Thánh Nhân quý tộc sự tình, ta nghe nói qua... Nếu quả thật có loại bí pháp này, cũng quá được rồi...” Hồng Diệp Vương nhẹ gật đầu, cổ tay khẽ đảo, một cái hộp ngọc bị lấy ra ngoài: “Đây là quyển kia di tích, Thiên Diệp Vương trước đặt ở ta chỗ này!”"),t("br"),t("br"),n._v("Thiên Diệp Vương chui vào Giang Nguyên trong cơ thể, đương nhiên không có khả năng đem thứ này đặt ở trên người mình, liền để cho trước mắt vị này."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Trương Huyền đưa tay nhận lấy hộp ngọc, nhẹ nhàng mở ra, cúi đầu hướng phía trong nhìn lại, con mắt kìm lòng không được sáng lên."),t("br"),t("br"),n._v("Bên trong bày đặt một quyển sách, thoạt nhìn cũ kỹ không chịu nổi, cùng trước đó theo Ngô Dương Tử trong tay lấy được cái kia bản đồ, bộ dáng vô cùng tương tự, hẳn là xuất từ cùng một đám lần."),t("br"),t("br"),n._v("Ngón tay nhẹ nhàng đụng một cái, trong đầu lập tức nhiều hơn vô số kiến thức."),t("br"),t("br"),n._v("Văn tự cùng trên bản đồ viết, quả nhiên tương đồng, tối nghĩa khó hiểu, để cho người ta nhìn không ra đến cùng là cái gì."),t("br"),t("br"),n._v("“Cẩn thận nghiên cứu lời nói, hẳn là có thể đủ nghiên cứu ra một chút nội dung tới...”"),t("br"),t("br"),n._v("Đây là một quyển sách, phía trên có thiếu hụt giới thiệu, chỉ cần cố gắng nghiên cứu lời nói, hẳn là có thể đủ rồi giải một chút nội dung, bất quá... Hắn biết không phải là nghiên cứu cái này thời điểm."),t("br"),t("br"),n._v("Tiện tay đem hộp ngọc khép lại, lần nữa nhìn lại: “Là như vậy, thi triển cái bí pháp này, cần bố trí một cái, Thất Tinh đỉnh phong cấp bậc đại trận, ngươi cũng biết, ta đối với trận pháp lý giải, kém xa ngươi, chờ một lúc chỉ sợ còn muốn làm phiền ngươi hỗ trợ...”"),t("br"),t("br"),n._v("“Bố trí đại trận?” Hồng Diệp Vương nhẹ gật đầu."),t("br"),t("br"),n._v("Hắn có thể khống chế cái này trong thiên cung trận pháp, đủ để chứng minh đối với trận pháp nghiên cứu, cực kỳ cường đại."),t("br"),t("br"),n._v("“Ừm, ta sẽ đem trận pháp phương vị nói cho ngươi nghe, hiện tại liền bắt đầu bố trí đi...” Trương Huyền nhẹ gật đầu, cổ tay khẽ đảo, lấy ra một đống cấp bảy tả hữu trận kỳ."),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Hồng Diệp Vương nhẹ gật đầu, lại không nhận lấy trận kỳ."),t("br"),t("br"),n._v("“Bên trái ba mươi mét, giáp hư vị trí. Phía bên phải hai mươi bảy mét, hàn cốc vị trí...”"),t("br"),t("br"),n._v("Trương Huyền mở miệng."),t("br"),t("br"),n._v("Hắn nói đều là cấp bảy trong trận pháp danh từ, đại biểu chính xác vị trí, chỉ cần đối với trận pháp có hiểu rõ, đều sẽ biết."),t("br"),t("br"),n._v("Nghe hắn nói ra nguyên một đám, cụ thể phương vị, Hồng Diệp Vương khen ngợi một tiếng, chân khí trong cơ thể sôi trào, đem từng cây trận kỳ đóng ở phía trên."),t("br"),t("br"),n._v("“Không đúng, trận pháp này làm sao cảm giác giống như là cái sát trận?”"),t("br"),t("br"),n._v("Bố trí một hồi, Hồng Diệp Vương lông mày nhíu một cái, giống như là phát hiện cái gì."),t("br"),t("br"),n._v("Bản thân hắn liền đối với trận pháp có rất sâu hiểu rõ, trước mắt Tử Diệp Vương nói những này phương vị, mặc dù là chưa bao giờ thấy qua, có thể hạch tâm bên trên nhìn giống như là một cái sát trận."),t("br"),t("br"),n._v("Không phải muốn bố trí một cái tìm kiếm bản đồ trận pháp ư?"),t("br"),t("br"),n._v("Lấy ra cái sát trận làm gì?"),t("br"),t("br"),n._v("Trong nháy mắt, Hồng Diệp Vương tràn đầy nghi ngờ."),t("br"),t("br"),n._v("“Sát trận? Hồng Diệp Vương quá lo lắng, có điều, trận pháp này quả thực có chút giống sát trận, nhưng một khi vận chuyển, ngươi liền sẽ phát hiện khác biệt...” Trương Huyền cười cười, một mặt thành khẩn."),t("br"),t("br"),n._v("“Khác biệt? Ta cảm thấy chính là sát trận, ba cái kia vị trí trận kỳ có thể dẫn động Thiên Cương, mấy cái kia vị trí, có thể dẫn động Địa Sát, nếu như bị nhốt trong đó, e là cho dù là nửa bước xuất khiếu, đều khó mà may mắn thoát khỏi...”"),t("br"),t("br"),n._v("Hồng Diệp Vương cau mày."),t("br"),t("br"),n._v("“Mấy cái này vị trí từ bên trên nhìn, quả thực cùng sát trận tương tự, nhưng từ phía dưới vị trí nhìn, liền không đồng dạng... Không tin ngươi qua đây!”"),t("br"),t("br"),n._v("Trương Huyền cười cười, thân thể nhoáng một cái, tiến vào trận cờ trưng bày trung tâm: “Ngươi tới nơi này nhìn...”"),t("br"),t("br"),n._v("“Ừm!” Hồng Diệp Vương mặc dù kỳ quái, vẫn gật đầu, theo sát tại đối phương sau lưng, đi tới hắn nói vị trí."),t("br"),t("br"),n._v("Dù sao đối phương cũng tại, lại thêm trận pháp còn thiếu một chút không hoàn thành, không có khả năng khởi động."),t("br"),t("br"),n._v("“Ngươi đứng ở chỗ này đừng động, nhìn về phía cái hướng kia... Ai, đúng, đừng động, ta động một cái trận kỳ, ngươi liền hiểu, trận pháp này bí ẩn...”"),t("br"),t("br"),n._v("Thấy hắn đi tới trước chân đứng vững, Trương Huyền thân thể nhoáng một cái, bay lên, vừa bay bên cạnh lớn tiếng la lên: “Tuyệt đối đừng động, khẽ động liền thấy không rõ lắm, đúng, chính là chỗ đó...”"),t("br"),t("br"),n._v("Lời còn chưa nói hết, lòng bàn tay bỗng nhiên nhiều ra mấy cái trận kỳ, trực tiếp bắn ra ngoài."),t("br"),t("br"),n._v("“Ngươi đây là... Bách Kỳ Định Hư? Ngươi không phải Tử Diệp Vương... Ngươi đến cùng là ai?”"),t("br"),t("br"),n._v("Đang tại phía dưới đứng vững, chờ lấy đối phương lay động trận kỳ thấy rõ ràng trận pháp kết cấu Hồng Diệp Vương, đột nhiên cái tên này lòng bàn tay trận kỳ bắn ra, con ngươi đột nhiên thu hẹp, chân khí trong cơ thể lập tức cuồng bạo bừng lên."),t("br"),t("br"),n._v("Tựa như bùng nổ."),t("br"),t("br"),n._v("Bách Kỳ Định Hư là Thất Tinh trận pháp sư mới có thể thi triển thủ đoạn, có thể để cho trận pháp nhanh chóng hình thành, không chậm trễ chiến đấu... Tử Diệp Vương đối với trận pháp cũng không am hiểu, lúc nào có thể nhanh như vậy bày trận?"),t("br"),t("br"),n._v("Rất rõ ràng, cái tên này là giả!"),t("br"),t("br"),n._v("Sưu sưu sưu sưu!"),t("br"),t("br"),n._v("Chân khí trào lên, đang định bay đi lên, đem cái này giả mạo gia hỏa chém giết, chỉ thấy chân tay hắn nhẹ nhàng giẫm một cái."),t("br"),t("br"),n._v("Ầm ầm!"),t("br"),t("br"),n._v("Vừa rồi bố trí trận pháp trong lúc đó khởi động, vô số huy hoàng kiếm khí, trực tiếp hướng hắn bắn tới."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Con ngươi thu hẹp, Hồng Diệp Vương suýt chút nữa không tại chỗ dọa ngất đi qua."),t("br"),t("br"),n._v("Hắn mặc dù là cấp bảy đỉnh phong cấp bậc trận pháp sư, cũng có thể thi triển ra Bách Kỳ Định Hư thủ đoạn... Có thể giống đối phương như thế, bàn chân giẫm một cái, trận pháp liền khởi động... Là vô luận như thế nào cũng làm không được."),t("br"),t("br"),n._v("Trước đó hắn dám vào vào trận pháp, cũng chính là cảm thấy, không có người có thể nhanh chóng khởi động trận pháp, cho dù có nguy hiểm, cũng có thể trong khoảng thời gian ngắn chạy ra..."),t("br"),t("br"),n._v("Nằm mơ đều không có nghĩ đến, cái tên này không chỉ có thể thi triển Bách Kỳ Định Hư, còn có thể nhanh như vậy khởi động trận pháp, liền để hắn chạy trốn thời gian đều không có!"),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Còn không có kịp phản ứng, Hồng Diệp Vương ngực liền bị một đạo kiếm khí đâm thủng, máu tươi cuồn cuộn chảy xuôi."),t("br"),t("br"),n._v("Mặc dù nguy cơ phía dưới tránh thoát chỗ hiểm, vẫn như cũ để hắn chật vật không chịu nổi, tại không có trước đó trêu tức mọi người bộ dáng."),t("br"),t("br"),n._v("“Ngươi đến cùng là ai?” Răng cắn chặt, điên cuồng gào thét."),t("br"),t("br"),n._v("Đường đường Thanh Điền nhất mạch thập đại vương giả bên trong, xếp hạng thứ hai siêu cấp cường giả, bị người ở trước mặt trêu đùa, đồng thời khốn vào trận pháp, Hồng Diệp Vương khí sắp bùng nổ."),t("br"),t("br"),n._v("“Thật không tiện, chính là ngươi muốn giết chết vị kia...” Trương Huyền cười cười, khôi phục dung mạo của mình."),t("br"),t("br"),n._v("“Ngươi... Là Trương Huyền? Ngươi làm sao có thể ngụy trang thành Tử Diệp Vương, để cho ta không cách nào phát hiện?”"),t("br"),t("br"),n._v("Hồng Diệp Vương khó tin."),t("br"),t("br"),n._v("Nhân loại không tu luyện chém giết chân khí, làm sao có thể ngụy trang Tử Diệp Vương, để hắn đều không phát hiện được thật giả?"),t("br"),t("br"),n._v("Đột nhiên nghĩ đến cái gì, thân thể run lên: “Ngươi... Tử Diệp Vương cũng là ngươi giết?”"),t("br"),t("br"),n._v("Vừa dứt lời, liền thấy trận pháp phía ngoài thanh niên, đỡ cái cằm, khắp khuôn mặt là thật không tiện: “Cái này... Không riêng gì Tử Diệp Vương, hiện nay loại trừ Thiên Diệp Vương cùng ngươi... Thập đại vương giả, cơ bản đều chết tại trên tay của ta...”"),t("br"),t("br"),n._v("“Phốc!”"),t("br"),t("br"),n._v("Hồng Diệp Vương hộc máu."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1073"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1075"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);