(window.webpackJsonp=window.webpackJsonp||[]).push([[980],{1334:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1857-trinh-duong-gap-ban-gai"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1857-trinh-duong-gap-ban-gai"}},[n._v("#")]),n._v(" Chương 1857: Trịnh Dương gặp bạn gái")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1857: Trịnh Dương gặp bạn gái (bên trên)"),t("br"),t("br"),n._v("Muốn nhìn tiểu thuyết hay, xin sử dụng Wechat chú ý công chúng hào “Đến ngưu đọc sách”."),t("br"),t("br"),n._v("Cấp tám đan dược, là cấp tám luyện đan sư mới có thể luyện chế ra tới, mỗi một viên đều giá trị liên thành, không nói cái khác, có như vậy một cái đan dược, đưa cho Thiên Huyền vương quốc quốc vương, khẳng định sẽ liền bệ hạ bảo tọa, đều không muốn chờ lâu, trực tiếp tặng người."),t("br"),t("br"),n._v("Trân quý như thế... Liền như thế tặng người nuốt vào..."),t("br"),t("br"),n._v("Cảm thụ trên người mênh mông khí tức, cùng hoàn hảo không chút tổn hại đùi phải, Mạc Hiểu tựa như trong mộng, khó tin: “Dương tử, cái này quá trân quý, ta không chịu nổi...”"),t("br"),t("br"),n._v("Còn tưởng rằng chỉ là cái chữa thương đan dược, nằm mơ cũng không dám nghĩ là cấp tám dược phẩm ah!"),t("br"),t("br"),n._v("Sớm biết trân quý như vậy, đánh chết cũng không ăn!"),t("br"),t("br"),n._v("“Đừng khách khí, một cái đan dược mà thôi, hai ta tình cảm, ở chỗ này trước, không tính là gì!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trịnh Dương vỗ vỗ hảo hữu bả vai."),t("br"),t("br"),n._v("Tuy là hai người thế giới quan khác biệt, không cách nào cố gắng câu thông, nhưng tình nghĩa còn tại, vẫn như cũ là bằng hữu tốt nhất, cái kia năm đó cùng một chỗ luyện thương phát tiểu."),t("br"),t("br"),n._v("“Mạc Hiểu, ngươi vị bằng hữu này là...”"),t("br"),t("br"),n._v("Cũng ý thức được Trịnh Dương đáng sợ, Lục Tầm vội vàng đi tới trước mặt, trong mắt mang theo kính sợ."),t("br"),t("br"),n._v("Có thể thoáng cái lấy ra cấp tám đan dược, tiện tay tặng người... Thân phận cùng lai lịch, cũng không phải hắn một cái vừa mới tấn thăng nhị tinh danh sư, có thể so sánh với."),t("br"),t("br"),n._v("“Lão sư, hắn là...”"),t("br"),t("br"),n._v("Gặp lão sư vậy mà không có nhận ra mình người bạn thân này, Mạc Hiểu tràn đầy nghi ngờ, vừa định giới thiệu, liền nghe đến bên tai vang lên truyền âm: “Thân phận của ta vẫn là đừng nói nữa, Lúc trước cùng Lục lão sư huyên náo cũng không quá vui vẻ...”"),t("br"),t("br"),n._v("Mạc Hiểu ngừng lại."),t("br"),t("br"),n._v("Hắn vị này hảo huynh đệ, bởi vì Lục Tầm lão sư làm nhục Trương sư sự tình, từng liên hợp Triệu Nhã đám người, đem Lục lão sư trước mặt mọi người đánh một trận, quan hệ có chút khẩn trương."),t("br"),t("br"),n._v("“Hắn là ta một lần ngẫu nhiên nhận thức bằng hữu, không nghĩ tới... Lại có trân quý như thế đan dược!”"),t("br"),t("br"),n._v("Hiểu được, Mạc Hiểu vội vàng nói."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Nhìn ra học sinh nghĩ một đằng nói một nẻo, Lục Tầm cuối cùng cũng không hỏi nhiều, mà là nhìn Trương Huyền đám người một cái, như có điều suy nghĩ gật gật đầu."),t("br"),t("br"),n._v("“Đan dược có thể tăng lên tu vi của ngươi, chẳng qua, còn cần cẩn thận củng cố, mới có thể vận dụng tự nhiên, ta sẽ không quấy rầy ngươi, sau này còn gặp lại!”"),t("br"),t("br"),n._v("Thấy được hảo hữu cùng Lục lão sư, biết tiếp tục tiếp tục chờ đợi cũng mất ý nghĩa, Trịnh Dương bàn giao một câu, cùng Trương Huyền đám người xoay người rời đi."),t("br"),t("br"),n._v("Mọi người lúc này, làm sao không biết mấy vị này không đơn giản, đặc biệt là nữ hài kia, vội vã đuổi theo, muốn lần nữa tìm kiếm bỏ qua cơ duyên, mới đi mấy bước, liền cảm thấy thấy hoa mắt, bóng người biến mất, hình như chưa hề xuất hiện qua một giống như."),t("br"),t("br"),n._v("Lúc trước tất cả, đều như là một giấc mộng, cẩn thận suy nghĩ, một chút cũng nghĩ không ra dung mạo của đối phương, chớ nói chi là dáng người đặc thù."),t("br"),t("br"),n._v("“Oa!” Kêu to một tiếng, khóc ra thành tiếng."),t("br"),t("br"),n._v("Như vậy cơ duyên, liền bị hắn triệt để bỏ qua, thật hận không thể đem tròng mắt đào ra."),t("br"),t("br"),n._v("“Hẳn là... Trương sư cùng Trịnh Dương bọn họ a!”"),t("br"),t("br"),n._v("Cùng nữ hài đám người khác biệt, Lục Tầm truyền âm cho bên người học sinh."),t("br"),t("br"),n._v("“Lão sư...”"),t("br"),t("br"),n._v("Mạc Hiểu hơi đỏ mặt."),t("br"),t("br"),n._v("Đó có thể thấy được, hắn vị này hảo huynh đệ không muốn tiết lộ thân phận, có thể bản thân lại không nguyện ý giấu diếm lão sư."),t("br"),t("br"),n._v("“Ta sẽ không làm khó ngươi... Ta sở dĩ có thể nhìn ra, là bởi vì, loại trừ hắn, thực tế nghĩ không ra, còn có người nào sẽ đến nơi này, hơn nữa tiện tay cho ngươi một cái cấp tám đan dược!”"),t("br"),t("br"),n._v("Lục Tầm cười khổ, nói “Cố mà trân quý người bạn này a, có thể nhận thức như vậy người đi đường, là ngươi cả đời lớn nhất của cải!”"),t("br"),t("br"),n._v("Khảo hạch đến nhị tinh danh sư, tiếp xúc đến một số bí mật, thế mới biết, vị này Lúc trước cùng hắn tiến hành tỷ thí Trương lão sư, rời đi Thiên Huyền vương quốc sau, đến cùng đã trải qua loại nào rực rỡ nhân sinh, lại làm ra cái nào nghịch thiên việc làm."),t("br"),t("br"),n._v("Hắn cái này cái gọi là thiên tài, cùng đối phương tỷ thí... Vẫn là đừng so, vượt so vượt cảm thấy mình cùng vũng bùn bên trong con giun một giống như, mà đối phương là bay lượn cửu thiên Thần Long..."),t("br"),t("br"),n._v("“Lão sư nếu biết... Cái kia hẳn là biết, Dương tử, hẳn không phải là chí tôn mà là... Cảnh giới cao hơn đi!”"),t("br"),t("br"),n._v("Gặp lão sư biết, Mạc Hiểu rõ ràng giấu diếm cũng vô dụng, cũng lại không kềm chế được trong lòng tò mò."),t("br"),t("br"),n._v("Lúc trước, cho rằng người bạn thân này, đã đột phá tông sư, về sau đâm trúng một thương đối phương con mắt, một chút thương đều không có nhận, ngược lại hắn nhận bị thương nặng, cho rằng có thể là không phải đột phá tới tôn."),t("br"),t("br"),n._v("Càng về sau, dùng đan dược, tu vi của hắn đạt tới Hóa Phàm nhị trọng Nguyên Khí cảnh, vẫn như cũ nhìn không ra đối phương chuẩn xác thực lực, giờ mới hiểu được... Vị này từ nhỏ cùng nhau lớn lên phát tiểu, đã đạt đến lão sư hắn, đều cần ngước nhìn tình trạng."),t("br"),t("br"),n._v("“Cảnh giới của hắn... Không phải chúng ta có tư cách biết, ngươi chỉ cần rõ ràng, ngươi người huynh đệ này vừa rồi thật muốn đánh trả, toàn bộ Thiên Huyền vương quốc đều sẽ bởi vậy biến mất...”"),t("br"),n._v("Đầy mặt bất đắc dĩ, Lục Tầm nói."),t("br"),t("br"),n._v("“Thiên Huyền vương quốc biến mất?”"),t("br"),t("br"),n._v("Mồm dài lớn, Mạc Hiểu cũng lại nói không ra lời."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Rời đi Lục Tầm giảng bài địa phương, mọi người trở lại năm đó học tập phòng học."),t("br"),t("br"),n._v("Cùng lúc trước không có bất kỳ biến hóa nào, vẫn như cũ có vẻ hơi nhỏ hẹp."),t("br"),t("br"),n._v("“Các ngươi đều đi gặp người thân a!”"),t("br"),t("br"),n._v("Đi vào gian phòng, Trương Huyền khoát tay áo."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Mọi người gật gật đầu, Vương Dĩnh, Triệu Nhã đám người bay ra ngoài."),t("br"),t("br"),n._v("Vương Dĩnh gia tộc là Thiên Huyền vương thành tứ đại gia tộc, ca ca, cha bọn người tại, Triệu Nhã phụ thân là Bạch Ngọc thành thành chủ, vì chiếm cứ một phương hào hùng, lần này trở về, tự nhiên muốn đi gặp bên trên một mặt."),t("br"),t("br"),n._v("Còn Ngụy Như Yên, Lộ Xung, sớm đã không còn người thân, không còn lo lắng."),t("br"),t("br"),n._v("“Như Yên sư muội, ngươi... Có thể hay không theo ta đi ra ngoài một chuyến?”"),t("br"),t("br"),n._v("Gặp Triệu Nhã đám người rời đi, Trịnh Dương chần chờ một chút, nói."),t("br"),t("br"),n._v("“Làm cái gì?” Ngụy Như Yên cau mày."),t("br"),t("br"),n._v("Nàng tu luyện mặc dù không phải băng hàn thuộc tính công pháp, nhưng cũng từ trước tới nay băng lãnh, đồng môn sư huynh đệ, cũng rất ít hiểu."),t("br"),t("br"),n._v("Cùng Trịnh Dương quan hệ tuy là quen thuộc, trên thực tế lại không nói qua bao nhiêu lời."),t("br"),t("br"),n._v("“Ta nghĩ... Để ngươi giúp ta một việc... Vậy cũng là nỗi khúc mắc của ta a!”"),t("br"),t("br"),n._v("Dừng lại một chút, Trịnh Dương nói."),t("br"),t("br"),n._v("“Tốt!”"),t("br"),t("br"),n._v("Ngụy Như Yên đáp ứng."),t("br"),t("br"),n._v("Lão sư Một mực dạy dỗ, đồng môn sư huynh đệ, nhất định phải cùng nhau trông coi, đã hắn có chuyện nhờ bản thân, đồng ý cũng không sao."),t("br"),t("br"),n._v("“Ta nghĩ dẫn ngươi gặp người...”"),t("br"),t("br"),n._v("“Gặp người?” Ngụy Như Yên nghi ngờ."),t("br"),t("br"),n._v("Nàng tính cách lãnh đạm, loại trừ lão sư, ai cũng không muốn gặp, đối phương cũng không phải không biết, lại còn có yêu cầu này?"),t("br"),t("br"),n._v("“Ngươi không cần nói chuyện, chỉ cần đứng tại chỗ là có thể...”"),t("br"),t("br"),n._v("Thấy nàng bộ dáng này, Trịnh Dương khó xử."),t("br"),t("br"),n._v("“Rốt cuộc là ai? Nếu như thực lực mạnh, có thể trình báo lão sư...”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, Ngụy Như Yên nói."),t("br"),t("br"),n._v("“Thực lực không mạnh, trái lại còn rất yếu...”"),t("br"),t("br"),n._v("Dừng lại một chút, Trịnh Dương cắn răng một cái: “Nói thật cho ngươi biết a... Ta tại bái sư lúc trước, từng thầm mến qua một cái nữ hài, vì nàng bỏ ra thành tâm, kết quả lại gặp đến trào phúng cùng cự tuyệt... Lần này đã trở về, muốn gặp lại một mặt, tuy là lại không còn ưa thích, nhưng cũng muốn cho đoạn này ý nghĩ, tranh một cái viên mãn dấu chấm tròn...”"),t("br"),t("br"),n._v("“Vì lẽ đó muốn cho ta giả mạo bạn gái của ngươi có đúng không?”"),t("br"),t("br"),n._v("Ngụy Như Yên cười nhạo."),t("br"),t("br"),n._v("Nàng vị sư huynh này, đều là Chiến Sư đường chủ, Cổ Thánh cường giả, không nghĩ tới tình cảm bên trên lại còn như vậy Tiểu Bạch."),t("br"),t("br"),n._v("Đối phương cự tuyệt, liền muốn tìm đẹp hơn, giả mạo người yêu, để hắn cho rằng hối hận không?"),t("br"),t("br"),n._v("Ý nghĩ của tiểu hài tử."),t("br"),t("br"),n._v("Ngây thơ cực kỳ!"),t("br"),t("br"),n._v("Đổi lại nàng... Không đồng ý, hạ độc chết chính là, đơn giản bớt việc, nào có nói nhảm nhiều như vậy.."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1868"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1870"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);