(window.webpackJsonp=window.webpackJsonp||[]).push([[981],{1336:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-178-xung-đot"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-178-xung-đot"}},[n._v("#")]),n._v(" Chương 178: Xung đột")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v(" “Ây...” "),i("br"),i("br"),n._v(" Nhìn thấy hắn dáng vẻ ấy, Âu Dương hội trưởng mấy người muốn cười lại không dám cười."),i("br"),i("br"),n._v(" Hồng Vân, tại trong lòng bọn họ bên trong cái kia là cao cao tại thượng siêu cường đan sư, coi như Thẩm Truy bệ hạ nhìn thấy, đều muốn một mực cung kính, không dám mảy may nhẹ nhục."),i("br"),i("br"),n._v(" Hiện tại ngược lại tốt, cả người cùng than đen như thế, tóc dựng lên, nếu không là vẫn kích động hai mắt, cũng hoài nghi có phải là người sống."),i("br"),i("br"),n._v(" Bất quá, bọn họ cũng biết giờ khắc này không phải cười thời điểm, đồng loạt đưa mắt tập trung tại Trương Huyền trên người."),i("br"),i("br"),n._v(" Muốn nghe một chút hắn lời giải thích."),i("br"),i("br"),n._v(" Cho nhị tinh luyện đan sư chỉ điểm... Đây chính là bọn họ cũng không dám nghĩ tới sự tình."),i("br"),i("br"),n._v(" Bọn họ cũng muốn nhìn một chút, lúc trước tại Luận đan lúc hăng hái thanh niên, có thể hay không tái hiện huy hoàng."),i("br"),i("br"),n._v(" Ánh mắt mọi người tập trung, liền thấy cách đó không xa thanh niên, một mặt quái lạ xem ra: “Ngươi xác định ngươi thực sự là nhị tinh luyện đan sư?” "),i("br"),i("br"),n._v(" “Ngươi có ý gì?” Hồng Vân đan sư quăng một cái ống tay áo, có vẻ hơi không vui."),i("br"),i("br"),n._v(" “Thanh Hoa thảo cái gì thuộc tính?” Không để ý tới của hắn không cao hứng, Trương Huyền hỏi."),i("br"),i("br"),n._v(" “Thanh Hoa thảo, toàn thân xanh xám, tính ôn.” Hồng Vân đan sư trả lời."),i("br"),i("br"),n._v(" “Kim Tiền hoa đây?” "),i("br"),i("br"),n._v(" “Kim Tiền hoa, kim ngân hai màu, cũng bị người thành là kim hoa ngân diệp, cũng là trung hoà tính dược liệu.” "),i("br"),i("br"),n._v(" “Này hai loại dược liệu, tuy rằng đều là trung tính dược liệu, dung hợp lên, lại sẽ phát sinh dị biến, biến thành cực kỳ táo bạo thuộc tính.” Trương Huyền nói."),i("br"),i("br"),n._v(" “Cái này ta biết, Tinh Nguyên đan đan phương, truyền thừa rất lâu, đều có hai thứ này dược liệu, nếu như thật vì vì chúng nó dung hợp mà nổ lô, những người khác cũng sẽ nổ.” Hồng Vân đan sư lắc đầu: “Hơn nữa, ta đặt thuốc trình tự không có bất luận cái gì sai lầm, hai thứ này dược liệu bên trong còn thả một cái Tử Hương thảo đến trung hoà, nghiêm ngặt dựa theo bước đi, về thời gian cũng không có một chút nào sai lầm!” "),i("br"),i("br"),n._v(" Hắn chuyên môn cùng người khác hỏi qua Tinh Nguyên đan luyện chế, mỗi một loại dược liệu, mỗi một cái bước đi, đều giống như đúc, nhưng kết quả nhưng có rất lớn sai biệt, lúc này mới để hắn tất cả đều là nghi hoặc."),i("br"),i("br"),n._v(" Đối phương nói này hai loại trung tính dược liệu dung hợp sẽ táo bạo, hắn cũng biết, đồng thời sớm dự phòng, không thể là cái này nguyên nhân."),i("br"),i("br"),n._v(" “Ta không nói ngươi trình tự không đúng, cũng không nói này hai loại dược liệu có vấn đề!” Trương Huyền lạnh nhạt nói."),i("br"),i("br"),n._v(" “Cái kia có ý gì?” "),i("br"),i("br"),n._v(" Không những Hồng Vân đan sư nghi hoặc, những người khác cũng tràn đầy kỳ quái."),i("br"),i("br"),n._v(" Nếu không phải vấn đề này, luôn có thuyết pháp chứ?"),i("br"),i("br"),n._v(" Không phải vậy, làm sao có khả năng luyện chế cố gắng, sẽ nổ lô?"),i("br"),i("br"),n._v(" Hơn nữa... Nếu này hai loại dược không thành vấn đề, ngươi hỏi làm cái gì?"),i("br"),i("br"),n._v(" Hai tay một lưng, không để ý tới mọi người kỳ quái, Trương Huyền vài bước đi tới bạo tạc đan lô trước mặt, cách đó không xa đất bên trên, vừa vặn còn có không dùng hết hai cây Thanh Hoa thảo cùng Kim Tiền hoa."),i("br"),i("br"),n._v(" “Thanh Hoa thảo cùng Kim Tiền hoa đều tính ôn, dung hợp phía sau sẽ táo bạo... Gia nhập Tử Hương thảo trung hoà, liền có thể giải quyết cái này mầm họa, hết thảy luyện đan sư đều sẽ hắn xem là chân lý, nói thật, làm như vậy cũng xác thực không có sai lầm, nhưng... Người khác làm đều chính xác, liền ngươi làm không được!” "),i("br"),i("br"),n._v(" Trương Huyền nhẹ nhàng nở nụ cười."),i("br"),i("br"),n._v(" “Ta không được? Lẽ nào ta còn có cái gì đặc thù?” Hồng Vân đan sư lông mày vừa nhíu."),i("br"),i("br"),n._v(" Ta cũng là đường đường chính chính thi đậu nhị tinh luyện đan sư, hơn nữa tại cái đội ngũ này bên trong, thực lực còn không thấp, dựa vào cái gì không được?"),i("br"),i("br"),n._v(" “Không sai, của ngươi xác thực đặc thù, nếu như ta không nhìn lầm, ngươi nên chưa bao giờ ăn qua Tử Hương thảo làm chủ dược luyện chế thuốc đi!” Trương Huyền nhìn sang."),i("br"),i("br"),n._v(" “Ây... Ta đối với Tử Hương thảo có chút dị ứng, không thể dùng...” "),i("br"),i("br"),n._v(" Hồng Vân đan sư gật đầu."),i("br"),i("br"),n._v(" “Kỳ thực không phải dị ứng, mà là chân khí của ngươi thuộc tính cùng với tương khắc...” Trương Huyền hai tay một lưng: “Ngươi tu luyện Hồng Viêm quyết, trời sinh mang hỏa, mà Tử Hương thảo sinh trưởng ở trong nước, là thuộc tính” Thủy “dược liệu, chân khí cùng với dung hợp, rất dễ dàng đem hắn bên trong thuộc tính” Thủy “hóa giải, để thuốc mất đi tác dụng. Người khác luyện chế Tinh Nguyên đan, Tử Hương thảo hiệu quả vẫn tại, ngươi lại không tác dụng, hai loại dược liệu mất đi trung hoà, đương nhiên phải phát sinh phản ứng, dẫn tới nổ lô!” "),i("br"),i("br"),n._v(" “Ngươi... Làm sao biết ta tu luyện Hồng Viêm quyết?” "),i("br"),i("br"),n._v(" Hồng Vân đan sư sắc mặt trắng nhợt."),i("br"),i("br"),n._v(" Hồng Viêm quyết là gia tộc hắn truyền thừa công pháp, từ nhỏ liền tu luyện, đến nay đã mấy chục năm, xác thực là thuộc tính “Lửa”, vận chuyển chân khí, liệt diễm chói chang, nhưng đây là bí mật của riêng hắn, chưa bao giờ cùng người ngoài nhắc qua, liền ngay cả cùng một chỗ không ít bạn tốt đều không biết, cái tên này làm sao sẽ một ngụm nói toạc ra?"),i("br"),i("br"),n._v(" “Ngươi không cần phải để ý đến ta là cái gì biết được, nếu như không tin, có thể thử đưa ngươi tinh luyện Tử Hương thảo, gia nhập Thanh Hoa thảo, Kim Tiền hoa, nếu như không nổ tung, liền nói rõ ta lời giải thích là sai lầm.” Trương Huyền cũng không giải thích."),i("br"),i("br"),n._v(" Chỉ cần luyện đan, Thiên Đạo thư viện liền có thể sinh thành thư tịch, đừng nói công pháp, liền ngay cả môn công pháp này thiếu hụt, thật lời muốn nói, đều có thể giảng rõ ràng, không tính là gì."),i("br"),i("br"),n._v(" Hồng Vân đan sư nghi hoặc một hồi, vẫn là quyết định thử xem, lấy ra Thanh Hoa thảo, Kim Tiền hoa cùng với Tử Hương thảo đồng thời luyện hóa thành nước thuốc, ba loại nước thuốc dung hợp."),i("br"),i("br"),n._v(" Ầm!"),i("br"),i("br"),n._v(" Quả nhiên, cùng đối phương nói như thế, lập tức nổ tung."),i("br"),i("br"),n._v(" “Chuyện này...” "),i("br"),i("br"),n._v(" Hồng Vân đan sư thân thể run rẩy."),i("br"),i("br"),n._v(" Tinh Nguyên đan hắn đã học giỏi mấy năm, vẫn chưa thành công, vốn tưởng rằng là luyện đan bước đi, dược liệu phối hợp có vấn đề, nằm mơ đều không nghĩ tới, lại có thể là chân khí hóa giải Tử Hương thảo dược tính..."),i("br"),i("br"),n._v(" Không phải chính tai nghe nói, đều không thể tin được là thật sự."),i("br"),i("br"),n._v(" “Có thể có phương pháp giải quyết?” "),i("br"),i("br"),n._v(" Chân kinh qua đi, Hồng Vân đan sư không nhịn được nhìn về phía cách đó không xa thanh niên."),i("br"),i("br"),n._v(" Giờ khắc này, hắn đối với người này dĩ nhiên tâm phục khẩu phục."),i("br"),i("br"),n._v(" Vốn là dự định lại đây thủ tiêu Trương Huyền tư cách, hiện tại loại ý nghĩ này nửa điểm đều không sinh được đến."),i("br"),i("br"),n._v(" Chỉ bằng luyện đan, liền xem ra bản thân công pháp tu luyện, cùng thuốc xung đột, chân chính luyện đan kỹ thuật thế nào không biết, chỉ bằng vào nhãn lực, coi như sát hạch nhị tinh, e sợ đều dư dả."),i("br"),n._v(" “Đường đường nhị tinh luyện đan sư, đã biết nguyên nhân, lẽ nào liền như thế đơn giản phương pháp giải quyết cũng không tìm tới?” Trương Huyền mặt lộ vẻ không vui."),i("br"),i("br"),n._v(" Thư viện có thể nhìn ra thiếu hụt, cũng không thể cấp cho hắn chính xác phương pháp giải quyết, đương nhiên, này không thể nói ra được... Vạn nhất đối phương cảm thấy ngươi không nói là, chống chế không trả thù lao làm sao bây giờ?"),i("br"),i("br"),n._v(" Như thế khó khăn trả lời vấn đề của hắn, là vì tiền, bằng không nhàn da đau?"),i("br"),i("br"),n._v(" “Thụ giáo...” "),i("br"),i("br"),n._v(" Nghe được quát lớn, Hồng Vân đan sư hơi đỏ mặt."),i("br"),i("br"),n._v(" Cũng đúng, hắn là nhị tinh luyện đan sư, chuyện này làm sao còn không thấy ngại hỏi..."),i("br"),i("br"),n._v(" Tử Hương thảo bất quá là trung hoà dược tính, chỉ cần tìm một mực cùng mình chân khí không xung đột, lại có thể thay thế dược liệu liền mới có thể giải quyết."),i("br"),i("br"),n._v(" “Chuyện này...” "),i("br"),i("br"),n._v(" Nhìn thấy vừa tới thời điểm, hung hăng không gì sánh được, thô bạo vô song Hồng Vân đan sư, giờ khắc này cùng học sinh tiểu học như thế, bị quát lớn không dám phản bác, Âu Dương hội trưởng từng cái từng cái nắm lấy tóc, hận không thể gặp trở ngại."),i("br"),i("br"),n._v(" Có ngốc bọn họ cũng biết, Trương Huyền nói khẳng định hoàn toàn đúng, không có sai lầm, không đúng vậy không thể làm cho đối phương như vậy tin phục."),i("br"),i("br"),n._v(" Một cái vừa mới lên cấp nhất tinh luyện đan sư, giáo huấn nhị tinh đan sư, còn để hắn không dám nói lời nào..."),i("br"),i("br"),n._v(" Mọi người dùng sức yết nước bọt."),i("br"),i("br"),n._v(" “Âu Dương hội trưởng, vừa nãy Hồng Vân đan sư mà nói... Chắc hẳn ngươi cũng nghe được, số tiền này vẫn là cho ta đi, còn có ta sớm giao một trăm vạn tiền thế chấp...” "),i("br"),i("br"),n._v(" Không để ý tới mọi người khiếp sợ, Trương Huyền quang minh chính đại đi tới Âu Dương hội trưởng trước mặt, đưa tay đem phía trước cho kim phiếu vồ tới."),i("br"),i("br"),n._v(" “...” "),i("br"),i("br"),n._v(" Mọi người không còn gì để nói."),i("br"),i("br"),n._v(" Ngươi như thế trâu bò luyện đan sư, tùy tiện bán mấy cái đan dược không phải tiền đến rồi, vì chút tiền này cho tới à?"),i("br"),i("br"),n._v(" Không để ý tới bọn họ ánh mắt cổ quái, Trương Huyền đem tiền thu cẩn thận, thoả mãn gật gật đầu."),i("br"),i("br"),n._v(" Vốn là vì hai thứ đồ này, chung quanh nghĩ biện pháp kiếm tiền, không ngại ngụy trang thành danh sư, khắp nơi làm cho người ta chỉ điểm... Kết quả... Dược liệu đến rồi, tiền không tốn..."),i("br"),i("br"),n._v(" Biết sớm như vậy, trang cái gì danh sư? Dẫn đến mình mệt mỏi đến cùng tôn tử như thế..."),i("br"),i("br"),n._v(" Trương Huyền tràn đầy phiền muộn."),i("br"),i("br"),n._v(" Hồng Vân đan sư thi kiểm tra xong, biết trước mắt người thanh niên này có chân tài thực học, không tiếp tục ngăn trở."),i("br"),i("br"),n._v(" Rời đi luyện đan sư công hội, Trương Huyền hướng học viện đi đến."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v(" Nghe được “Dương Huyền” danh sư xuất hiện tin tức, Lục Tầm cùng Vương Siêu cũng không ngồi yên được nữa, vội vội vàng vàng hướng giáo đi ra ngoài, mới vừa đi tới cửa, liền nhìn thấy một người thanh niên phờ phạc đi tới."),i("br"),i("br"),n._v(" Cái tên này ánh mắt đỏ như máu, tóc tùm la tùm lum, nhìn chút chính là chừng mấy ngày không ngủ."),i("br"),i("br"),n._v(" “Lục Tầm, là Trương Huyền...” Vương Siêu lặng lẽ nhỏ giọng."),i("br"),i("br"),n._v(" “Hắn?” Lục Tầm ngẩng đầu liếc mắt nhìn, không nhịn được lắc đầu, lộ ra vẻ khinh bỉ: “Đường đường học viện giáo sư, lôi thôi lếch thếch, không chú trọng dáng vẻ, thật không nghĩ ra, lúc trước học viện vì sao đem người như thế chiêu lại đây!” "),i("br"),i("br"),n._v(" “Ta cảm thấy cái tên này khẳng định là bị sự khiêu chiến của ngươi dọa sợ, những ngày qua vẫn nghĩ đối sách, lúc này mới không ngớt không ngủ, biến thành như vậy...” "),i("br"),i("br"),n._v(" Vương Siêu cười nói."),i("br"),i("br"),n._v(" Lục Tầm lão sư là ai?"),i("br"),i("br"),n._v(" Toàn bộ Hồng Thiên học viện nổi danh nhất minh tinh giáo sư, muốn bái sư học viên, đều xếp thành đội."),i("br"),i("br"),n._v(" Bị hắn khiêu chiến, coi như trưởng lão đô đầu bên trên đổ mồ hôi, một cái năm ngoái mới đến nho nhỏ lão sư mà thôi, khẳng định đã sớm sợ đến tè ra quần."),i("br"),i("br"),n._v(" Không phải vậy, làm sao sẽ dáng vẻ ấy?"),i("br"),i("br"),n._v(" “Nghĩ đối sách?” Lục Tầm lão sư lắc lắc đầu, trong mắt lộ ra nồng nặc tự tin: “Hồng Thiên học viện, muốn đang dạy học giảng bài vượt qua ta... Nằm mơ mà thôi!” "),i("br"),i("br"),n._v(" “Đúng đấy, nói thật, cũng trách hắn xui xẻo, ngươi vừa vặn muốn tìm lý do, quật khởi mạnh mẽ, hấp dẫn danh sư chú ý, hắn liền nhô ra, đáng đời phải bị làm mất mặt...” Vương Siêu nở nụ cười."),i("br"),i("br"),n._v(" Thẩm Truy bệ hạ mời Lưu sư mấy người, tuy rằng bí ẩn, đối ngoại tuyên bố đi ra ngoài săn bắn, trên thực tế không ít tin tức linh thông người, đã sớm biết."),i("br"),i("br"),n._v(" Vì hấp dẫn danh sư chú ý, trở thành học đồ, Lục Tầm đã sớm chuẩn bị, coi như không có Vương Đào, Vương Nham lùi khóa một chuyện, cũng nhất định sẽ tìm lý do, quật khởi mạnh mẽ, vị này Trương Huyền bản thân đem mặt đưa ra để hắn đánh, đương nhiên phải động thủ."),i("br"),i("br"),n._v(" “Đi thôi! Nhanh tìm Dương sư, không phải vậy chậm, không biết muốn xếp hàng tới khi nào đi.” "),i("br"),i("br"),n._v(" Lục Tầm khoát tay áo một cái."),i("br"),i("br"),n._v(" Đối với hắn mà nói, Trương Huyền chỉ là hắn trở thành danh sư trên đường một cái khúc nhạc dạo ngắn mà thôi, không quan hệ đau khổ, căn bản không có cái gọi là cừu hận cùng lửa giận."),i("br"),i("br"),n._v(" “Đúng đấy, Dương sư mới là chúng ta chắc là phấn đấu mục tiêu, cái này Trương Huyền bất quá tiểu học viện tiểu nhân vật mà thôi...” "),i("br"),i("br"),n._v(" Vương Siêu cũng gật gù, không tiếp tục để ý, hai người khí vũ hiên ngang theo Trương Huyền trước mặt đi qua, không hề liếc mắt nhìn một chút."),i("br"),i("br"),n._v("..."),i("br"),i("br"),n._v(" Nhìn thấy trước mặt đến hai vị lão sư cố ý nâng lên đầu, một bộ cao cao tại thượng, nhẹ như mây gió, không chọc ghẹo bụi trần dáng dấp, Trương Huyền tràn đầy nghi hoặc."),i("br"),i("br"),n._v(" Này hai ngốc tử... Ai vậy?"),i("br"),i("br"),n._v(" Tuy rằng cùng Lục Tầm lão sư ký xuống tỷ thí, nhưng hắn chưa từng gặp đối phương, căn bản không biết vị này chính là."),i("br"),i("br"),n._v(" Hồng Thiên học viện lão sư liền có mấy trăm, hắn năm ngoái mới đến, lại lăn lộn không được, không quen biết vị này minh tinh giáo sư cũng rất bình thường."),i("br"),i("br"),n._v("Convert by: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-186"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-188"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);