(window.webpackJsonp=window.webpackJsonp||[]).push([[982],{1337:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1858-trinh-duong-gap-ban-gai-ha"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1858-trinh-duong-gap-ban-gai-ha"}},[n._v("#")]),n._v(" Chương 1858: Trịnh Dương gặp bạn gái (hạ)")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1858: Trịnh Dương gặp bạn gái (hạ)"),t("br"),t("br"),n._v("Trịnh Dương vò đầu, tràn đầy xấu hổ."),t("br"),t("br"),n._v("“Ta đồng ý!”"),t("br"),t("br"),n._v("Thấy hắn bộ dáng này, Ngụy Như Yên lắc đầu, trong mắt mang theo mong đợi: “Cần ta hạ độc ư? Bảo đảm lập tức liền chết, không có bất cứ dấu vết gì, còn có thể hỗ trợ hủy thi diệt tích, độc chết một nhà, cắt đứt trả thù khả năng...”"),t("br"),t("br"),n._v("“Không cần...”"),t("br"),t("br"),n._v("Run run thoáng cái, Trịnh Dương đột nhiên cảm thấy, tìm vị sư muội này, có vẻ như có chút không quá ổn thoả."),t("br"),t("br"),n._v("“Không cần khách khí, nếu như bây giờ không muốn hạ độc chết, còn có thể cho ngươi luyện chế một ít, biến thành khôi lỗi dược vật, sau khi phục dụng, cam tâm tình nguyện trở thành ngươi đồ chơi, sẽ không phản kháng...”"),t("br"),t("br"),n._v("Ngụy Như Yên khuôn mặt không thay đổi."),t("br"),t("br"),n._v("“Khụ khụ...” Trịnh Dương một mặt nghiêm mặt: “Sư muội, ta đột nhiên nhớ tới, ta còn có chuyện khác, ngươi nhìn ngươi cũng rất bận, liền không phiền toái, cáo từ...”"),t("br"),t("br"),n._v("Xoay người rời đi."),t("br"),t("br"),n._v("Không hổ là Độc điện điện chủ... Thực tế không thể trêu vào!"),t("br"),t("br"),n._v("Còn chưa đi xa, sau lưng tiếng gió rít gào, một cái nhàn nhạt thân ảnh tiếp tục đi theo tới, quay đầu nhìn lại, ba hồn dọa rơi mất hai hồn."),t("br"),t("br"),n._v("“Sư muội...”"),t("br"),t("br"),n._v("Trịnh Dương phát khổ."),t("br"),t("br"),n._v("“Tốt, không cần nói nhảm, ta chỉ là thuận miệng nói một chút, cũng không phải sát nhân cuồng! Coi như muốn giết người, cũng phải là Cổ Thánh mới được, người bình thường, còn không có hứng thú gì!”"),t("br"),t("br"),n._v("Hừ một tiếng, Ngụy Như Yên nói."),t("br"),t("br"),n._v("Nàng cũng chỉ là thuận miệng nói một chút mà thôi, mặc dù thân là Độc điện điện chủ, nhưng cũng không phải lạm sát kẻ vô tội người, làm sao có thể tiện tay liền đem người hạ độc chết."),t("br"),t("br"),n._v("Thật muốn làm như vậy, đoán chừng lão sư cái thứ nhất trước đem nàng quất chết."),t("br"),t("br"),n._v("“Nhưng mà...” Trịnh Dương một mặt phát khổ."),t("br"),t("br"),n._v("Tuy là cũng biết nàng khẳng định là đang nói đùa, có thể như vậy một cái bất cứ lúc nào cũng sẽ phóng độc cường giả, cũng không dám tùy tiện dùng ah!"),t("br"),t("br"),n._v("Ngộ nhỡ tức giận, hạ độc chết một mảnh, thật sự chết vô ích khó từ tội lỗi."),t("br"),t("br"),n._v("“Không có nhưng mà, phía trước dẫn đường, lại nói nhảm, trước tiên đem ngươi độc câm!”"),t("br"),t("br"),n._v("Ngụy Như Yên tay ngọc vẫy một cái."),t("br"),t("br"),n._v("“...” Trịnh Dương khóc lên."),t("br"),t("br"),n._v("Chuyện này là sao."),t("br"),t("br"),n._v("Sớm biết cũng không cùng nàng nhiều lời... Lần này tốt, lên thuyền giặc, đều xuống không nổi."),t("br"),t("br"),n._v("Hai người rất nhanh bay ra Hồng Thiên học viện phạm vi, thời gian không dài đi tới một chỗ, ngừng lại, xuất hiện ở trước mắt chính là một mảnh to lớn phủ đệ, liên miên chập trùng, không biết bao xa."),t("br"),t("br"),n._v("“Không đúng...”"),t("br"),t("br"),n._v("Đứng tại trên không nhìn chung quanh một vòng, Trịnh Dương lơ ngơ."),t("br"),t("br"),n._v("“Làm sao vậy?” Ngụy Như Yên đi tới trước mặt."),t("br"),t("br"),n._v("“Trước kia ta ở nơi này, là một mảnh khu bình dân, làm sao biến thành như vậy lớn phủ đệ?”"),t("br"),t("br"),n._v("Trịnh Dương một mặt kỳ quái rơi xuống."),t("br"),t("br"),n._v("Gia đình hắn điều kiện cũng không như thế nào, nếu không, có chính quy giáo dục, cũng không đến mức liền Vương Siêu lớp học đều thi không đậu."),t("br"),t("br"),n._v("Một năm rưỡi phía trước, nơi này chỉ là dân thường khu, lúc nào toát ra như vậy lớn một cái phủ đệ?"),t("br"),t("br"),n._v("“Cái này còn không đơn giản, tìm người hỏi một chút không được sao...”"),t("br"),t("br"),n._v("Khẽ hừ một tiếng, Ngụy Như Yên rơi xuống, mấy bước đi tới một cái trà vị trước mặt, nhìn về phía chủ quán: “Vị bằng hữu này, ta muốn hỏi thoáng cái, tòa phủ đệ này, có phải hay không vừa xây xong?”"),t("br"),t("br"),n._v("Chủ quán vốn không muốn chú ý, nhìn thấy cô gái trước mắt dung mạo, lập tức lộ ra vui vẻ: “Cô nương là vừa tới Thiên Huyền thành đi! Tòa phủ đệ này, một năm trước liền bắt đầu xây dựng, không được một tháng liền xây dựng hoàn thành, mời đều là đứng đầu nhất công tượng, ngươi nhìn viện này hạ bố cục còn có kiến trúc, nghe nói còn vận dụng lâm viên sư, quả thực so hoàng cung đều muốn khí phách!”"),t("br"),n._v(" “Còn vận dụng lâm viên sư?”"),t("br"),t("br"),n._v("Ngụy Như Yên cùng Trịnh Dương nhìn nhau."),t("br"),t("br"),n._v("Người khác không biết Đạo Viện lâm sư, hai người bọn họ nhưng mà biết, thưa thớt vô cùng, coi như Trương gia loại này đại gia tộc, tìm tới một vị cũng không quá dễ dàng, nơi này toát ra một cái, xây dựng biệt thự?"),t("br"),t("br"),n._v("“Đây là ai nhà ở?”"),t("br"),t("br"),n._v("Cũng lại kìm nén không được, Trịnh Dương tò mò hỏi."),t("br"),t("br"),n._v("“Tô gia!” Chủ quán nói."),t("br"),t("br"),n._v("“Tô?” Trịnh Dương tràn đầy kỳ lạ: “Thiên Huyền vương thành, lúc nào xuất hiện cái Tô gia?”"),t("br"),t("br"),n._v("Thiên Huyền vương thành tứ đại gia tộc, Vương, Lưu, Bạch, Đỗ, mỗi một cái đều tên tuổi lẫy lừng, chưa từng nghe nói qua cái gì Tô gia a?"),t("br"),t("br"),n._v("“Tô gia ngươi đều không biết? Gia chủ Tô Mậu Thanh!”"),t("br"),t("br"),n._v("Thấy hắn vẻ mặt vô cùng nghi hoặc, chủ quán nhíu nhíu mày."),t("br"),t("br"),n._v("“Tô Mậu Thanh?” Trịnh Dương thân thể thoáng một cái, giống như là nghe được cái gì khó tin sự tình: “Hắn... Con gái nhưng mà Tô Phi Phi?”"),t("br"),t("br"),n._v("“Đúng vậy a!”"),t("br"),t("br"),n._v("Chủ quán gật đầu: “Nếu không phải nữ nhi của hắn tìm cái con rể tốt, hắn Tô Mậu Thanh có thể ở lại như vậy tòa nhà? Không nói gạt ngươi, hai năm trước, hắn đến nơi này của ta, liền trà đều uống không nổi, nghèo đều không có mấy bộ y phục, hiện tại vung tiền như rác, nghe nói hôm qua ăn bữa cơm, tốn trọn vẹn hơn mười viên kim tệ...”"),t("br"),t("br"),n._v("“Con rể?” Ngắt lời đối phương, Trịnh Dương nắm đấm xiết chặt."),t("br"),t("br"),n._v("“Đúng vậy a, xem ra ngươi không rõ ràng, có điều, hỏi ta liền hỏi đúng, ta ở đây làm ăn hơn mười năm, bốn phía sự tình, thứ nào đều biết rõ ràng!”"),t("br"),t("br"),n._v("Chủ quán giật ra máy hát, có loại không dừng được xu thế: “Là nữ nhi của hắn Tô Phi Phi, không biết làm sao biết cái gì đế quốc công tử ca, đối phương lại có tiền, lại mạnh mẽ, cho nên mới có cái này biệt thự, nếu không, ngươi cho rằng, chỉ bằng vào bọn họ hai cha con, có thể mua được như vậy lớn địa phương?”"),t("br"),t("br"),n._v("“Ta đã biết...”"),t("br"),t("br"),n._v("Trịnh Dương lắc đầu, dường như có cái gì khúc mắc sắp thả ra."),t("br"),t("br"),n._v("“Cái này Tô Phi Phi chính là trước ngươi ưa thích nữ hài? Thoạt nhìn, ánh mắt cũng không thế nào đi!”"),t("br"),t("br"),n._v("Khẽ cười một tiếng, Ngụy Như Yên truyền âm tới."),t("br"),t("br"),n._v("Cái gì đế quốc công tử ca, liền xem như hoàng đế bệ hạ, cùng Trịnh Dương so ra, lại coi là cái gì?"),t("br"),t("br"),n._v("Vị này Tô Phi Phi, vậy mà coi trọng như vậy người, bỏ qua trước mắt châu ngọc, thật không biết nghĩ như thế nào."),t("br"),t("br"),n._v("“Ta...” Trịnh Dương nói không ra lời."),t("br"),t("br"),n._v("Lúc trước hắn bái lão sư trước đó, vừa mới chịu đựng được ngăn trở, thổ lộ bị cự tuyệt, lúc này mới dẫn đến cương mãnh thương pháp trở nên do dự không tiến, cuối cùng chưa đi đến vào Vương Siêu lão sư môn đình."),t("br"),t("br"),n._v("“Mau nhìn, bọn họ đi ra...”"),t("br"),t("br"),n._v("Nhưng vào lúc này, trước mắt phủ đệ cửa ngõ, “Kẹt kẹt!” Một tiếng từ từ mở ra, một cái dung mạo tuấn tú thiếu nữ, kéo một người trung niên cánh tay, chậm rãi đi ra."),t("br"),t("br"),n._v("Thiếu nữ không tính quá đẹp, trung thượng chi tư, đừng nói Ngụy Như Yên, Triệu Nhã đám người, liền Thẩm Bích Như bọn người kém xa tít tắp, bên người trung niên nhân, thì toàn thân mập mạp, dung mạo xấu xí, dáng người thoạt nhìn, so Viên Đào đều muốn khoa trương."),t("br"),t("br"),n._v("Kéo như vậy thanh niên, đổi lại người bình thường, đều sẽ cảm giác đến xấu hổ, mà nữ hài thì hùng dũng oai vệ khí phách hiên ngang, giống như là kéo cao quý anh tuấn hoàng tử đồng dạng, trên mặt không nói ra được tự hào cùng kiêu ngạo."),t("br"),t("br"),n._v("Ngụy Như Yên cười nhạo: “Xấu như vậy, già như vậy, cũng có thể gọi công tử ca?”"),t("br"),t("br"),n._v("Dưới cái nhìn của nàng, cái gọi là công tử ca, đều là anh tuấn phóng khoáng, tiêu tiền như nước, không nghĩ tới lại béo lại xấu, mấu chốt còn già, cô bé này làm sao coi trọng đối phương?"),t("br"),t("br"),n._v("“Chúng ta đi thôi!”"),t("br"),t("br"),n._v("Thở ra một hơi, Trịnh Dương quay đầu nói."),t("br"),t("br"),n._v("Đây là hắn ưa thích cái thứ nhất nữ hài, thầm mến không dưới năm năm, lần này trở về, chỉ muốn gặp một lần, hóa giải chấp niệm trong lòng, đã nhìn thấy, hơn nữa qua... Rất hạnh phúc, cũng liền thỏa mãn, khúc mắc có thể mở ra."),t("br"),t("br"),n._v("“Đi? Trò hay còn không có nhìn, sao có thể đi?”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Ngụy Như Yên khóe miệng nâng lên."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1869"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1871"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);