(window.webpackJsonp=window.webpackJsonp||[]).push([[983],{1340:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1859-chim-cung-rung"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1859-chim-cung-rung"}},[n._v("#")]),n._v(" Chương 1859: Chim cùng rừng")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Bất kể nói thế nào, Trịnh Dương đều là sư huynh của nàng, lúc trước cảm mến cùng đối phương, tìm đường đường chính chính sinh hoạt ngược lại cũng thôi, hiện tại tìm bụng phệ trung niên nhân, bất kể nói thế nào, đều cho rằng khó chịu!"),t("br"),t("br"),n._v("Để nàng đường đường Độc điện điện chủ khó chịu, đối phương thế nào thoải mái lên?"),t("br"),t("br"),n._v("“Sư muội, đừng làm loạn, bọn họ chỉ là người bình thường...”"),t("br"),t("br"),n._v("Không nghĩ tới Ngụy Như Yên không muốn đi, còn một bộ không sợ phiền phức lớn hình dáng, Trịnh Dương trong lòng sinh ra linh cảm không lành."),t("br"),t("br"),n._v("“Không sao, sẽ không chết người!”"),t("br"),t("br"),n._v("Không tiếp tục để ý, Ngụy Như Yên ngồi ở trà quán trên chỗ ngồi, một mặt nụ cười ngọt ngào: “Ông chủ, tới một bình trà!”"),t("br"),t("br"),n._v("Nói xong, một bộ người không việc gì đồng dạng yên tĩnh ngồi xuống, cẩn thận phẩm trà."),t("br"),t("br"),n._v("Thấy không rời đi, biết cưỡng ép không được, Trịnh Dương đành phải đỡ lấy cái trán, cũng đầy mặt bất đắc dĩ ngồi ở bên cạnh."),t("br"),t("br"),n._v("“Tường ca, chúng ta thật muốn đi hoàng cung?” Tô Phi Phi nhìn về phía bên cạnh bụng phệ trung niên nhân."),t("br"),t("br"),n._v("“Để Thẩm Truy cái tên này, cho ta kiếm nhiều tử sĩ tới, đến bây giờ đều không có làm được, ta nhìn cái tên này quốc vương chi vị, không muốn làm!”"),t("br"),t("br"),n._v("Gọi là Tường ca béo trung niên nhân hừ lạnh, trong mắt lóe lên nhất đạo tàn nhẫn."),t("br"),t("br"),n._v("“Dám vi phạm Tường ca lời nói, cái này Thẩm Truy lá gan quả thực đủ lớn, không hảo hảo dạy bảo, khả năng thật đúng là cho rằng, bản thân là cái đế quốc hoàng đế!” Tô Phi Phi che miệng cười khẽ, ánh mắt lộ ra nồng đậm khinh thường."),t("br"),t("br"),n._v("Trong mắt người khác, tôn kính vô cùng quốc vương bệ hạ, dưới cái nhìn của nàng, đã không tính là gì."),t("br"),t("br"),n._v("“Không tệ, chỉ là cái vương quốc nho nhỏ quốc vương bệ hạ mà thôi, thật đúng là đem mình làm nhân vật, cũng chính là ta không muốn đem sự tình làm lớn chuyện, nếu không, cái tên này đã sớm chết cơ hội tốt...”"),t("br"),t("br"),n._v("Tường ca cười lạnh, vừa định nói chuyện, đột nhiên da mặt bỗng nhiên co lại, đầu gối mềm nhũn, “Phù phù!” Một tiếng, quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Tường ca...”"),t("br"),t("br"),n._v("Giật nảy mình, Tô Phi Phi tràn đầy kinh hoàng."),t("br"),t("br"),n._v("Trước mắt vị này thực lực, nàng biết rất rõ ràng, so Thẩm Truy bệ hạ đều cường đại hơn, vượt xa quá chí tôn cảnh, giờ phút này chỉ một cái quỳ xuống... Muốn nói không có vấn đề, tuyệt không có khả năng."),t("br"),t("br"),n._v("“Ai ám toán ta?”"),t("br"),t("br"),n._v("Không nghĩ tới sẽ xuất hiện loại tình huống này, Tường ca vẻ mặt trở nên dữ tợn, nhìn quanh một vòng, cắn răng nói."),t("br"),t("br"),n._v("“Ngươi trúng , muốn sống, cần phải có người cam tâm tình nguyện, dâng hiến ra bên trong thân thể máu tươi, tới thay đổi, nếu không, thời gian một nén nhang phía trong, thất khiếu chảy máu mà chết, thần tiên khó cứu!”"),t("br"),t("br"),n._v("Một cái thanh âm nhàn nhạt vang lên."),t("br"),t("br"),n._v("Theo thanh âm, Tường ca vội vàng ngẩng đầu, ngay sau đó nhìn thấy một thiếu nữ ngồi ngay ngắn ở trà trước sạp, chén trà trong tay bốc hơi nóng."),t("br"),t("br"),n._v("“Ngươi... Là ai? Hai ta không oán không cừu, vì sao muốn giết ta?”"),t("br"),t("br"),n._v("Không nghĩ tới đối phương có thể cách hắn xa như vậy cho hắn hạ độc, để hắn không có chút nào năng lực phản kháng, hơn nữa khí tức cao thâm khó dò, một cái khó mà nhìn thấu, Tường ca biết, khẳng định không cách nào đối kháng, không dám hành động thiếu suy nghĩ, hàm răng cắn chặt."),t("br"),t("br"),n._v("“Trên thế giới nào có nhiều như vậy thù oán, chẳng qua là cảm thấy ngươi không vừa mắt mà thôi!”"),t("br"),t("br"),n._v("Một tay khẽ lướt qua sợi tóc, Ngụy Như Yên đập một ngụm trà đậm, thanh âm lãnh đạm: “Thời gian của ngươi không nhiều, muốn sống, cũng nhanh chút, đừng trách ta không có nhắc nhở!”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Chân khí ở trong người vận chuyển, va chạm mấy lần, phát hiện cái này độc căn vốn không pháp hóa giải, chỉ sợ cùng đối phương nói tới đồng dạng, cần phải có người thay đổi máu tươi, sắc mặt tái xanh, quay đầu nhìn về phía một bên Tô Phi Phi: “Phi Phi, ngươi tới!”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Biết đối phương nghĩ cái gì, Tô Phi Phi sợ tới mức vẻ mặt trắng bệch, liên tiếp lui về phía sau."),t("br"),t("br"),n._v("Sinh tử phía trước, tình yêu còn khó mà kháng trụ, huống chi loại này."),t("br"),t("br"),n._v("“Thế nào, ngươi muốn làm trái với mệnh lệnh của ta?” Thấy nàng rời đi, Tường ca ánh mắt híp lại."),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Tô Phi Phi run rẩy."),t("br"),n._v(" “Nông thôn dã nha đầu mà thôi, chẳng lẽ thật đúng là cho rằng, bản thân rất cao quý? Cho ngươi vinh hoa phú quý, liền muốn biết sẽ bỏ ra cái gì! Nhanh lên một chút tới, thay đổi máu tươi của ngươi, nếu như ta có thể khôi phục, còn có thể cùng trước kia đồng dạng cưng chiều ngươi, nếu không, ta sẽ hiện tại liền giết ngươi...”"),t("br"),t("br"),n._v("Lông mày giương lên, Tường ca trong mắt đằng đằng sát khí."),t("br"),t("br"),n._v("Cái gì Tô Phi Phi, cái gì Tô Mậu Thanh, đối với hắn mà nói, bất quá là có thể tùy ý sử dụng công cụ thôi."),t("br"),t("br"),n._v("“Đừng...”"),t("br"),t("br"),n._v("Biết thay đổi máu tươi, có thể sẽ trực tiếp tử vong, Tô Phi Phi lần nữa lui về phía sau, vừa lui lại mấy bước, liền cảm thấy một cỗ lực lượng cuốn tới, Tường ca khí tức đã đem hắn bao phủ, còn dám lui về phía sau, tuyệt đối sẽ bị tại chỗ đánh chết."),t("br"),t("br"),n._v("Ánh mắt lập tức ảm đạm xuống, Tô Phi Phi tràn đầy đau buồn."),t("br"),t("br"),n._v("Một năm trước, từng có cái thành tâm người thích nàng, hướng nàng thổ lộ, chỉ vì nghèo khổ chi thân, bị tại chỗ cự tuyệt... Ai biết vị này, bái cái tốt lão sư, một đường bước mây xanh, liền Thẩm Truy bệ hạ, đều phong làm khách quý."),t("br"),t("br"),n._v("Vốn cho rằng, cơ hội như vậy mất đi, kết quả một năm trước gặp vị này Tường ca."),t("br"),t("br"),n._v("Đối phương đối nàng rất tốt, càng là vì đó xây dựng lớn như vậy phủ đệ, vốn cho rằng là thật tâm ưa thích, giờ phút này mới hiểu được... Đồ chơi thôi."),t("br"),t("br"),n._v("“Thế nào?”"),t("br"),t("br"),n._v("Không để ý tới hai người cãi lộn, Ngụy Như Yên mang trên mặt vui vẻ, nhìn về phía một bên sư huynh."),t("br"),t("br"),n._v("Sinh tử phía trước, mới có thể khảo nghiệm hai người có hay không có thật tình cảm, rất hiển nhiên... Hai người kia, đều là lợi dụng lẫn nhau thôi!"),t("br"),t("br"),n._v("“Nàng... Lựa chọn như thế nào, là chính nàng sự tình, ta đã can thiệp không được nữa...”"),t("br"),t("br"),n._v("Trịnh Dương chỉ cảm thấy trái tim như là đao quấy."),t("br"),t("br"),n._v("Cô gái này, hắn từ ngây thơ bắt đầu, vẫn ưa thích, trước kia nguyện vọng lớn nhất chính là tới cùng một chỗ, nằm mơ đều không có nghĩ đến, lần nữa gặp mặt, sẽ là kết quả này."),t("br"),t("br"),n._v("“Ta cho ngươi một lần anh hùng cứu mỹ nhân cơ hội... Ngươi không phải muốn cho nàng hối hận không? Hiện tại chính là thời cơ tốt nhất!”"),t("br"),t("br"),n._v("Ngụy Như Yên xem náo nhiệt không sợ phiền phức lớn."),t("br"),t("br"),n._v("“Không cần... Trong lòng ta đã triệt để bỏ xuống!”"),t("br"),t("br"),n._v("Trịnh Dương lắc đầu."),t("br"),t("br"),n._v("Làm cho đối phương hối hận lại như thế nào?"),t("br"),t("br"),n._v("Hai người đã cách xa nhau mấy cái thế giới, lại không cùng một chỗ khả năng."),t("br"),t("br"),n._v("Huống chi, đối phương ngại nghèo yêu giàu, coi như cùng một chỗ, cũng là ưa thích thân phận bây giờ, cùng bản thân hắn không có bất cứ quan hệ nào."),t("br"),t("br"),n._v("Thà rằng như vậy, cần gì lại làm chuyện vô ích."),t("br"),t("br"),n._v("“Ngươi bỏ xuống vô dụng, không cứu người, nàng có thể sẽ chết ah!”"),t("br"),t("br"),n._v("Mí mắt vừa nhấc, Ngụy Như Yên tiếp tục uống trà, chẳng muốn nói nhảm."),t("br"),t("br"),n._v("Trịnh Dương vội vàng ngẩng đầu, ngay sau đó nhìn thấy vị kia Tường ca con mắt đã ửng hồng, bàn tay lăng không một trảo, liền đem Tô Phi Phi kéo xuống trước mặt, chập ngón tay làm kiếm, nhẹ nhàng vạch một cái, nữ hài trắng tinh trên cổ tay, nhiệt huyết cuồn cuộn mà ra."),t("br"),t("br"),n._v("Thay đổi máu tươi, cùng thay đổi huyết mạch là hai việc khác nhau, chỉ là dùng máu của người khác, thay thế mình trúng độc huyết dịch, một ít nhị tinh cấp bậc y sư liền có thể hoàn thành, không tính thái phức tạp, vị này Tường ca thực lực mạnh mẽ, tuy là thân trúng kịch độc, có thể tu vi cũng không bị áp chế, chỉ cần cẩn thận đề phòng nói, bản thân cũng có thể hoàn thành."),t("br"),t("br"),n._v("“Dừng tay!”"),t("br"),t("br"),n._v("Thấy thật muốn thay đổi, nữ hài hẳn phải chết không nghi ngờ, Trịnh Dương cũng nhịn không được nữa, đứng dậy."),t("br"),t("br"),n._v("Tường ca quay đầu, tràn đầy dữ tợn."),t("br"),t("br"),n._v("“Trịnh Dương... Ngươi, ngươi trở về?”"),t("br"),t("br"),n._v("Nghe được thanh âm, kiều nữ hài thân thể run lên, vội vàng nhìn về phía đi tới thân ảnh, nước mắt tràn mi mà ra."),t("br"),t("br"),n._v("Thanh âm quá quen thuộc, chính là vị kia lúc trước hướng nàng thổ lộ, mà bị cự tuyệt quật cường thiếu niên."),t("br"),t("br"),n._v("Giờ phút này, chính từng bước một đi tới, thân ảnh dưới ánh mặt trời, lộ ra vô cùng đồ sộ."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1870"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1872"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);