(window.webpackJsonp=window.webpackJsonp||[]).push([[984],{1338:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1860-tuong-ca-than-phan"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1860-tuong-ca-than-phan"}},[n._v("#")]),n._v(" Chương 1860: Tường ca thân phận")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Tuy là không muốn ra tay, nhưng Trịnh Dương biết mình vị sư muội này tính tình, thật có khả năng tùy ý vị này Tường ca làm ẩu, cuối cùng dẫn đến Tô Phi Phi tử vong."),t("br"),t("br"),n._v("Mặc dù trong lòng sớm mất lúc trước cái kia phần rung động, nhưng cũng không đành lòng, đã từng ưa thích nữ hài, liền như thế bị giết."),t("br"),t("br"),n._v("“Trịnh Dương, cứu ta...”"),t("br"),t("br"),n._v("Thấy hắn đi tới, nữ hài giống như là nắm lấy một cọng cỏ cứu mạng, cắn răng nói."),t("br"),t("br"),n._v("“Yên tâm!”"),t("br"),t("br"),n._v("Trịnh Dương bàn tay nhẹ nhàng vung lên, Tường ca bao phủ tại nữ hài trên người lực lượng, lập tức bị chém đứt."),t("br"),t("br"),n._v("“Ngươi là ai?”"),t("br"),t("br"),n._v("Tường ca lần nữa híp mắt lại."),t("br"),t("br"),n._v("Hắn chỉ có thời gian một nén nhang, không nhanh chút, liền sẽ chết."),t("br"),t("br"),n._v("Cái tên này từ nơi nào xuất hiện, hơn nữa nhìn bộ dáng, cùng Tô Phi Phi có cái gì không nói được quan hệ, để hắn trong lòng cảnh giác chi ý đại tăng."),t("br"),t("br"),n._v("“Ta là ai, ngươi không cần biết, ta chỉ muốn hỏi ngươi, đã không thích, vì sao muốn cùng với nàng, hơn nữa xây dựng như vậy lớn một tòa phủ đệ!”"),t("br"),t("br"),n._v("Trịnh Dương nhìn qua, ánh mắt không có bất kỳ cái gì chấn động, tựa như khống chế sinh tử phán quan."),t("br"),t("br"),n._v("Tu vi đạt tới Cổ Thánh cấp bậc, càng là chinh chiến vô số, tâm tính sớm đã rèn luyện như là sắt thép."),t("br"),t("br"),n._v("Nếu không phải Tô Phi Phi là trái tim của hắn chỗ sâu mềm mại nhất khối kia, thiếu niên hồn nhiên nhất ước muốn, cũng sẽ không tới."),t("br"),t("br"),n._v("“Ta là thật thích nàng, mới ở lại chỗ này, nếu không, thân phận của ta, làm sao có thể đợi tại một cái nho nhỏ vương quốc...” Dừng lại một chút, Tường ca nói."),t("br"),t("br"),n._v("Vị này cùng cái kia hạ độc người ngồi cùng một chỗ, lại thêm tu vi nhìn không thấu, tuỳ tiện chặt đứt khống chế Tô Phi Phi chân khí, ngu nữa cũng rõ ràng, tuyệt không phải kẻ vớ vẩn."),t("br"),t("br"),n._v("Loại người này, lấy thực lực của hắn tuyệt không dám đắc tội."),t("br"),t("br"),n._v("“Thích nàng?”"),t("br"),t("br"),n._v("Trịnh Dương cười nhạo: “Ưa thích, sẽ vì tính mạng của mình, không nói hai lời, liền mặc kệ sống chết của nàng...”"),t("br"),t("br"),n._v("Trước đó, cũng cho rằng, hai bên là chân chính lẫn nhau, thật sự là như thế, cũng không quan trọng, dù sao, hắn đã thả ra."),t("br"),t("br"),n._v("Nhưng mà... Đối phương vậy mà vì bản thân sống, bất chấp Tô Phi Phi sinh tử, thậm chí trong mắt không có chút nào cảm thông cùng tình nghĩa, để hắn triệt để tức giận."),t("br"),t("br"),n._v("Đây cũng không phải là ưa thích, mà là sử dụng!"),t("br"),t("br"),n._v("Một cái đế quốc cường giả, chạy đến vương quốc xây dựng như vậy lớn phủ đệ, không vì tình nghĩa, vậy thì khẳng định có toan tính mưu."),t("br"),t("br"),n._v("“Ta là thật tâm ưa thích...” Tường ca vội nói."),t("br"),t("br"),n._v("“Không cần nghĩ đến gạt ta, thực lực của ngươi, còn chưa đủ...”"),t("br"),t("br"),n._v("Chẳng muốn cùng đối phương nói nhảm, Trịnh Dương cong ngón tay búng một cái."),t("br"),t("br"),n._v("“Ah...”"),t("br"),t("br"),n._v("Một tiếng kêu thảm, Tường ca té ngã trên đất, trong cơ thể kịch liệt đau đớn, một đầu mồ hôi nước."),t("br"),t("br"),n._v("Chỉ thoáng cái, hắn liền rõ ràng, cùng đối phương chênh lệch thực tế quá lớn, muốn giết hắn, một ý niệm mà thôi."),t("br"),t("br"),n._v("“Ngươi đến cùng là ai? Chúng ta gì oán gì thù? Nếu như chỉ là bởi vì nàng, có thể trực tiếp cho ngươi...”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, một chỉ Tô Phi Phi."),t("br"),t("br"),n._v("Sinh mệnh trước mặt, nữ nhân yêu mến cũng có thể đưa tặng, huống chi, không có gì tình cảm."),t("br"),t("br"),n._v("“Cho ta?” Trịnh Dương vẻ mặt băng lãnh tựa như tuyết lạnh: “Chẳng lẽ nàng trong mắt ngươi, chỉ là cái có thể giao dịch đồ vật? Chỉ là cái có thể đồ vật bảo mệnh? Tùy tiện liền có thể tặng người?”"),t("br"),t("br"),n._v("“Ta...”"),t("br"),t("br"),n._v("Tường ca hàm răng run lên."),t("br"),n._v("Đối phương dưới sự phẫn nộ, khí tức không có khống chế lại, lập tức cảm thấy giống như là đối mặt với một cái to lớn hung thú, một ý niệm liền có thể bị nghiền thành bột phấn, không còn sót lại một chút cặn."),t("br"),t("br"),n._v("Cái này Tô Phi Phi, hắn đặc biệt điều tra qua, Thiên Huyền vương thành sinh trưởng ở địa phương tiểu nhân vật, lúc nào, toát ra một vị cường đại như vậy bằng hữu?"),t("br"),t("br"),n._v("“Hắn...”"),t("br"),t("br"),n._v("Bên này sợ tới mức run rẩy, một bên Tô Phi Phi trong mắt cũng lộ ra mê man."),t("br"),t("br"),n._v("Vị này Trịnh Dương tại hơn một năm trước, vẫn là nàng xem thường liếm chó, điểu ti, tùy tiện ném lên một đống, đều không thèm để ý chút nào, ai cũng nào ngờ, một năm sau, đạt đến loại tình trạng này."),t("br"),t("br"),n._v("Thẩm Truy hoàng đế cũng không dám đắc tội Tường ca, hét một tiếng xuống, sắp dọa chết..."),t("br"),t("br"),n._v("Loại thực lực nào?"),t("br"),t("br"),n._v("“Đừng trách ta không có nhắc nhở ngươi, gây ra mạng người, lão sư sẽ tức giận!”"),t("br"),t("br"),n._v("Thấy khí áp bên dưới, vị này Tường ca bất cứ lúc nào cũng sẽ bị tươi sống đè chết, Ngụy Như Yên duỗi lưng một cái, cười khanh khách nói."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Nghe được lão sư, Trịnh Dương thả ra tinh thần lực, thu hồi lại, lần nữa nhìn về phía trước mắt Tường ca, híp mắt lại: “Ta chỉ cần ngươi nói thật, đừng nghĩ lấy giở trò gian, cho ngươi hạ độc, là sư muội ta, nếu như muốn lừa gạt, ta cũng có thể tìm ta sư đệ, cho ngươi sưu hồn!”"),t("br"),t("br"),n._v("“Sưu hồn?”"),t("br"),t("br"),n._v("Thân thể run lên, Tường ca vội vàng quay đầu nhìn về phía cách đó không xa Ngụy Như Yên, thấy nàng cười khanh khách nhìn qua, lần nữa cứng đờ."),t("br"),t("br"),n._v("Sư muội đều lợi hại như vậy, sư huynh chẳng phải càng thêm cường đại?"),t("br"),t("br"),n._v("“Nói đi!”"),t("br"),t("br"),n._v("Chẳng muốn tiếp tục nói nhiều, Trịnh Dương lạnh lùng nhìn tới, đầu ngón tay nhất đạo lam nhạt quang mang không ngừng lấp lánh."),t("br"),t("br"),n._v("Thật ra thì không cần sưu hồn, hắn cũng có mấy trăm loại phương pháp, làm cho đối phương nói thật."),t("br"),t("br"),n._v("Chiến Sư đường đường chủ, nếu như ngay cả thẩm vấn năng lực đều không có, thế nào cùng Dị Linh tộc nhân đối chiến vài vạn năm, mà không rơi vào thế hạ phong?"),t("br"),t("br"),n._v("Nhìn thấy đạo tia sáng này, Tường ca cảm giác chân khí toàn thân, đều giống như bị đánh tan đồng dạng, từng cơn đau đớn."),t("br"),t("br"),n._v("Chỉ nhìn một cái liền loại cảm giác này, một khi đi vào trong cơ thể, nhất định sống không bằng chết."),t("br"),t("br"),n._v("Biết tránh không khỏi, Tường ca cắn răng, nói: “Ta tên Vân Tường, là một vị tầm bảo sư! Trời sinh đối bảo vật liền mẫn cảm, những năm gần đây, một mực tại xung quanh các quốc gia thăm dò, tìm kiếm đủ loại bảo vật... Cho đến đến nơi này!”"),t("br"),t("br"),n._v("“Tầm bảo sư?”"),t("br"),t("br"),n._v("Liếc mắt nhìn nhau, Trịnh Dương cùng Ngụy Như Yên riêng phần mình gật đầu."),t("br"),t("br"),n._v("Tầm bảo sư, bọn họ trước đó nghe nói qua, đại lục ở bên trên nổi danh nhất chính là Thánh Nhân Vân gia."),t("br"),t("br"),n._v("Quả thực có người thường khó mà có thủ đoạn, có thể thuận lợi tìm tới bảo vật vị trí cụ thể."),t("br"),t("br"),n._v("“Đi qua thăm dò, ta phát hiện Tô Phi Phi nơi ở phía dưới, ẩn chứa năng lượng cường đại chấn động, hẳn là có một cái thập phần cường đại chí bảo, vì lẽ đó, ta liền thảo luận muốn mua xuống đối phương tòa nhà, bất quá... Ta một ngoại nhân chạy tới mua sắm, hơn nữa tìm kiếm bảo vật, là cái trường kỳ quá trình, rất dễ dàng bị ngoại nhân phát giác, trêu ra phiền phức, vì lẽ đó... Liền nói dối xưng coi trọng Tô Phi Phi, muốn cùng nàng song tê song phi...”"),t("br"),t("br"),n._v("Vân Tường nói: “Có nàng làm che chở, bốn phía phòng ở mua lên cũng liền đơn giản, tất cả mọi chuyện đều thuận lý thành chương, vì che giấu tai mắt người, lúc này mới xây dựng to lớn phủ đệ...”"),t("br"),t("br"),n._v("Tài không lộ ra ngoài, thực lực của hắn tại Thiên Huyền vương quốc, quả thực không người có thể địch, nhưng thông qua dò xét, phía dưới món bảo vật này, thực tế quá mức kinh người, một khi tiết lộ, đừng nói là hắn, coi như gia tộc của hắn, cũng chưa chắc có thể chiếm thành của mình."),t("br"),t("br"),n._v("Vì lẽ đó... Lấy kết hôn làm lý do, bốn phía mua sắm nhà, xây dựng phủ đệ, sau đó đang bố trí ra đủ loại đại trận..."),t("br"),t("br"),n._v("Như vậy trải qua, đều cho là người chỗ ở, cũng liền không có người hoài nghi."),t("br"),t("br"),n._v("“Ngươi nói thích ta, chỉ là... Vì bảo vật?”"),t("br"),t("br"),n._v("Nghe hắn nói ra lời này, một bên Tô Phi Phi khuôn mặt trắng bệch, toàn thân vô lực, xụi lơ ngồi dưới đất."),t("br"),t("br"),n._v("Lúc trước đối phương hoa ngôn xảo ngữ, lại thêm đủ loại bảo vật, để nàng mê mẩn tâm trí, còn tưởng rằng bản thân thật mị lực vô tận, bây giờ mới biết, đối phương mưu đồ căn bản cũng không phải là nàng, mà là phòng ở phía dưới bảo tàng."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1871"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1873"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);