(window.webpackJsonp=window.webpackJsonp||[]).push([[985],{1339:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1861-kinh-khung-bia-đa"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1861-kinh-khung-bia-đa"}},[n._v("#")]),n._v(" Chương 1861: Kinh khủng bia đá")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("“Liền như ngươi loại này chê nghèo yêu giàu tính cách, cùng nha hoàn cũng không bằng dung mạo, thật đúng là cho rằng có thể để cho ta động tâm?”"),t("br"),t("br"),n._v("Biết hiện tại cùng đối phương nét càng sạch sẽ càng tốt, Vân Tường hừ một tiếng: “Nếu không phải là bởi vì nhà ngươi phía dưới, nắm giữ bảo tàng, ta lại như thế nào có thể cùng ngươi hư dữ ủy xà đến bây giờ...”"),t("br"),t("br"),n._v("“Đủ rồi!” Trịnh Dương vung một cái ống tay áo."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Ngực gặp phải trọng kích, Vân Tường bay ngược mà ra, trùng trùng điệp điệp té ngã trên đất, hơi đỏ mặt, một ngụm máu tươi phun tới."),t("br"),t("br"),n._v("Trịnh Dương hừ lạnh: “Trả lời ta muốn biết, không quan hệ, không cần nhiều lời!”"),t("br"),t("br"),n._v("“Vâng! Vâng!”"),t("br"),t("br"),n._v("Cố nén ngực sôi trào, Vân Tường biết triệt để rũ sạch liên quan, thở phào nhẹ nhõm, không còn dám nói nhảm: “Bảo tàng là cái gì, ta cũng không biết, có điều, tiếp cận một năm khai quật, sắp đào được, chỉ là bốn phía trận pháp quá lợi hại, thực lực của ta thiếu, tạm thời phá giải không được...”"),t("br"),t("br"),n._v("Trịnh Dương nhíu nhíu mày: “Dẫn chúng ta qua đi xem một chút!”"),t("br"),t("br"),n._v("Có thể để cho Thánh Nhân Vân gia cường giả, phí lớn trắc trở ở đây khai quật, cái gọi là bảo tàng hẳn là sẽ không đơn giản, đã biết, tự nhiên muốn đi qua kiến thức một hai."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Vân Tường vội vàng gật đầu."),t("br"),t("br"),n._v("Mạng tại trong tay đối phương, không đáp ứng cũng không được."),t("br"),t("br"),n._v("Giải trừ vị này Vân Tường trên người lực lượng áp chế, Trịnh Dương, Ngụy Như Yên đi theo, Tô Phi Phi chần chờ một chút, cuối cùng cũng theo sát phía sau."),t("br"),t("br"),n._v("Đi vào phủ đệ, quả nhiên thấy bố trí vô số ngăn cách khí tức trận pháp, đem trọn cái sân nhỏ bao phủ như thùng sắt, cái gì đều toả ra không ra."),t("br"),t("br"),n._v("Thời gian không dài đi tới một tòa hùng vĩ rộng lớn đại điện, đẩy cửa tiến vào trong đó, lúc này mới phát hiện, toàn bộ mặt đất đều bị đào ra một cái động lớn, phía dưới bạch cốt trắng xoá, hoàn toàn tĩnh mịch."),t("br"),t("br"),n._v("“Vì bảo tàng, ngươi vậy mà giết nhiều người như vậy?”"),t("br"),t("br"),n._v("Híp mắt lại, Trịnh Dương sát khí sôi trào."),t("br"),t("br"),n._v("Chiến Sư đường, không chỉ đối kháng Dị Linh tộc nhân, càng là muốn bảo vệ người bình thường bình yên, cái tên này vì bảo tàng, thế mà trắng trợn chém giết, đã xúc phạm Danh Sư đường điểm mấu chốt."),t("br"),t("br"),n._v("“Không phải ta giết... Là ta dùng nhiều tiền mời tới công nhân, ai biết... Đào được một cái bia đá, chẳng biết tại sao, khí tức tiết lộ, trong nháy mắt hơn một trăm cái công nhân toàn bộ tử vong... Ta không dám để cho tin tức tiết lộ, đành phải đem bọn hắn thi thể hóa thành bạch cốt, lưu ở nơi đây...”"),t("br"),t("br"),n._v("Liền vội vàng lắc đầu, Vân Tường một mặt lo lắng: “Công nhân gia đình, ta đều cho cực cao trợ cấp, ta tuy là muốn bảo tàng, nhưng cũng tuyệt không dám lạm sát kẻ vô tội...”"),t("br"),t("br"),n._v("“Bia đá?” Biết những người này chết rồi, tiếp tục đuổi kiểm tra, là cái hồ đồ án, Trịnh Dương tiếp tục hỏi."),t("br"),t("br"),n._v("“Chính ở đằng kia dưới bùn đất mặt, ta đã phái mười vị tử sĩ đi qua dò xét, ai biết đều là đến lúc đó liền chết, liền một điểm hữu dụng tin tức đều không có truyền tới, cụ thể chuyện gì xảy ra ta là thật không rõ ràng...”"),t("br"),t("br"),n._v("Vân Tường một mặt đắng chát."),t("br"),t("br"),n._v("Vốn cho rằng là kiện đại bảo vật, nằm mơ đều không có nghĩ đến, người càng chết càng nhiều, đến bây giờ, đã là đâm lao phải theo lao."),t("br"),t("br"),n._v("Muốn lui, đã tiêu phí nhiều như vậy, muốn tiến hành, đã không có người còn dám đi dò xét, lúc này mới đi tìm Thẩm Truy bệ hạ, hy vọng có thể từ chỗ của hắn kiếm nhiều tử sĩ tới, không nghĩ tới, vừa ra khỏi cửa liền gặp hai vị này cường giả."),t("br"),t("br"),n._v("“Đi xuống xem một chút!”"),t("br"),t("br"),n._v("Nghe được cái gọi là bia đá, vậy mà giết nhiều người như vậy, Trịnh Dương càng thêm kỳ lạ."),t("br"),t("br"),n._v("“Ta... Thực lực quá thấp, hai vị đại nhân, vẫn là bản thân đi qua đi...”"),t("br"),t("br"),n._v("Vân Tường sắc mặt trắng nhợt."),t("br"),t("br"),n._v("Những ngày gần đây, hắn vẫn luôn không có đi xuống qua, thật muốn đi xuống, đoán chừng cũng giống như những người khác, biến thành bạch cốt."),t("br"),t("br"),n._v("“Ở đâu ra nói nhảm...” Hừ lạnh một tiếng, Ngụy Như Yên bàn tay nhẹ nhàng nhấc lên, Vân Tường kêu thảm đều không có tới đến, liền theo sát tại hai người sau lưng, hướng về phía trước hố sâu bay đi."),t("br"),t("br"),n._v("Cái hố có sâu vài trăm mét, phía dưới hiện đầy đủ loại dạ minh châu, chiếu rọi giống như ban ngày."),t("br"),t("br"),n._v("Đi tới đáy hố, nhìn quanh hai bên một vòng, Trịnh Dương cùng Ngụy Như Yên đồng thời nhíu nhíu mày."),t("br"),t("br"),n._v("“Cỗ khí tức này... Có chút kỳ quái!”"),t("br"),t("br"),n._v("Sau một lúc lâu, Trịnh Dương gật đầu."),t("br"),t("br"),n._v("“Là Cổ Thánh!”"),t("br"),n._v("Ngụy Như Yên lên tiếng, quay đầu nhìn về phía bên người Vân Tường: “Bia đá ở đâu?”"),t("br"),t("br"),n._v("“Ngay tại phía dưới này...”"),t("br"),t("br"),n._v("Nuốt ngụm nước bọt, Vân Tường một chỉ cách đó không xa."),t("br"),t("br"),n._v("Trịnh Dương cũng cảm giác được chỗ này không đúng, hít sâu một hơi, trong tay bất ngờ xuất hiện trường thương, nhẹ nhàng run lên."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Một mảng lớn bùn đất lật lên, ngay sau đó xuất hiện một cái to lớn bia đá."),t("br"),t("br"),n._v("Bia đá điêu khắc hoa văn, để cho người ta xem không hiểu, có điều, từng đạo mạnh mẽ sát lục chi khí, không ngừng lan ra, để cho người ta nhìn lên một cái, liền đau đầu muốn nứt."),t("br"),t("br"),n._v("Hai người cảm giác ứng Cổ Thánh khí tức, chính là như vậy phát ra."),t("br"),t("br"),n._v("“Chính là cái này bia đá... Hai vị đại nhân, thực lực của ta quá yếu, đi trước...”"),t("br"),t("br"),n._v("Vân Tường bờ môi run rẩy, cũng lại kìm nén không được, xoay người liền muốn chạy trốn."),t("br"),t("br"),n._v("“Có chúng ta ở đây, ngươi không chết được! Nếu như chúng ta chết rồi, không có người giải độc, ngươi đồng dạng muốn chết...”"),t("br"),t("br"),n._v("Ngụy Như Yên một mặt lãnh đạm."),t("br"),t("br"),n._v("Vân Tường da mặt co lại, tràn đầy muốn khóc biểu lộ, thân thể cứng ngắc, không dám tiếp tục động đậy."),t("br"),t("br"),n._v("Đối phương nói không sai, hắn hiện tại thân trúng kịch độc, hai người kia một khi chết rồi, hắn cũng hẳn phải chết, mặc kệ chạy trốn tới cái nào cũng vô dụng."),t("br"),t("br"),n._v("“Ta trước đi qua nhìn một chút!”"),t("br"),t("br"),n._v("Hít sâu một hơi, trường thương ở trước ngực xoay tròn, Trịnh Dương hừ nhẹ, sải bước hướng bia đá đi tới."),t("br"),t("br"),n._v("Còn chưa tới đến trước mặt, lập tức cảm thấy một cỗ nồng đậm sát khí phá không mà đến, giống như là muốn đem hắn đầu lôi kéo thành hai nửa."),t("br"),t("br"),n._v("Đổi lại Đại Thánh cường giả, chỉ sợ đều không chịu nổi, đầu hỗn loạn mà chết, nhưng mà hắn đã đạt tới Cổ Thánh, càng là Chiến Sư đường đường chủ, đặc biệt tu luyện qua ý chí, dùng để đối kháng Dị Linh tộc nhân chém giết chân khí, những khí tức này mặc dù nồng đậm, đối với hắn mà nói, còn không tạo được uy hiếp tính mạng."),t("br"),t("br"),n._v("Mấy bước đi tới bia đá trước mặt, không biết thứ gì điêu khắc mà thành, tản mát ra ôn nhuận quang mang, phảng phất cất giữ trong lòng đất vài vạn năm, đều không có mảy may tổn thương, tựa như mới điêu khắc đồng dạng."),t("br"),t("br"),n._v("“Đây là thời kỳ Thượng Cổ văn tự, ta nhận không ra!”"),t("br"),t("br"),n._v("Ngón tay nhẹ nhàng vuốt ve ở phía trên, Trịnh Dương lắc đầu."),t("br"),t("br"),n._v("Trên tấm bia đá điêu khắc đều là thời kỳ Thượng Cổ đặc thù văn tự, học thức của hắn có hạn, căn bản là không có cách nhận ra, đổi lại lão sư ở đây, có lẽ có thể rõ ràng."),t("br"),t("br"),n._v("“Kẽo kẹt! Kẽo kẹt!”"),t("br"),t("br"),n._v("Thấy cái tên này lại dám vuốt ve bia đá, Vân Tường sợ tới mức hàm răng run lên."),t("br"),t("br"),n._v("Hắn phái ra công nhân, tử sĩ, khoảng cách bia đá rất xa liền trực tiếp tử vong, trước mắt vị này vậy mà có thể đi tới trước mặt, thậm chí vuốt ve... Thực lực rốt cục mạnh đến mức nào?"),t("br"),t("br"),n._v("Người lợi hại như vậy, vì sao muốn đến Thiên Huyền vương quốc loại địa phương nhỏ này?"),t("br"),t("br"),n._v("“Hắn nhận thức Tô Phi Phi, lại dùng thương... Vị sư muội này, am hiểu hạ độc, chẳng lẽ...”"),t("br"),t("br"),n._v("Đột nhiên thân thể cứng đờ, hai cái tên nhảy vào trong óc."),t("br"),t("br"),n._v("Thân là Thánh Nhân Vân gia hậu nhân, mặc dù những năm này không ở nhà tộc, tin tức đối lập bế tắc, có thể... Cái nào tên, cùng hắn rất nhiều đệ tử sự tích, vẫn là có chỗ nghe nói."),t("br"),t("br"),n._v("Dường như... Bọn họ đều là Thiên Huyền vương quốc đi ra."),t("br"),t("br"),n._v("Chẳng lẽ trước mắt vị này, chính là vị kia Chiến Sư đường thiên tài nhất Trịnh đường chủ?"),t("br"),t("br"),n._v("Chỉ biết là, họ Trịnh, cũng không rõ ràng tên... Suy nghĩ kỹ một chút, hình như liền gọi Trịnh Dương..."),t("br"),t("br"),n._v("Tô Phi Phi như vậy một cái bình thường nữ hài, để Chiến Sư đường đường chủ ưa thích?"),t("br"),t("br"),n._v("Cái này..."),t("br"),t("br"),n._v("Vân Tường trên đầu không ngừng đổ mồ hôi, cho rằng yết hầu phát khô, bất cứ lúc nào cũng sẽ ngất đi."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1872"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1874"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);