(window.webpackJsonp=window.webpackJsonp||[]).push([[986],{1341:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1862-khong-su-thi-the"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1862-khong-su-thi-the"}},[n._v("#")]),n._v(" Chương 1862: Khổng sư thi thể")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Không để ý tới sắp mệt lả Vân Tường, Trịnh Dương lông mày càng nhăn càng chặt, dọc theo bia đá dạo qua một vòng, nhìn về phía cách đó không xa Ngụy Như Yên."),t("br"),t("br"),n._v("Gật gật đầu, nữ hài trong mắt mang theo khẳng định chi sắc."),t("br"),t("br"),n._v("Không do dự nữa, Trịnh Dương hít sâu một hơi, lui về phía sau mấy bước, dài bằng bàn tay thương bỗng nhiên điểm một cái."),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Không khí xuất hiện từng cơn sóng gợn, một thương cắm vào trong tấm bia đá ở giữa một chỗ hoa văn bên trong."),t("br"),t("br"),n._v("Răng rắc!"),t("br"),t("br"),n._v("Trận pháp xé rách thanh âm vang lên, ngay sau đó bia đá đung đưa, mặt đất xuất hiện một đạo vết rách to lớn, một đầu hẹp dài hướng phía dưới lan ra thông đạo xuất hiện tại phía trước."),t("br"),t("br"),n._v("Trong thông đạo, cũng không có mục nát khí tức, ngược lại cho người ta một loại cổ xưa âm trầm cảm giác, giống như là thông hướng Địa Ngục."),t("br"),t("br"),n._v("“Đi xuống xem một chút!”"),t("br"),t("br"),n._v("Trịnh Dương kẻ tài cao gan cũng lớn, nhấc chân hướng phía dưới đi đến."),t("br"),t("br"),n._v("Ngụy Như Yên theo sát trên đó."),t("br"),t("br"),n._v("Trong thông đạo, âm u ẩm ướt, trước đó cảm thụ qua Cổ Thánh lực lượng lan ra, trên vách tường điêu khắc đặc thù phù văn, đem những khí tức này một mực khóa ở trong đó, tu vi không đạt tới Cổ Thánh, đứng trên mặt đất căn bản không cảm thấy được."),t("br"),t("br"),n._v("“Cái này hẳn là Khổng sư thời đại kia xây dựng...”"),t("br"),t("br"),n._v("Vừa quan sát, Ngụy Như Yên vừa nói."),t("br"),t("br"),n._v("Tuy là thoạt nhìn hoàn hảo không chút tổn hại cùng mới không có cái gì khác nhau, nhưng kiến trúc thủ pháp cùng trên vách tường phù văn loại hình, rõ ràng mang theo Thượng Cổ thời đại dấu vết."),t("br"),t("br"),n._v("Hẳn là thời đại kia xây dựng mà thành."),t("br"),t("br"),n._v("Vừa nhìn vừa hướng phía dưới đi lại, tốc độ cực nhanh, đi tiếp cận nửa canh giờ, đi xuống mấy chục cây số, một cái to lớn thạch thất xuất hiện tại phía trước."),t("br"),t("br"),n._v("Trịnh Dương đem trường thương để ở trước ngực, phòng bị lúc nào cũng có thể sẽ xuất hiện biến cố, đồng thời cong ngón tay búng một cái, mấy cái dạ minh châu lập tức khảm nạm đến trên vách tường, đem bốn phía chiếu đèn đuốc sáng trưng."),t("br"),t("br"),n._v("Là cái đường kính hơn bốn mươi mét cung điện dưới đất, chính giữa chỗ, một cái đá xanh điêu khắc thạch quan, đặt ngang trong đó, tản mát ra xa xăm khí tức."),t("br"),t("br"),n._v("“Liền một cái thạch quan?”"),t("br"),t("br"),n._v("Ngụy Như Yên tràn đầy nghi ngờ."),t("br"),t("br"),n._v("Xây dựng một cái hướng phía dưới lan ra mấy chục cây số địa đạo, phong cấm tất cả khí tức bia đá, chỉ là vì khóa lại một cái thạch quan?"),t("br"),t("br"),n._v("Cái này có chút kỳ quái đi!"),t("br"),t("br"),n._v("Cũng không nhiều lời, Trịnh Dương chậm rãi tới gần, vừa định dùng trong tay trường thương, phá vỡ quan tài, đột nhiên bàn tay chấn động, một cỗ to lớn hấp lực, từ quan tài bên trong sinh đi ra."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Trường thương đi tới thạch quan trước mặt, nhẹ nhàng thoáng một cái, bái xuống dưới."),t("br"),t("br"),n._v("“Làm sao vậy?” Ngụy Như Yên vội vàng đi tới trước mặt, toàn thân khí độc lan ra, dường như vừa nhìn thấy không đúng, tựu tùy lúc đều sẽ bán ra."),t("br"),t("br"),n._v("“Ta trường thương, nhận lấy thương ý quấy nhiễu, hình như trong quan tài, có cao thâm hơn thương đạo chân giải!” Trịnh Dương nói."),t("br"),t("br"),n._v("Hắn trường thương tuy là không phải Cổ Thánh chí bảo, nhưng nhận bản thân thương ý hun đúc, đối mặt chân chính Cổ Thánh chí bảo, cũng sẽ không yếu quá nhiều, dù vậy, vẫn như cũ bị quan tài hút đi qua, rõ ràng trong đó nắm giữ càng cường đại hơn chân giải, để trường thương trong tay kìm lòng không được thuyết phục, thậm chí liền năng lực phản kháng đều không có."),t("br"),t("br"),n._v("“So ngươi thương pháp đều muốn lợi hại?” Ngụy Như Yên khó tin."),t("br"),t("br"),n._v("Nàng vị sư huynh này, truyền thừa tại lão sư, đối thương pháp lý giải, đã sớm đạt đến chân giải tình trạng, có thể xưng đương thời người thứ hai, dù vậy, đều chống lại không được, trong quan tài đến cùng là cái gì, nắm giữ cường đại như thế uy lực?"),t("br"),t("br"),n._v("“Mặc kệ là cái gì, đều muốn mở ra nhìn một chút mới có thể biết... Sư muội, một lát nữa ngươi ta đồng loạt ra tay, cưỡng ép vén lên!”"),t("br"),t("br"),n._v("Trịnh Dương nói."),t("br"),t("br"),n._v("Ngụy Như Yên gật đầu đồng ý, hai người hai bên trái phải, phân biệt đứng tại thạch quan hai bên."),t("br"),t("br"),n._v("Liếc mắt nhìn nhau, riêng phần mình cho đối phương một ánh mắt, Trịnh Dương cùng Ngụy Như Yên đồng thời lông mày nâng lên, riêng phần mình hướng về phía trước bắt tới."),t("br"),t("br"),n._v("Hai người đều là Cổ Thánh cấp bậc cường giả, lại trải qua Trương Huyền hơn một tháng đơn độc chỉ điểm, lực lượng sớm đã đạt đến làm người ta kính sợ tình trạng, tích huyết trùng sinh cấp bậc Cổ Thánh gặp lên đều khó mà chống lại, cùng huống chi một cái quan tài."),t("br"),t("br"),n._v("Kẹt kẹt!"),t("br"),n._v("Một tiếng chói tai vang lên, quan tài xuất hiện một vết nứt, một đạo mạnh mẽ khí tức, thẳng tắp phóng hướng thiên không, giống như là muốn đem Địa quật đều đánh nát."),t("br"),t("br"),n._v("Trịnh Dương bàn tay lớn vồ một cái, đem cỗ khí tức này phong ấn lại, không cho tiết lộ mảy may, lúc này mới chậm rãi hướng về phía trước."),t("br"),t("br"),n._v("Cổ Thánh khí tức, hủy thiên diệt địa, dù là tiết lộ một tia, đều sẽ hủy diệt vô số, cỗ khí tức này đối bọn hắn tới nói, tiện tay có thể diệt, chỉ khi nào tiết lộ đến Thiên Huyền vương thành, toàn bộ thành thị, cũng có thể bị trực tiếp hủy diệt."),t("br"),t("br"),n._v("Đi tới trước mặt, đồng thời hướng thạch quan nội bộ nhìn lại, chỉ thấy nằm ngang một cỗ thi thể, không nhúc nhích, tựa như ngủ thiếp đi đồng dạng."),t("br"),t("br"),n._v("“Là Cổ Thánh thi thể...”"),t("br"),t("br"),n._v("Hai người lông mày đồng thời nhảy dựng."),t("br"),t("br"),n._v("Cũng chỉ có Cổ Thánh sau khi chết thi thể, mới có uy lực như thế, mới có thể làm được vạn năm bất hủ, dung mạo không thay đổi."),t("br"),t("br"),n._v("“Đây là...”"),t("br"),t("br"),n._v("Trịnh Dương nhìn kỹ hướng thi thể, sau một lúc lâu nhớ tới cái gì, khuôn mặt một trắng, nhịn không được lui về phía sau mấy bước."),t("br"),t("br"),n._v("“Làm sao vậy?”"),t("br"),t("br"),n._v("Ngụy Như Yên nghi ngờ."),t("br"),t("br"),n._v("Sư huynh muội ở chung lâu, đối cái tên này cũng biết rất rõ ràng, từ trước tới nay không sợ trời không sợ đất, tùy ý làm bậy, làm sao thấy được một cái thi thể, bộ dáng này?"),t("br"),t("br"),n._v("“Hắn là... Hắn là Nhiễm Cầu Cổ Thánh!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Trịnh Dương thân thể cứng ngắc."),t("br"),t("br"),n._v("“Nhiễm Cầu Cổ Thánh? Khổng sư môn hạ chiến lực đệ nhất nhân, Chiến Sư đường khai phái tổ sư?” Ngụy Như Yên sững sờ."),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Thở ra một hơi, Trịnh Dương ổn định ở hỗn loạn tâm cảnh."),t("br"),t("br"),n._v("Nhiễm Cầu Cổ Thánh, là Khổng sư môn hạ, thập hiền một trong, loại trừ Khổng sư cường đại nhất Cổ Thánh, Chiến Sư đường khai phái tổ sư..."),t("br"),t("br"),n._v("Lúc trước kế thừa đường chủ chi vị cùng chiến tử chi vị thời điểm, từng đặc biệt quỳ lạy qua tượng, cùng trước mắt cái này thi thể giống nhau như đúc, bởi vậy, chỉ nhìn thoáng cái liền nhận ra được."),t("br"),t("br"),n._v("Khổng sư đắc ý nhất môn đồ một trong, thiên hạ đệ nhị Cổ Thánh, vậy mà chết ở chỗ này..."),t("br"),t("br"),n._v("Đây là bất kỳ thư tịch đều không có ghi lại."),t("br"),t("br"),n._v("Khó trách hắn trường thương gặp được quan tài, trực tiếp quỳ mọp xuống đất, Nhiễm Cầu Cổ Thánh dùng vũ khí chính là trường thương, Long Cốt thần thương đúng là hắn luyện chế mà thành."),t("br"),t("br"),n._v("Có thể luyện chế ra cường đại như thế vũ khí, đối thương pháp lý giải có thể tưởng tượng được, tuyệt đối so với hắn cũng cao hơn sâu không biết bao nhiêu."),t("br"),t("br"),n._v("“Sư muội, ta ở đây bảo vệ, ngươi nhanh đi báo tin lão sư!”"),t("br"),t("br"),n._v("Nắm đấm xiết chặt, Trịnh Dương khôi phục lại, xoay người dặn dò."),t("br"),t("br"),n._v("Nơi này xuất hiện cường đại như thế Cổ Thánh thi thể, lấy bọn họ kiến thức cùng lý giải, đã không có biện pháp gì, chỉ có tìm lão sư, để hắn làm chủ."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Ngụy Như Yên cũng rõ ràng đạo lý này, thân thể mềm mại thoáng một cái, thẳng tắp hướng ra phía ngoài bay ra ngoài."),t("br"),t("br"),n._v("Thấy nàng rời đi, Trịnh Dương dọc theo quan tài chậm rãi đi vòng, muốn cẩn thận chiêm ngưỡng thoáng cái vị này khai sáng Chiến Sư đường tiền bối, đột nhiên một tiếng “Kẽo kẹt! Kẽo kẹt!” Máy lò xo thanh âm truyền đến, ngay sau đó lại một cái lối đi từ quan tài phía dưới chậm rãi hiện lên."),t("br"),t("br"),n._v("Chần chờ một chút, Trịnh Dương vội vàng hướng phía dưới đi vào, vừa mới đi vào, lần nữa nghe được “Kẹt kẹt!” Thanh âm, trên đầu mất đi quang minh."),t("br"),t("br"),n._v("Thông đạo vậy mà tại trong nháy mắt đóng lại."),t("br"),t("br"),n._v("Nhíu nhíu mày, Trịnh Dương lấy ra một cái dạ minh châu, chiếu sáng bốn phía, hướng phía dưới đi đến, mấy bước sau đó, lại một cái rộng lớn đại sảnh xuất hiện tại phía trước, trước mắt vẫn như cũ nằm ngang một cái quan tài, cùng trước đó bố cục giống nhau như đúc."),t("br"),t("br"),n._v("Cắn răng một cái, lần nữa mở ra nắp quan tài, một thân ảnh xuất hiện ở trước mắt."),t("br"),t("br"),n._v("Hướng về phía trước nhìn rõ ràng thi thể hình dáng, Trịnh Dương da đầu đột nhiên nổ tung, kìm lòng không được liên tục lui về sau vài chục bước, sống lưng đâm vào trên tường đều không có chậm tới."),t("br"),t("br"),n._v("Không biết qua bao lâu, lẩm bẩm thanh âm mới từ cổ họng chậm chạp phun ra: “Khổng, Khổng sư?”"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1873"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1875"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);