(window.webpackJsonp=window.webpackJsonp||[]).push([[989],{1343:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1865-muon-lam-thien-đia-su"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1865-muon-lam-thien-đia-su"}},[n._v("#")]),n._v(" Chương 1865: Muốn làm thiên địa sư")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Một cái thô to bàn tay, từ trong thư tịch đưa ra ngoài, chấn toàn bộ địa cung đều không ngừng lay động, tựa như động đất, thô to ngón tay, bỗng nhiên bổ vào bình chướng bên trên."),t("br"),t("br"),n._v("Ầm!"),t("br"),t("br"),n._v("Sóng khí cuồn cuộn mà tới, Trương Huyền đám người kìm lòng không được lui về phía sau mấy bước, lần nữa nhìn lại, chỉ thấy bình chướng vẫn như cũ hoàn hảo không chút tổn hại, không có một chút tổn thương, mà Ngoan Nhân năm ngón tay, đồng thời toác ra máu tươi."),t("br"),t("br"),n._v("“Chủ nhân, ta thử lại lần nữa...”"),t("br"),t("br"),n._v("Một chưởng không có phá vỡ, Ngoan Nhân có chút khó xử, mở miệng lần nữa."),t("br"),t("br"),n._v("“Không cần, ngươi lại ra tay, đoán chừng địa cung đều sẽ sụp đổ!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Hiện tại địa cung cũng nhanh phải thừa nhận không được, lại tiếp tục, nhất định không chịu nổi, tuy là thực lực của bọn hắn, sẽ không đả thương đến mảy may, có thể lên Phương Thiên huyền vương quốc người bình thường, nhất định gặp phải liên luỵ."),t("br"),t("br"),n._v("Cổ Thánh cường giả lực lượng quá lớn, huống chi, phá toái hư không, hơi không khống chế được, dù là một tia, cũng đủ làm cho phạm vi mấy vạn dặm phạm vi biến thành Địa Ngục."),t("br"),t("br"),n._v("“Vậy làm sao bây giờ...”"),t("br"),t("br"),n._v("“Không thể ngạnh công, khẳng định có những biện pháp khác, lớp bình phong này, ngươi một cái đạt tới phá toái hư không người đều không cách nào phá mở, từ xưa đến nay, cũng chỉ có Khổng sư một người mới có thể bố trí mà ra, biết điểm ấy còn lại liền dễ dàng!”"),t("br"),t("br"),n._v("Trương Huyền cười cười."),t("br"),t("br"),n._v("Để Ngoan Nhân ra tay, chỉ là thử một chút, bình phong này đến cùng có thể thừa nhận mạnh cỡ nào lực lượng, thử qua về sau, cũng đã biết, cùng Khổng sư liên quan đến."),t("br"),t("br"),n._v("Tiến về phía trước một bước, hít sâu một hơi, trong cơ thể trời nhận danh sư lực lượng, hướng trong đó quán dũng."),t("br"),t("br"),n._v("Đều là trời nhận danh sư, có lẽ có thể mở ra trước mắt ràng buộc."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Lực lượng tràn vào trong đó, bình chướng đột nhiên lay động, vô số kiến thức cùng đối giáo dục cảm ngộ, tràn vào trong đầu, để hắn trong nháy mắt đối danh sư chức nghiệp, lý giải càng sâu, đối lão sư cái thân phận này, càng thêm trầm mê."),t("br"),t("br"),n._v("Hình như trong nháy mắt, Khổng sư đối với học sinh giáo dục lý niệm, truyền vào trong óc."),t("br"),t("br"),n._v("“Lấy thiên địa vi sư, có thể chính nội tâm, quy tự nhiên, tuân theo vạn vật...”"),t("br"),t("br"),n._v("Thanh âm truyền vào trong óc, giống như là ý niệm, lại giống là lĩnh ngộ của mình."),t("br"),t("br"),n._v("Trời nhận danh sư, là thiên địa công nhận danh sư, sư tòng thiên địa, lấy Thiên đạo vi sư, cất bước là đơn giản, nhưng muốn vượt qua, độ khó lớn, có thể tưởng tượng được."),t("br"),t("br"),n._v("Liền giống như Lưu Dương, Trịnh Dương đám người, muốn vượt qua bản thân, đời này đều cơ hồ không có khả năng."),t("br"),t("br"),n._v("Không phải bọn họ yếu, mà là tu tập công pháp, toàn bộ do bản thân truyền thụ, ở trong người tạo thành cố định hình thức, vượt qua bản thân, tương đương với lật đổ trước đó tất cả thành quả tu luyện."),t("br"),t("br"),n._v("Khổng sư môn đồ thiên hạ, nổi danh người ba ngàn, không một người vượt qua, đạt tới tới tương đồng thành tựu cũng không có một cái, từ đó có thể biết độ khó."),t("br"),t("br"),n._v("“Ta hiểu, ta là thiên địa công nhận danh sư, tương đương với lấy thiên địa làm sư, vì lẽ đó... Muốn lĩnh ngộ ra vượt qua Thiên đạo pháp tắc, gần như không có khả năng! Đã như vậy... Ta vì sao muốn nhận thiên địa vi sư?”"),t("br"),t("br"),n._v("Chấn động trong lòng, Trương Huyền giật mình, cả người thể hồ quán đỉnh: “Nhận ngươi vi sư, không cho phép ta đột phá, vậy ta liền làm... Thiên địa lão sư!”"),t("br"),t("br"),n._v("Đột phá Cổ Thánh, mượn thương đạo chân giải, đao đạo chân giải, thậm chí quyền đạo chân giải, đều có thể làm đến, có điều, một khi hoàn thành, lực lượng liền dừng lại, lại nghĩ lấy hắn phương diện, tìm con đường thuộc về mình, gần như không có khả năng."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, Trương Huyền một mực tại tìm kiếm thứ thuộc về chính mình, không ngừng áp chế tu vi, hơn hai tháng đều không có xung kích."),t("br"),t("br"),n._v("Mà bây giờ, hiểu ra trong lòng, rốt cục có phương hướng."),t("br"),t("br"),n._v("Thân phận của hắn là trời nhận danh sư, thu hoạch được thiên địa công nhận... Cũng liền chứng minh, thiên địa là có linh!"),t("br"),t("br"),n._v("Đã như vậy, vì sao muốn để nó công nhận, mà không phải, để nó bái sư?"),t("br"),t("br"),n._v("Lấy thiên địa làm sư, cuối cùng muốn giam cầm tại thiên địa xuống, mãi mãi cũng không cách nào vượt qua, mà làm thiên địa sư, vừa bắt đầu liền đứng ở cực điểm, Cổ Thánh cũng liền có thể ung dung có thể bước ra."),t("br"),t("br"),n._v("“Bất quá, muốn vì thiên địa sư, cũng không có đơn giản như vậy, cần hiểu rõ thiên địa, lĩnh ngộ thiên địa, biết nó tốt thiếu sót, chỉ có như vậy, mới có thể hoàn thành thụ nghiệp... Đương nhiên càng quan trọng hơn là, từ bỏ trời nhận danh sư thân phận!”"),t("br"),t("br"),n._v("Trong đầu nhanh chóng vận chuyển, rất nhanh xác định phương hướng, lông mày nâng lên, trong cơ thể trời nhận danh sư lực lượng, lập tức phóng thích mà ra."),t("br"),t("br"),n._v("Nghĩ đến, liền muốn trước có sai lầm."),t("br"),n._v("Muốn chứa hoàng kim, phải đem trong túi bạc ném đi."),t("br"),t("br"),n._v("Trời nhận danh sư, đối với người khác tới nói, không ngừng hâm mộ, đối với hắn mà nói, bỏ qua liền bỏ qua, không tính là gì!"),t("br"),t("br"),n._v("Hắn dựa vào là cố gắng, phấn đấu, ưu tú phẩm cách, cùng bất khuất nội tâm, trời nhận danh sư... Ha ha, mây trôi mà thôi!"),t("br"),t("br"),n._v("Tâm cảnh trầm ổn, năm lần trời nhận danh sư hấp thụ lực lượng, trong nháy mắt hướng ra phía ngoài toả ra, một cỗ mạnh mẽ khí tức, bắn ra, dọc theo mặt đất liền xông lên phía trên đi, bay thẳng Thiên Đình, một cái nháy mắt, trên không trung nổ tung, hóa thành hàng tỉ khí lưu, hướng bốn phía tán loạn."),t("br"),t("br"),n._v("đọc truyện ở //truyencuatu"),t("br"),n._v("i.net/ Trời nhận danh sư, thiên địa công nhận, kèm theo lực lượng toả ra, Trương Huyền như là thiên địa hóa thân, ý niệm huyền phù bầu trời, thừa nhận Thiên đạo ép cùng lôi kéo, hình như bất cứ lúc nào cũng sẽ vỡ vụn."),t("br"),t("br"),n._v("..."),t("br"),t("br"),n._v("Bạch Giang đế quốc Danh Sư đường."),t("br"),t("br"),n._v("“Sư chi đạo, truyền ngôn thân hành tâm giáo, Khổng sư sở dĩ có thể truyền thụ thiên hạ, là hữu giáo vô loại đức tính, thiên địa là công lòng dạ, làm danh sư, các ngươi cũng hẳn là lấy truyền đạo học nghề là bản thân nhớ rõ trách nhiệm, đem nhân loại lực lượng, truyền thừa tiếp, vạn cổ bất diệt...”"),t("br"),t("br"),n._v("Phong Sư điện phía trước, mới khảo hạch thông qua một nhóm danh sư, đứng tại trước điện, một cái lão giả nhìn quanh một vòng, vẻ mặt nghiêm túc."),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Rất nhiều danh sư đồng thời gật đầu."),t("br"),t("br"),n._v("Trở thành danh sư một khắc, bọn họ liền hiểu bản thân trách nhiệm, không cho phép lui bước."),t("br"),t("br"),n._v("Vài vạn năm đến, Danh Sư đường, có người sùng bái, có người nhục mạ, nhưng vẫn như cũ có thể truyền thừa lâu như vậy sừng sững đại lục chi đỉnh, dựa vào là chính là vô số hậu nhân, thủ vững không đổi nội tâm."),t("br"),t("br"),n._v("“Rất tốt, hiện tại bắt đầu tế bái tiên tổ...”"),t("br"),t("br"),n._v("Thoả mãn gật gật đầu, lão giả vừa định tránh ra vị trí, để cái này tân nhiệm danh sư, tế bái Phong Sư điện bài vị, liền cảm thấy mặt đất không ngừng lay động, từng cái đứng sừng sững không biết bao nhiêu năm bài vị, bắt đầu đung đưa."),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Đồng loạt ngã xuống, đồng thời đối mặt một cái phương hướng."),t("br"),t("br"),n._v("“Chuyện gì xảy ra?”"),t("br"),t("br"),n._v("Lão giả đồng tử co rụt lại, vội vàng hướng bài vị quỳ gối phương hướng nhìn lại, ngay sau đó nhìn thấy một cái do đám mây cùng cầu vồng hội tụ bóng lưng, người mặc trường bào đứng sừng sững ở không biết nhiều nơi xa xôi, đỉnh thiên lập địa, không nhìn thấy phần cuối."),t("br"),t("br"),n._v("Đối phương cũng không trong chớp mắt, nhưng toả ra một loại, không dám nhìn thẳng, để cho người ta tùy thời đều muốn quỳ gối khí thế."),t("br"),t("br"),n._v("“Là... Trời nhận danh sư!”"),t("br"),t("br"),n._v("Chấn động trong lòng, lão giả thân thể cứng ngắc."),t("br"),t("br"),n._v("Vạn cổ đến nay, một vị duy nhất trời nhận danh sư, là Khổng sư!"),t("br"),t("br"),n._v("Về sau, vô số danh sư, lại thế nào cố gắng, đều không thể làm được, vốn cho rằng, loại trừ hắn lại không người có thể hoàn thành, không nghĩ tới, lúc này, lại có người hoàn thành."),t("br"),t("br"),n._v("Phù phù!"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, quỳ rạp xuống đất."),t("br"),t("br"),n._v("“Trời nhận danh sư xuất thế, nhân tộc đại hưng, còn không mau một chút quỳ gối!”"),t("br"),t("br"),n._v("Nhìn về phía còn đang ngẩn người rất nhiều tân nhiệm danh sư, một tiếng quát tháo."),t("br"),t("br"),n._v("“Vâng!”"),t("br"),t("br"),n._v("Mọi người lúc này mới phản ứng lại, đồng thời quỳ gối."),t("br"),t("br"),n._v("Trời nhận danh sư, trước đó chỉ lưu truyền tại rất số ít Danh Sư đường, mà giờ khắc này, lại bị hàng tỉ người đồng thời tận mắt nhìn thấy."),t("br"),t("br"),n._v("Người ở ngoài xa ảnh, đầu lâu cắm đến trong đám mây, đội Thượng Thương, chân đạp đại địa, thiên địa tới gần gũi, lại bị hắn một mặt ghét bỏ đẩy ra."),t("br"),t("br"),n._v("Lại là tại ghét bỏ... Ông trời!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1876"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1878"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);