(window.webpackJsonp=window.webpackJsonp||[]).push([[99],{453:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1066-ban-đo-dung-hop"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1066-ban-đo-dung-hop"}},[n._v("#")]),n._v(" Chương 1066: Bản đồ dung hợp")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("Chương 1066: Bản đồ dung hợp"),t("br"),t("br"),n._v("Thanh Điền nhất mạch thập đại vương giả, mỗi một cái đều đạt đến Thánh vực tứ trọng, có thể một mình đảm đương một phía, bất kỳ cái gì một cái đặt vào Hồng Viễn đế quốc, thậm chí Thanh Nguyên đế quốc, đều có thể gây nên một hồi gió tanh mưa máu, để vô số nhân loại sinh linh đồ thán."),t("br"),t("br"),n._v("Vốn cho rằng chỉ cần bọn họ ra tay, cái này di tích dễ như trở bàn tay, thậm chí Thanh Nguyên phong hào đế quốc, đều sẽ bởi vậy bị thương nặng, vô số danh sư vẫn lạc..."),t("br"),t("br"),n._v("Kết quả... Bát đại vương giả phái đi ra, bọt nước đều không có một đóa, liền toàn đàn bị tiêu diệt, vốn cho rằng là Danh Sư đường hoặc là Chiến sư đường tổng bộ, phái người tới, mới để cho bọn họ ngay cả chạy trốn đi cơ hội đều không có... Ai biết đều bị như vậy một cái chỉ có Tòng Thánh gia hỏa giết chết..."),t("br"),t("br"),n._v("Càng nghĩ càng thấy đến buồn bực, càng nghĩ càng thấy đến muốn làm chuyện điên rồ."),t("br"),t("br"),n._v("May mắn Thanh Điền Hoàng không biết, nếu như biết tin tức này, có thể hay không tại chỗ tức ngất đi?"),t("br"),t("br"),n._v("Cái gì thù cái gì oán?"),t("br"),t("br"),n._v("Nhưng phải sức lực đối với chúng ta thập đại vương giả họa họa?"),t("br"),t("br"),n._v("Ta không muốn tìm cái gì di tích, ta muốn về nhà..."),t("br"),t("br"),n._v("Trong lòng tràn đầy buồn bực, có điều, lúc này căn bản cũng không cho hắn buồn bực thời gian."),t("br"),t("br"),n._v("Trận pháp này đại bộ phận đều là do hắn bố trí, đã đạt đến cấp bảy đỉnh phong, uy lực vô cùng, cho dù thủ đoạn hắn rất nhiều, bây giờ bị khốn trong đó cũng không có biện pháp gì, chỉ có bị động bị đánh phần."),t("br"),t("br"),n._v("Mới vừa tránh né mấy lần công kích, nghĩ biện pháp theo trong trận pháp chạy đi, liền nghe đến bầu trời thanh niên thanh âm vang lên."),t("br"),t("br"),n._v("“Hàn hội trưởng, ngươi tới vận chuyển trận pháp, mau chóng đem cái tên này giết...”"),t("br"),t("br"),n._v("Tiếng nói kết thúc, trận pháp giống như nhận được càng đại lực hơn lượng khởi động, uy lực lần nữa tăng nhiều."),t("br"),t("br"),n._v("“Ngươi...”"),t("br"),t("br"),n._v("Hồng Diệp Vương vẻ mặt trở nên trắng bệch."),t("br"),t("br"),n._v("Trận pháp này mặc dù là hắn bố trí, nhưng cũng không biết, cụ thể loại hình, muốn nghiên cứu ra được, tìm tới sinh môn, ít nhất phải nửa canh giờ thời gian."),t("br"),t("br"),n._v("Đối phương chỉ có Tòng Thánh thực lực, khởi động trận pháp cố hết sức, liều mạng bị thương, hắn còn có thể kiên trì... Thế nhưng là khởi động trận pháp nếu như là Hàn Tự, liền thật chống lại không được."),t("br"),t("br"),n._v("Cái tên này bản thân liền đạt đến Thánh vực tứ trọng đỉnh phong, càng là trận pháp sư công hội hội trưởng, đối với trận pháp nghiên cứu mười phần thông suốt, do hắn khởi động uy lực càng lớn, xem ra chạy trốn, đã biến thành không thể nào!"),t("br"),t("br"),n._v("Hồng Diệp Vương tuyệt vọng, trên trận pháp mặt, Hàn hội trưởng đám người con mắt tỏa ánh sáng."),t("br"),t("br"),n._v("Nghe được tiếng la, bọn họ theo giấu địa phương đi ra, lúc này mới phát hiện Hồng Diệp Vương đã bị vây ở một cái lợi hại sát trận bên trong!"),t("br"),t("br"),n._v("Không cần làm bất kỳ động tác dư thừa nào, chỉ cần vận chuyển sát trận, liền nhất định có thể đem hắn tươi sống chém giết."),t("br"),t("br"),n._v("“Trương sư... Trận pháp này nếu như không nhìn lầm, hẳn là cấp bảy đỉnh phong a? Đây là... Ngươi bố trí đi ra?”"),t("br"),t("br"),n._v("Một bên vận chuyển trận pháp, Hàn hội trưởng một bên nhịn không được nhìn lại."),t("br"),t("br"),n._v("Bọn họ vừa rồi giấu đi, cũng không biết Trương Huyền như thế nào đối phó Hồng Diệp Vương."),t("br"),t("br"),n._v("Cái này sát trận, đã đạt đến cấp bảy đỉnh phong, liền xem như hắn, muốn bố trí đi ra, đều cần tiêu tốn cực lớn đánh đổi cùng thời gian, vị này Trương sư chỉ là Tòng Thánh thôi, làm sao có thể nhanh như vậy liền hoàn thành?"),t("br"),t("br"),n._v("“Không phải ta bố trí, là Hồng Diệp Vương...” Trương Huyền dao lắc đầu, không dám giành công."),t("br"),t("br"),n._v("“Hồng Diệp Vương?” Mọi người tất cả đều trợn mắt hốc mồm."),t("br"),t("br"),n._v("Bản thân bố trí cấm pháp, đem bản thân khốn đi vào... Cái tên này có bị bệnh không?"),t("br"),t("br"),n._v("“Khả năng hắn nghĩ quẩn, muốn tự sát đi... Ngươi cũng biết Dị Linh tộc nhân đầu óc cũng không quá dùng tốt...” Trương Huyền cười lắc đầu."),t("br"),t("br"),n._v("Lừa dối Hồng Diệp Vương mắc lừa sự tình, không thể nhiều lời, nếu không, ngụy trang Tử Diệp Vương sự tình, cũng không tốt lắm giải thích..."),t("br"),t("br"),n._v("Để Hàn hội trưởng cùng Ngô sư phối hợp với vận chuyển trận pháp, chém giết phía dưới Hồng Diệp Vương, Trương Huyền thì đến đến mọi người sau lưng, tìm cái địa phương khoanh chân ngồi xuống, ý thức đắm chìm trong thư viện, nhìn là nghiên cứu theo Hồng Diệp Vương trong tay lấy được quyển kia di tích giới thiệu."),t("br"),t("br"),n._v("Quyển sách này, thông thiên dùng đúng phương trong miệng nói tới “Cổ Thần ngữ” viết, hắn một cái đều nhận không ra, loáng thoáng đó có thể thấy được, cùng trước đó Ngô Dương Tử trên bản đồ rất nhiều văn tự, cực kỳ tương tự."),t("br"),t("br"),n._v("Cổ tay khẽ đảo, một kiện đồ vật xuất hiện tại lòng bàn tay."),t("br"),t("br"),n._v("Là một tấm cổ xưa da dê, không biết đã bao nhiêu năm, lít nha lít nhít dùng một loại cổ quái văn tự ghi chú ra một phần bản đồ."),t("br"),t("br"),n._v("“Phía trên này địa hình cùng nơi này không có bất kỳ cái gì chỗ tương tự ah...”"),t("br"),t("br"),n._v("Cúi đầu nhìn một cái, Trương Huyền tràn đầy kỳ quái."),t("br"),t("br"),n._v("Trên da cừu khắc hoạ kiểu chữ, hắn mặc dù không nhận ra, nhưng mà địa hình địa vật, lại là có thể thấy rõ ràng... Cùng nơi này không có một chút chỗ tương tự."),t("br"),t("br"),n._v("Nguyên nhân chính là như vậy, hắn mới không nghĩ tới, cái này bản đồ, sẽ cùng chỗ này di tích liên quan đến."),t("br"),t("br"),n._v("“Cái này bản đồ khoảng cách hiện tại có chừng mấy vạn năm... Chẳng lẽ vẽ thời điểm là loại địa hình này, mà bây giờ đã thương hải tang điền, biến thành mặt khác một bộ dáng?” Trong lòng hơi động, Trương Huyền suy đoán."),t("br"),t("br"),n._v("Đại lục, địa hình cũng không phải là đã hình thành thì không thay đổi, mà là sẽ nương theo thời gian trôi qua tiến hành biến thiên, có phải hay không là bởi vì thời gian lâu dài, địa hình thay đổi, mới đưa đến bản đồ cùng nơi này hoàn toàn khác biệt?"),t("br"),t("br"),n._v("“Không đúng... Nơi này là không gian gấp, không có đất vỏ chuyển động, cũng không có nước chảy xâm nhập, làm sao sẽ biến hóa?”"),t("br"),t("br"),n._v("Ý nghĩ này duy trì không bao lâu, Trương Huyền liền lắc đầu."),t("br"),t("br"),n._v("Không gian gấp cùng ngoại giới khác biệt, không có đủ loại thiên nhiên biến hóa, hơn nữa nơi này đâu đâu cũng có trận pháp, cho dù biến hóa, cũng không có khả năng như thế hoàn toàn, không có một chỗ tương đồng."),t("br"),t("br"),n._v("“Ừm...”"),t("br"),t("br"),n._v("Đang tại kỳ quái, đột nhiên phát hiện, trong tay thư tịch cùng trước mắt da dê, tựa hồ có chút chỗ tương tự."),t("br"),t("br"),n._v("Ngón tay khẽ động, đem hai dạng đồ vật đặt ở cùng một chỗ."),t("br"),n._v(" Ông!"),t("br"),t("br"),n._v("Một tiếng kêu khẽ, bản đồ cùng giới thiệu chậm rãi tới gần, “Phần phật!” Thoáng cái, dung hợp lại cùng nhau, thời gian nháy mắt, tạo thành một cái mới tinh quyển da cừu bản đồ."),t("br"),t("br"),n._v("Trương Huyền giật nảy mình: “Cái này... Lại là cùng một cái bản đồ một phân thành hai?”"),t("br"),t("br"),n._v("Khó trách đơn độc giới thiệu xem không hiểu, đơn độc bản đồ cũng không rõ ràng cho lắm, nguyên lai đều chỉ là hoàn chỉnh bản đồ một bộ phận."),t("br"),t("br"),n._v("Vội vàng đem dung hợp sau quyển da cừu cầm lên, cúi đầu nhìn sang."),t("br"),t("br"),n._v("Chỉ thấy phía trên cùng vừa rồi nhìn thấy đã hoàn toàn khác biệt, là mặt khác một bức tình cảnh."),t("br"),t("br"),n._v("“Đây có phải hay không là chúng ta vừa rồi khi đi tới dung nham? Đây là lôi đình chi hải, đây là Hắc Yên Trần Sa?”"),t("br"),t("br"),n._v("Phùng Huân chẳng biết lúc nào đi tới sau lưng, chỉ vào trên bản đồ xuất hiện nội dung, nhịn không được lên tiếng."),t("br"),t("br"),n._v("“Ừm!”"),t("br"),t("br"),n._v("Không cần hắn nói, Trương Huyền cũng nhận ra được, dung hợp sau quyển da cừu, đem cái này di tích tất cả nội dung toàn bộ thư hoạ ở bên trong, sinh động như thật, thậm chí trong địa đồ tình cảnh đều tựa như đang sống, tự động vận chuyển, hình như bất cứ lúc nào cũng sẽ nhảy ra."),t("br"),t("br"),n._v("“Sợi tơ hồng này chẳng lẽ là... Chính xác con đường? Thế mà còn có con đường này, chúng ta làm sao trước đó chưa hề phát hiện qua?”"),t("br"),t("br"),n._v("Phùng Huân tiếp lấy kêu lên."),t("br"),t("br"),n._v("Nghe lời này, Trương Huyền nhìn sang, quả nhiên thấy một đầu màu đỏ nhạt đường cong, theo dung nham bắt đầu, xuyên qua cả trương bản vẽ."),t("br"),t("br"),n._v("“Cái này đường nét quả thực là chính xác con đường, chẳng qua không có cái này quyển da cừu lời nói, căn bản liền không biết xuất hiện...” Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Nếu như đi vào di tích phía trước, liền có thứ này, hoàn toàn có thể dựa theo đường dây này bên trên ghi rõ lộ tuyến tiến lên, một đường không trở ngại, sẽ không gặp phải bất kỳ nguy hiểm nào, sở dĩ không phát hiện con đường này, cũng không phải là bọn họ nhãn lực không tốt, mà là chưa hoàn chỉnh bản bản đồ, con đường này căn bản sẽ không xuất hiện."),t("br"),t("br"),n._v("Đồng dạng dính dáng không gian bên trên bí ẩn, không phải hắn giờ phút này có thể lý giải."),t("br"),t("br"),n._v("Dọc theo bản đồ nhìn xem đến, rất nhanh liền thấy được Thiên cung vị trí."),t("br"),t("br"),n._v("“Nguyên lai di tích chỗ sâu nhất, ngay tại cái này phía sau thác nước...” Rất nhanh xác nhận phương hướng cùng con đường, Trương Huyền thở phào nhẹ nhõm, đứng dậy, nhìn về phía Hàn hội trưởng đám người."),t("br"),t("br"),n._v("Hàn hội trưởng cùng Ngô sư hai đại Thánh vực tứ trọng cường giả liên thủ, lại phối hợp Thất Tinh đỉnh phong cấp bậc sát trận, Hồng Diệp Vương mặc dù mạnh mẽ, vẫn không có chịu đựng, giờ phút này đã bị cứ thế mà chém giết."),t("br"),t("br"),n._v("Nếu như nói trước đó Hỏa Diệp vương bị Kim Nguyên đỉnh tại chỗ đập chết, liền đủ biệt khuất, hắn khẳng định còn hơn."),t("br"),t("br"),n._v("Bị bản thân bố trí trận pháp cứ thế mà giết chết, đường đường thứ hai vương giả, quả thực rối tinh rối mù."),t("br"),t("br"),n._v("“Đi thôi, có bản đồ, phía sau liền an toàn...”"),t("br"),t("br"),n._v("Giải quyết Hồng Diệp Vương cái này tai hoạ ngầm, mọi người thở phào nhẹ nhõm, Trương Huyền cười cười, bàn tay nâng lên lòng bàn tay quyển da cừu liền bay lên, trong nháy mắt hóa thành một đầu cầu vồng cầu nối, vượt qua Thiên cung hướng thác nước chỗ sâu lan tràn mà đi."),t("br"),t("br"),n._v("“Đi thôi!”"),t("br"),t("br"),n._v("Nói một tiếng, đi đầu đạp vào cầu nối, mới vừa đi mấy bước, nhớ tới cái gì bàn tay một trảo, đem phía dưới sát trận trận kỳ thu vào nhẫn, đồng thời nhẹ nhàng điểm một cái, Hồng Diệp Vương trên đầu ngón tay trữ vật giới chỉ, liền rơi vào lòng bàn tay."),t("br"),t("br"),n._v("Đường đường Thanh Điền nhất mạch thứ hai vương giả, trong tay đồ tốt khẳng định không ít, không cần thì phí."),t("br"),t("br"),n._v("Dọc theo cầu nối một đường hướng về phía trước, rất nhanh liền thông qua được trước đó đi không ra được Thiên cung, ngay sau đó đi vào thác nước."),t("br"),t("br"),n._v("Vừa rồi bọn họ đã đã nhìn ra, cái này thác nước cùng Thiên cung trên thực tế là một cái cực lớn bức tranh, che chắn cái nào đó thế giới, xuyên qua thác nước, tình cảnh một đổi, lập tức đi vào một tòa cự đại cung điện."),t("br"),t("br"),n._v("“Thật là nồng nặc linh khí...”"),t("br"),t("br"),n._v("Vừa tiến vào trong, lập tức cảm thấy toàn thân lỗ chân lông mở rộng, chân khí nhảy loạn."),t("br"),t("br"),n._v("Bên trong tòa cung điện này, linh khí nồng đậm tới cực điểm, tựa như bất cứ lúc nào cũng sẽ ngưng kết thành nước, ở đây tu luyện, cho dù không cần lên phẩm linh thạch, cũng có thể làm cho người tu vi tấn cấp tốc độ tăng nhiều!"),t("br"),t("br"),n._v("“Cái kia... Là linh thạch điêu khắc?”"),t("br"),t("br"),n._v("Đi về phía trước mấy bước, mọi người hô hấp lập tức dồn dập lên."),t("br"),t("br"),n._v("Chỉ thấy trước mắt một cái cực lớn bia đá, óng ánh long lanh, toả ra hào quang chói sáng, lại là dùng nguyên một khối linh thạch thượng phẩm điêu khắc."),t("br"),t("br"),n._v("“Trên thế giới còn có như thế khối lớn linh thạch?” Tất cả mọi người nuốt ngụm nước bọt, ngay cả Trương Huyền, đều con mắt trợn tròn, tròng mắt sắp rơi trên mặt đất."),t("br"),t("br"),n._v("Trước mắt cái này bia đá, cao tới năm, sáu mét, rộng hai, ba mét, độ dày cũng có hơn nửa thước, to lớn như vậy đồ vật, vậy mà toàn bộ dùng thượng phẩm linh thạch điêu khắc mà thành, cái này có phần quá mức dọa người rồi."),t("br"),t("br"),n._v("Hắn gặp qua lớn nhất linh thạch thượng phẩm, cũng chỉ to bằng nắm đấm trẻ con, cái này chừng hơn trăm tấn..."),t("br"),t("br"),n._v("Thật muốn chia cắt thành từng khối linh thạch thượng phẩm lời nói, phải có bao nhiêu ít khối?"),t("br"),t("br"),n._v("Mấy chục vạn? Mấy trăm vạn?"),t("br"),t("br"),n._v("Lúc này mới vừa tiến vào di tích chỗ sâu liền thấy cái này, bên trong đến cùng có bao nhiêu bảo vật?"),t("br"),t("br"),n._v("“Khưu Ngô cung... Nơi này chẳng lẽ là Khưu Ngô cổ thánh lưu lại di tích?”"),t("br"),t("br"),n._v("Thấy rõ ràng trên tấm bia đá điêu khắc chữ viết, Ngô sư một mặt kinh ngạc."),t("br"),t("br"),n._v("Trương Huyền lúc này cũng mới chú ý, trên tấm bia đá quả nhiên điêu khắc ba chữ to “Khưu Ngô cung!”, kiểu chữ cùng trước đó Hành Giả Vô Cương bí tịch bên trên, giống nhau như đúc."),t("br"),t("br"),n._v("“Nhìn tới... Cái này đích xác là Khưu Ngô cổ thánh lưu lại...”"),t("br"),t("br"),n._v("Quay đầu nhìn một cái Lạc Nhược Hi, hai người đồng thời nhẹ gật đầu."),t("br"),t("br"),n._v("Trước đó chỉ là đoán, hiện tại đã có thể trăm phần trăm khẳng định."),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1074"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1076"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);