(window.webpackJsonp=window.webpackJsonp||[]).push([[994],{1349:function(n,h,t){"use strict";t.r(h);var g=t(44),i=Object(g.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1869-thon-hoa"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1869-thon-hoa"}},[n._v("#")]),n._v(" Chương 1869: Thôn hoa")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Những người này, người mặc áo đuôi ngắn ngắn tay, thoạt nhìn hết sức bình thường, trong tay cầm cuốc, lưỡi hái, thuổng sắt các loại làm nông đồ vật, khí thế hùng hổ."),t("br"),t("br"),n._v("“Thực lực của bọn hắn...”"),t("br"),t("br"),n._v("Trịnh Dương sững sờ."),t("br"),t("br"),n._v("Những người trước mắt này, phổ thông không thể tại bình thường, tại danh sư đại lục, liền võ giả cũng không phải, mà trước mắt những này, vậy mà từng cái đều đạt đến cấp bậc tông sư."),t("br"),t("br"),n._v("“Bọn họ không có tu luyện qua, trong cơ thể không có chân khí dấu vết...”"),t("br"),t("br"),n._v("Ngụy Như Yên cũng có chút kinh ngạc."),t("br"),t("br"),n._v("Lấy nàng nhãn lực, có thể tuỳ tiện nhìn ra những người này, thân thể nặng nề, trong đan điền không có chân khí, hẳn là chưa hề tiếp xúc qua tu luyện."),t("br"),t("br"),n._v("Không có rèn luyện linh lực, không có thuế biến chân khí, càng không rèn luyện qua thân thể, liền có cấp bậc tông sư khí tức..."),t("br"),t("br"),n._v("“Hẳn là quanh năm dùng Thanh Nha mạch, thể chất có thay đổi, cùng Dị Linh tộc nhân đồng dạng, không giống với nhân loại bình thường...”"),t("br"),t("br"),n._v("Trương Huyền giải thích."),t("br"),t("br"),n._v("ngantruyen.com"),t("br"),n._v("Xuất hiện loại tình huống này, có lẽ cùng những này ruộng lúa mạch liên quan đến, những người ở trước mắt, tổ tông dùng loại này thu hoạch, thể chất năm này tháng nọ, đạt được triệt để thay đổi."),t("br"),t("br"),n._v("Cùng Dị Linh tộc nhân đồng dạng, vừa ra đời, dù là không tu luyện cũng nắm giữ Hóa Phàm cảnh thực lực."),t("br"),t("br"),n._v("Liền giống như hắn tiền thân, vị kia Trương gia tiểu thiên tài, trời sinh Thánh giả, vừa ra đời liền đạt đến Thánh vực..."),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Trịnh Dương con mắt sáng lên."),t("br"),t("br"),n._v("Trước đó, lão sư nói, còn không chút nào để ý, giờ phút này nhìn thấy những người bình thường này, đều có loại lực lượng này, nhất thời tràn đầy hưng phấn."),t("br"),t("br"),n._v("Nếu như danh sư đại lục người, cũng có thể như vậy, lần nữa đối mặt Dị Linh tộc nhân, thật sự không sợ hãi."),t("br"),t("br"),n._v("Mạnh hơn danh sư, học sinh không nguyện ý học, cũng không có khả năng đem hắn bồi dưỡng thành cường giả, Khổng sư ba ngàn môn đồ, đạt tới Cổ Thánh cũng chỉ có bảy mươi hai người mà thôi."),t("br"),t("br"),n._v("Mà quanh năm dùng loại thực vật này, có thể khiến người ta vừa ra đời liền nắm giữ có thể so với tông sư thực lực..."),t("br"),t("br"),n._v("Đã là người người như rồng."),t("br"),t("br"),n._v("“Các ngươi là ai, dám phá hoại hoa màu...”"),t("br"),t("br"),n._v("Mọi người đi tới trước mặt, tức giận la lên."),t("br"),t("br"),n._v("Cầm đầu là cái lão giả, tuổi tác ít nhất tám mươi, nhưng tinh thần khỏe mạnh, sợi râu cùng tóc đều đen nhánh, cho người ta một loại khí lực tràn đầy cảm giác."),t("br"),t("br"),n._v("Đồng dạng không có tu luyện, thế mà đã đạt đến Hóa Phàm phía dưới đỉnh phong nhất chí tôn cảnh!"),t("br"),t("br"),n._v("Thiên Huyền vương quốc, có thể tu luyện tới chí tôn cảnh, quốc vương Thẩm Truy cũng có thể không để vào mắt, mà ở đây, kèm theo tuổi tác tăng trưởng có thể trực tiếp đạt tới..."),t("br"),t("br"),n._v("Nói ra cũng không dám để cho người ta tin tưởng."),t("br"),t("br"),n._v("“Chúng ta không có ý định phá hoại, nguyện ý bồi thường...”"),t("br"),t("br"),n._v("Trương Huyền một mặt áy náy, cổ tay khẽ đảo, một đống dược liệu, vũ khí các loại rơi xuống."),t("br"),t("br"),n._v("Những này tuy là đều là trong tay hắn, rẻ nhất đồ vật, nhưng đặt ở bất kỳ địa phương nào, đều có giá trị không nhỏ."),t("br"),t("br"),n._v("Vốn khí thế hung hăng mọi người, thấy cảnh này, tất cả đều ngẩn ngơ."),t("br"),t("br"),n._v("Bọn họ mặc dù chưa thấy qua cái gì ra dáng bảo vật, nhưng trước mắt những vật này toả ra linh khí, vẫn có thể cảm thụ đi ra."),t("br"),t("br"),n._v("Sở dĩ xông lại, là bởi vì vừa rồi mấy người hủy hoại bọn họ vài mẫu hoa màu, nhưng hủy hoại lúa mạch, cùng những vật phẩm này so... Kém không chỉ một đoạn."),t("br"),t("br"),n._v("“Đền bù cho chúng ta?”"),t("br"),t("br"),n._v("Dừng lại một chút, lão giả vẫn như cũ khó tin."),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Trương Huyền nhẹ nhàng cười một tiếng: “Các ngươi cầm a, vừa vặn, ta còn có chuyện muốn hỏi thăm, còn hi vọng, có thể có được đáp án!”"),t("br"),n._v(" “Có chuyện gì, cứ nói đừng ngại...”"),t("br"),t("br"),n._v("Phái người đem đền bù đồ vật lắp lại, cẩn thận kiểm tra hoàn mỹ, xác định có giá trị không nhỏ, lão giả đám người vẫn như cũ cho rằng đang nằm mơ, vội vàng khom người."),t("br"),t("br"),n._v("Đã đền bù, lại bộ dáng này, thực tế sinh không được khí."),t("br"),t("br"),n._v("“Ba người chúng ta, lần đầu tiên tới nơi này, xin hỏi đây là nơi nào?” Trương Huyền đạo."),t("br"),t("br"),n._v("“Đây là Khai Minh phủ cảnh nội, Thạch Nham thôn!” Lão giả gật đầu."),t("br"),t("br"),n._v("“Thạch Nham thôn? Khai Minh phủ?” Trương Huyền nhíu nhíu mày: “Xin hỏi... Khai Minh phủ, thuộc về ở đâu?”"),t("br"),t("br"),n._v("“Là Tử Trì Cổ Thánh phủ đệ quản lý một nơi!” Một người trung niên chen vào nói."),t("br"),t("br"),n._v("“Tử Trì Cổ Thánh?”"),t("br"),t("br"),n._v("Gật gật đầu, Trương Huyền thở phào nhẹ nhõm."),t("br"),t("br"),n._v("Tử Trì Cổ Thánh là Khổng sư môn hạ bảy mươi hai hiền một trong, tuy là danh khí cùng Nhiễm Cầu, Bốc Thương những này, kém cực lớn, không tại cùng một cái cấp bậc, nhưng trong lịch sử, cũng có chỗ ghi chép."),t("br"),t("br"),n._v("Am hiểu canh tác, có lẽ... Thanh Nha mạch, là Khổng sư đưa ra lối suy nghĩ, mà gieo trồng đi ra, là công lao của hắn."),t("br"),t("br"),n._v("Đã về Tử Trì Cổ Thánh quản lý, nói cách khác, cùng đoán đồng dạng, nơi này thật là Chư Tử bách gia phạm vi."),t("br"),t("br"),n._v("Vốn nghĩ đến Thiên Huyền vương quốc về sau, cẩn thận tìm kiếm, không nghĩ tới bị Trịnh Dương đám người trong lúc vô tình phát hiện."),t("br"),t("br"),n._v("“Không biết như thế nào mới có thể đi Tử Trì Cổ Thánh phủ đệ?” Trương Huyền hỏi tiếp."),t("br"),t("br"),n._v("Vài vạn năm lịch sử, Cổ Thánh khẳng định đã sớm không còn nữa, nhưng hậu nhân khẳng định còn sống, muốn tìm kiếm Khổng sư dấu chân, tìm hắn nắm lấy “Thần Linh” về sau, đi nơi nào, nhất định phải tìm tới Chư Tử bách gia cường giả."),t("br"),t("br"),n._v("“Cổ Thánh phủ đệ tại Phàn thành, cách nơi này có chút khoảng cách, ba vị không vội vã lời nói, ta để trong thôn Tú Như mang các ngươi đi qua!”"),t("br"),t("br"),n._v("Lão giả cười cười: “Tú Như là thôn chúng ta hoa, từng đặc biệt tu hành qua, thuần phục qua một đầu linh thú, để nàng mang các ngươi đi, ngồi linh thú, tốc độ có thể nhanh một chút...”"),t("br"),t("br"),n._v("“Thôn hoa?”"),t("br"),t("br"),n._v("Trương Huyền một mặt cười khổ: “Ngươi chỉ cần nói cho chúng ta biết phương hướng là được, không cần làm phiền...”"),t("br"),t("br"),n._v("“Sao có thể gọi làm phiền, Phàn thành là thành phố lớn, không phải là cái gì người đều có thể tiến vào, nếu như không phải người nơi này, tùy tiện đi qua, rất dễ dàng bị bắt, vẫn là để Tú Như dẫn đường a, nàng hết sức quen thuộc, đi qua sau, cũng có thể thuận tiện làm việc!”"),t("br"),t("br"),n._v("Lão giả trong giọng nói mang theo không thể nghi ngờ."),t("br"),t("br"),n._v("Trước mắt mấy người làm hỏng bọn họ hoa màu, nhưng cam tâm tình nguyện đền bù, lại thái độ vô cùng tốt, bọn họ cũng tự nhiên mười phần khách khí."),t("br"),t("br"),n._v("Thấy cố chấp không qua, Trương Huyền đành phải gật gật đầu."),t("br"),t("br"),n._v("Thạch Nham thôn, là cái không lớn thôn trang, chỉ có chừng một trăm hộ, nghe được có khách đến, tất cả đều mười phần nhiệt huyết."),t("br"),t("br"),n._v("Rất nhanh, thấy được trong truyền thuyết “Thôn hoa”, dung mạo chỉ có thể nói nhìn được, cùng Tô Phi Phi không sai biệt lắm, trung thượng chi tư, nhưng ở một cái trong thôn nhỏ, quả thực lộ ra siêu quần bạt tụy."),t("br"),t("br"),n._v("“Hóa Phàm nhị trọng thực lực!”"),t("br"),t("br"),n._v("Đối phương đi tới, Trịnh Dương nhìn ra tu vi."),t("br"),t("br"),n._v("Quả thực tu luyện qua, chân khí trong cơ thể di động, cho thấy xác thực thực lực, Hóa Phàm nhị trọng."),t("br"),t("br"),n._v("“Ta có thể mang các ngươi đi Phàn thành, chẳng qua trước thời hạn dặn dò một tiếng, Tử Trì Cổ Thánh phủ đệ, không phải là cái gì người cũng có thể đi qua, thật muốn viếng thăm, cần trước thời hạn đưa lên bái thiếp, tùy tiện xông tới, đắc tội người, ta cũng mặc kệ sinh tử của các ngươi!”"),t("br"),t("br"),n._v("Đi tới trước mặt, lông mày nâng lên, Tú Như một mặt ngạo nghễ."),t("br"),t("br"),n._v("Thân là thôn hoa, có sự kiêu ngạo của chính mình cùng tôn nghiêm."),t("br"),t("br"),n._v("“Yên tâm...” Trương Huyền gật đầu."),t("br"),t("br"),n._v("Tuy là thực lực của bọn hắn, đã đạt đến phương thế giới này đỉnh phong nhất, nhưng lần này tới là tìm kiếm đáp án, cũng không phải là đánh nhau, giết người, đương nhiên sẽ không lỗ mãng."),t("br"),t("br"),n._v("“Vậy là tốt rồi, còn có, trước thời hạn cảnh cáo các ngươi một câu, ta lần này là dâng mệnh lệnh của thôn trưởng, mang các ngươi đi Phàn thành, cũng không phải là ta chủ ý, không được bởi vì cùng một chỗ đi lại, liền lặng lẽ thích ta, ta chỉ tiếp thụ trong thôn người thổ lộ, tạm thời còn không có nghĩ tới muốn gả đến ngoài thôn đi!”"),t("br"),t("br"),n._v("Tú Như một mặt nghiêm mặt."),t("br"),t("br"),n._v("“...” Trương Huyền đám người tựa như táo bón."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1880"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1882"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=i.exports}}]);