(window.webpackJsonp=window.webpackJsonp||[]).push([[996],{1352:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1871-phan-tieu-huc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1871-phan-tieu-huc"}},[n._v("#")]),n._v(" Chương 1871: Phàn Tiểu Húc")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Không đi quản kinh ngạc sắp phát điên “Thôn hoa”, Trương Huyền đám người tăng thêm tốc độ, thời gian không dài, liền thấy phía trước phi hành ba cái thanh niên."),t("br"),t("br"),n._v("Ba người này, tuy là sẽ không phá vỡ không gian phi hành, nhưng thi triển đặc thù thân pháp, tốc độ cũng là cực nhanh."),t("br"),t("br"),n._v("“Có muốn hay không ngăn lại hỏi thăm?”"),t("br"),t("br"),n._v("Trịnh Dương theo sau lưng."),t("br"),t("br"),n._v("“Không cần, coi như hỏi, bọn họ cũng biết không nhiều, theo ở phía sau là được, đến lúc đó, tự nhiên cái gì đều hiểu!”"),t("br"),t("br"),n._v("Trương Huyền lắc đầu."),t("br"),t("br"),n._v("Mấy người này là muốn đi khảo hạch Đại Nho đường, bọn họ muốn tìm đông tây, quá mức cơ mật, coi như ngăn lại, khẳng định cũng không rõ, ngược lại làm cho người biết nhiều, tin tức dễ dàng tiết ra ngoài, không bằng trước theo sau, đến Đại Nho đường lại nói."),t("br"),t("br"),n._v("Biết lão sư quyết định không có sai, Trịnh Dương không nói thêm lời."),t("br"),t("br"),n._v("Đoàn người cấp tốc phi hành, ba ngày sau, đi tới một tòa cao vút sơn mạch trước mặt, lúc này nơi này đã chất đầy người, thoạt nhìn chừng mấy ngàn."),t("br"),t("br"),n._v("Rơi trên mặt đất, Trịnh Dương đi hỏi thăm tin tức, Trương Huyền cùng Ngụy Như Yên thì nhìn bốn phía."),t("br"),t("br"),n._v("Đứng ở chỗ này mấy ngàn người, tuổi tác đều không có vượt qua ba mươi tuổi, tu vi cùng trước đó ba người đồng dạng, toàn bộ đều là Thánh vực cửu trọng, thậm chí đều có chút đạt tới nửa bước Đại Thánh."),t("br"),t("br"),n._v("Trẻ tuổi như vậy, liền nắm giữ loại thực lực này, danh sư đại lục loại trừ Trịnh Dương đám người, hầu như chưa từng thấy, tại đây lại một lần toát ra nhiều như vậy... Không hổ là Chư Tử bách gia, quả nhiên không tầm thường."),t("br"),t("br"),n._v("“Nắm giữ Cổ Thánh huyết mạch, lại thêm Khổng sư truyền thừa cùng Thanh Nha mạch, thế hệ trẻ tuổi, danh sư đại lục, quả thực tới không cách nào so sánh...”"),t("br"),t("br"),n._v("Trương Huyền âm thầm gật đầu."),t("br"),t("br"),n._v("Danh sư đại lục, thiên tài cũng không ít, nhưng có thể đạt tới Thánh vực cửu trọng, coi như tại tam đại gia tộc, cũng đều là trưởng lão cấp bậc, nhỏ tuổi nhất, đều không thua kém hai trăm tuổi."),t("br"),t("br"),n._v("Không được ba mươi liền có loại tu vi này, quả thực để cho người ta cảm thấy kinh diễm."),t("br"),t("br"),n._v("“Lão sư, bọn họ những người này, hình như đều là từ chiến đấu bên trong đi ra, so danh sư đại lục cường giả, càng thêm thích hợp chiến đấu!”"),t("br"),t("br"),n._v("Cũng nhìn ra cái gì, Ngụy Như Yên truyền âm tới."),t("br"),t("br"),n._v("Trương Huyền gật đầu."),t("br"),t("br"),n._v("Không cần nàng nói, hắn cũng nhìn ra điểm ấy."),t("br"),t("br"),n._v("Danh sư đại lục cường giả, phần lớn đều là danh sư, tuy là sức chiến đấu cực mạnh, nhưng càng thích hợp dạy học, truyền thụ kiến thức, trước mắt những người tu luyện này, từng cái trong mắt mang theo lạnh lùng, trong lòng có chút vắng lặng, cho người ta một loại chiến sư cảm giác."),t("br"),t("br"),n._v("Xem ra, bọn họ không chỉ tu vi sâu sắc, đối với chiến đấu lý giải, cũng đều từng cái không tầm thường."),t("br"),t("br"),n._v("“Lão sư, thăm dò được, Đại Nho đường cũng không ở đây, mà là ẩn giấu ở một chỗ không gian bên trong, muốn đi vào, nhất định phải trở thành một thành viên trong đó! Bọn họ những này, chính là bảy mươi hai Cổ Thánh gia tộc hậu nhân, đến đây khảo hạch!”"),t("br"),t("br"),n._v("Chỉ chốc lát, Trịnh Dương đi tới trước mặt, truyền âm tới."),t("br"),t("br"),n._v("“Trở thành một thành viên?”"),t("br"),t("br"),n._v("“Đại Nho đường nghe nói tại đặc thù trong không gian, muốn đi vào, nhất định phải đi qua trùng trùng điệp điệp khảo hạch, nếu không, bình chướng sẽ tự động đem hắn bài xích mà ra.”"),t("br"),t("br"),n._v("Trịnh Dương đạo."),t("br"),t("br"),n._v("Nhíu nhíu mày, Trương Huyền Minh Lý Chi Nhãn vận chuyển, hướng bốn phía nhìn lại, rất nhanh lắc đầu."),t("br"),t("br"),n._v("Nơi này tuy là có trận pháp, lại không phải không gian gấp các loại, xem ra, muốn lặng lẽ lẻn vào Đại Nho đường, cũng không tưởng tượng dễ dàng như vậy."),t("br"),t("br"),n._v("“Chẳng lẽ muốn ngụy trang thành người tham gia khảo hạch, trà trộn vào đi?”"),t("br"),t("br"),n._v("Tràn đầy bất đắc dĩ."),t("br"),t("br"),n._v("Nắm giữ phá toái hư không xuống, sức chiến đấu mạnh nhất, Nhan Thanh Cổ Thánh đều khó mà chống lại, nhưng muốn ngụy trang thành học sinh trà trộn vào đi... Làm sao đều cảm thấy có chút khó xử."),t("br"),t("br"),n._v("Bất quá, không làm như vậy lời nói, Đại Nho đường ở nơi nào, không rõ ràng, trong đó phong ấn rốt cuộc mạnh cỡ nào, cũng không hiểu, tùy tiện xông vào, phiền phức càng lớn."),t("br"),t("br"),n._v("Khổng sư vật lưu lại, cho dù hắn hiện tại tu vi tiến nhanh, cũng không dám tùy tiện có chỗ cử động, liền giống như trước đó bình chướng, ngộ nhỡ Đại Nho đường cũng có loại vật này, cho dù có Ngoan Nhân trợ giúp, cũng rất khó chạy trốn."),t("br"),t("br"),n._v("“Đại Nho đường nguy hiểm vô cùng, các ngươi không biết ngụy trang, không có thân phận thích hợp, liền tại phụ cận tìm địa phương ở lại, ta trở ra, có tin tức, sẽ truyền lại cho ngươi bọn họ!”"),t("br"),n._v("Dừng lại một chút, Trương Huyền nhìn về phía hai vị học sinh."),t("br"),t("br"),n._v("Hắn có Lạc Nhược Hi cho ngụy trang phù lục, ngụy trang về sau, huyết mạch đều điều tra không ra, tuyệt đối có thể ung dung lẫn vào trong đó, Trịnh Dương, Ngụy Như Yên không làm được điểm ấy, cho dù dung mạo thay đổi, một khi có Cổ Thánh cẩn thận kiểm tra, đồng dạng sẽ lộ tẩy."),t("br"),t("br"),n._v("Cùng hắn như thế, còn không bằng để cho bọn họ ở lại bên ngoài, cũng được có chỗ chăm sóc."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Biết lão sư bản lĩnh, hai người không có kiên trì, đồng thời gật gật đầu."),t("br"),t("br"),n._v("Thảo luận cố định, đưa mắt liếc ra ý qua một cái, Trịnh Dương cùng Ngụy Như Yên cười tiến lên, rất mau đem Phàn gia hai vị thanh niên dẫn ra, Trương Huyền lúc này mới đi tới trước mặt, mang theo tươi cười."),t("br"),t("br"),n._v("“Phàn Tiểu Húc ngươi khỏe!”"),t("br"),t("br"),n._v("Ba ngày bám đuôi, sớm đã biết ba người này nội tình, trước mắt vị này gọi Phàn Tiểu Húc, chính là trong ba người, cực kỳ có cơ hội đi qua khảo hạch, đi vào Đại Nho đường."),t("br"),t("br"),n._v("“Ngươi là...”"),t("br"),t("br"),n._v("Trương Huyền đám người đi theo, Phàn Tiểu Húc cho dù là Tử Trì Cổ Thánh hậu nhân, cũng khó có thể phát giác, thấy có người trực tiếp hô lên tên, tràn đầy nghi ngờ."),t("br"),t("br"),n._v("“Là như vậy, ta là phụ thân ngươi bằng hữu, là muốn cùng ngươi nói riêng một chút lần khảo hạch này kỹ xảo!”"),t("br"),t("br"),n._v("Mỉm cười, Trương Huyền đạo."),t("br"),t("br"),n._v("“Cha bằng hữu?”"),t("br"),t("br"),n._v("Phàn Tiểu Húc lộ ra nghi ngờ."),t("br"),t("br"),n._v("Phụ thân hắn chính là Tử Trì Cổ Thánh gia tộc, hiện nay tộc trưởng, ba ngày trước tiễn hắn đi ra ngoài vị kia trung niên nhân, nếu có bằng hữu lời nói, khẳng định cũng đã sớm nói, làm sao hắn một chút tin tức đều không biết."),t("br"),t("br"),n._v("“Đúng vậy a, nhiều người ở đây nhãn tạp, còn xin bên cạnh nói chuyện!” Nhìn ra đối phương nghi ngờ, Trương Huyền vẻ mặt không thay đổi."),t("br"),t("br"),n._v("“Được!”"),t("br"),t("br"),n._v("Tuy là kỳ lạ, Phàn Tiểu Húc vẫn gật đầu."),t("br"),t("br"),n._v("Không nói trước thực lực của hắn, đạt đến Thánh vực cửu trọng đỉnh phong, chỉ nói nơi này đâu đâu cũng có cao thủ, đối phương cho dù có cái gì ý đồ xấu, hẳn là cũng không dám trực tiếp động thủ."),t("br"),t("br"),n._v("Lại nói, hắn chỉ là cái không nổi danh tiểu nhân vật, cũng không có gì đáng giá mưu đồ."),t("br"),t("br"),n._v("Theo sau lưng đi tới một chỗ kiến trúc đằng sau, Phàn Tiểu Húc vừa định hỏi thăm, muốn làm gì liền thấy bàn tay của đối phương bao phủ xuống."),t("br"),t("br"),n._v("Mắt tối sầm lại, trực tiếp hôn mê bất tỉnh."),t("br"),t("br"),n._v("Trước khi hôn mê tràn đầy buồn bực... Thật là có người dám ở chỗ này động thủ ah!"),t("br"),t("br"),n._v("Đem hắn thu vào không gian gấp, Trương Huyền lúc này mới vận chuyển chân khí, thời gian nháy mắt, biến thành bộ dáng của đối phương, thậm chí liền y phục, đều giống như đúc."),t("br"),t("br"),n._v("Tuy là tới đây cái thế giới thời gian hơi ngắn, nhưng cũng biết quy luật."),t("br"),t("br"),n._v("Chư Tử bách gia người, mới có thể tu luyện, người bình thường căn bản không có công pháp, chớ nói chi là đạt tới Thánh vực cửu trọng."),t("br"),t("br"),n._v("Giả mạo bản thân là tán tu, khẳng định là không thể được, bởi vậy, chỉ có thể oan ức vị này."),t("br"),t("br"),n._v("Bất quá, đối với hắn mà nói, khẳng định cũng có chỗ tốt."),t("br"),t("br"),n._v("Chí ít thay thế đối phương khảo hạch, trăm phần trăm có thể đi qua, không cần lo lắng xuất hiện chuyện ngoài ý muốn."),t("br"),t("br"),n._v("Trở lại nơi vừa nãy, bị Trịnh Dương cùng Ngụy Như Yên lôi đi hai vị Phàn gia thanh niên, cũng đi trở về, một mặt ngỡ ngàng."),t("br"),t("br"),n._v("Hiển nhiên không có hiểu rõ, hai người kia tìm bọn hắn làm cái gì."),t("br"),t("br"),n._v("Thuận miệng lừa gạt vài câu, đợi tại nguyên chỗ, thời gian không dài, mấy cái bóng người cao lớn xuất hiện trên không trung, một cỗ nồng đậm uy áp, bao phủ bốn phía."),t("br"),t("br"),n._v("Đại Nho đường cao thủ đến rồi!"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1882"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1884"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);