(window.webpackJsonp=window.webpackJsonp||[]).push([[997],{1351:function(n,h,i){"use strict";i.r(h);var t=i(44),g=Object(t.a)({},(function(){var n=this,h=n.$createElement,i=n._self._c||h;return i("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[i("h1",{attrs:{id:"chuong-1872-khao-hach-hang-muc"}},[i("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1872-khao-hach-hang-muc"}},[n._v("#")]),n._v(" Chương 1872: Khảo hạch hạng mục")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("br"),i("br"),n._v("Hướng mấy người này nhìn lại, Trương Huyền mỉm cười."),i("br"),i("br"),n._v("Lại còn có hai cái người quen, chính là trước đó đi Trương gia, Lạc gia đi tìm phiền phức Nam Cung Nguyên Phong cùng Đạm Đài Chấn Thanh."),i("br"),i("br"),n._v("Tu vi đạt tới Cổ Thánh, phần lớn thời gian đều tại ngủ say, bình thường sự tình, đều giao cho Đại Thánh cường giả xử lý, hai người này rõ ràng tại Đại Nho đường địa vị không thấp."),i("br"),i("br"),n._v("“Chư vị đều là Cổ Thánh gia tộc ưu tú người thừa kế, có thể từ trong tộc trổ hết tài năng, đi tới nơi này, chắc hẳn bỏ ra không ít!”"),i("br"),i("br"),n._v("Cầm đầu một vị Bất Hủ cảnh cường giả đỉnh phong, nhìn quanh một vòng, thanh âm tự nhiên mà xuống: “Bất quá, tài nguyên có hạn, Đại Nho đường thu nhận tiêu chuẩn, không cần ta nói, các ngươi cũng hẳn là rõ ràng, hàng năm chỉ có một trăm cái danh ngạch, năm nay cũng sẽ không ngoại lệ.”"),i("br"),i("br"),n._v("“Lần này báo danh, hết thảy 2,143 vị, nói cách khác, hơn hai mươi vị mới có thể đi qua một cái, mười phần tàn khốc, sinh tử không phải do bản thân, nếu như lòng có sợ hãi, hiện tại liền có thể lui ra!”"),i("br"),i("br"),n._v("“Chỉ thu nhận một trăm cái?”"),i("br"),i("br"),n._v("Trương Huyền nhíu nhíu mày."),i("br"),i("br"),n._v("Có thể tới đây hơn hai ngàn người, đều là ba mươi tuổi không được liền đạt tới Thánh vực cửu trọng, tại danh sư đại lục, mỗi một cái đều kinh tài tuyệt diễm, đủ để vang danh thiên hạ, mà ở đây, lại bị đào thải nhiều như vậy, thậm chí không tính toán sinh tử..."),i("br"),i("br"),n._v("Cái khảo hạch này, có phần quá nghiêm khắc đi!"),i("br"),i("br"),n._v("“Trước khi đến, tộc trưởng không phải nói nói ư? Đại Nho đường từ trước tới nay như vậy nghiêm khắc, thật ra thì... Gia tộc không phải cũng giống nhau sao?”"),i("br"),i("br"),n._v("Thấy hắn kỳ lạ, một bên thanh niên nói."),i("br"),i("br"),n._v("Chính là Phàn gia hai người khác một trong, Phàn Tiểu Tinh, tu vi tuy là không kịp Phàn Tiểu Húc, nhưng cũng đạt đến Thánh vực cửu trọng đỉnh phong."),i("br"),i("br"),n._v("“Cái gì đồng dạng?”"),i("br"),i("br"),n._v("“Ta nói là tuyển chọn, hàng năm đều tiến hành khảo hạch, thất bại, không có tài nguyên cùng công pháp cung cấp, từ đó mất đi tu luyện tư cách... Chúng ta có thể đi đến nơi này, cái nào không trải qua vô số chiến đấu!”"),i("br"),i("br"),n._v("Cười khổ lắc đầu, Phàn Tiểu Tinh nghi ngờ nhìn qua: “Nhiều chuyện như vậy, ngươi chẳng lẽ quên đi?”"),i("br"),i("br"),n._v("“Sao có thể quên đi, chỉ là cảm xúc một câu thôi!”"),i("br"),i("br"),n._v("Cười nhạt một tiếng, Trương Huyền trong lòng phiên giang đảo hải rung động."),i("br"),i("br"),n._v("Hàng năm đều khảo hạch, thất bại không có tài nguyên cùng công pháp cung cấp... Trong gia tộc, thi hành cái này, có thể hay không quá tàn khốc chút?"),i("br"),i("br"),n._v("Không nên người người như rồng ư?"),i("br"),i("br"),n._v("Vì sao muốn chặt đứt tài nguyên tu luyện, hủy người căn cơ?"),i("br"),i("br"),n._v("“Tốt, thêm lời thừa thãi, ta cũng không nhiều lời, lần này khảo hạch cùng trước kia khác biệt, có điều, càng thêm đơn giản, dễ dàng. Phía trước sơn phong, chư vị chắc hẳn đều thấy được, đi vào trước đó, sẽ ở mỗi người các ngươi trước ngực, đeo một cái đặc thù truyền tống ngọc phù, một khi bóp nát, liền sẽ lập tức bị truyền tống đi ra, từ đó mất đi đi vào Đại Nho đường tư cách!”"),i("br"),i("br"),n._v("“Hơn hai ngàn người đồng thời đi vào sơn phong, có thể liên minh, cũng có thể đối với những khác người động thủ, chỉ cần bảo đảm bản thân không bị đào thải là được, đợi đào thải đến chỉ còn lại có một trăm người thời điểm, khảo hạch kết thúc, lưu lại, liền chờ tại đi qua, đi vào Đại Nho đường! Mà bị đào thải, đem không còn cơ hội!”"),i("br"),i("br"),n._v("Cầm đầu Bất Hủ cảnh trung niên nhân, lần nữa nói."),i("br"),i("br"),n._v("“Trực tiếp đào thải?”"),i("br"),i("br"),n._v("“Như vậy không công bằng, nếu có người am hiểu ẩn nấp, vừa tiến vào sơn phong liền nấp đi, người người cũng không tìm tới, chẳng phải không cần chiến đấu, cũng thông qua được khảo hạch?”"),i("br"),i("br"),n._v("“Có người am hiểu sắp đặt trận pháp lời nói, bố trí tốt phòng ngự, tới một cái cho rơi đài một cái, người khác làm sao so?”"),i("br"),i("br"),n._v("“Tất cả mọi người nấp đi, không đi đào thải người khác đâu?”"),i("br"),i("br"),n._v("Phía dưới lập tức vang lên thảo luận thanh âm."),i("br"),i("br"),n._v("Vốn cho rằng giống như trước đây, tiến hành khiêu chiến hoặc là tỷ thí, không nghĩ tới trực tiếp đào thải."),i("br"),i("br"),n._v("“Mọi người không cần phải gấp, đã xác định quy củ, tự nhiên không cho phép xuất hiện loại vấn đề này, Đại Nho đường sẽ phái ra đặc biệt đội chấp pháp, có người che giấu hoặc là tiêu cực biếng nhác, đồng dạng sẽ bị nắm lấy đào thải! Còn có, chỉ cho các ngươi ba canh giờ thời gian, trong khoảng thời gian này, không thể đào thải chỉ còn lại có một trăm người, tất cả mọi người không cách nào đi qua!”"),i("br"),i("br"),n._v("Bất Hủ cảnh trung niên nhân tiếp tục nói."),i("br"),i("br"),n._v("“Ba canh giờ?”"),i("br"),i("br"),n._v("“Đội chấp pháp?”"),i("br"),i("br"),n._v("Mọi người sắc mặt tất cả đều biến đổi."),i("br"),i("br"),n._v("Vốn đang mang theo may mắn tâm lý mọi người, giờ phút này tất cả đều không còn ý nghĩ."),i("br"),i("br"),n._v("Ba canh giờ, đào thải đến hơn hai ngàn người, thực tế quá mức gấp gáp, thật muốn nấp đi, không những không cách nào đi qua, làm không cẩn thận đồng dạng sẽ bị đào thải."),i("br"),i("br"),n._v("Cho nên, muốn trở thành một trăm bên trong một thành viên, chỉ có tăng thêm tốc độ, cố gắng đào thải người khác."),i("br"),n._v("“Còn có không hiểu ư?”"),i("br"),i("br"),n._v("Giải thích xong quy tắc, Bất Hủ cảnh trung niên nhân lần nữa nhìn quanh."),i("br"),i("br"),n._v("“Rõ ràng!”"),i("br"),i("br"),n._v("Mọi người đồng loạt gật đầu."),i("br"),i("br"),n._v("“Rất tốt, hiện tại chư vị lấy gia tộc thân phận, đi nhận lấy truyền tống ngọc phù!”"),i("br"),i("br"),n._v("Bất Hủ cảnh trung niên nhân vung tay lên, giữa đám người lập tức xuất hiện một cái đài cao, phía trên để mấy ngàn viên ngọc bài."),i("br"),i("br"),n._v("Một vị thanh niên đi đầu đi lên phía trước, cắn nát đầu ngón tay, một giọt máu bay đi."),i("br"),i("br"),n._v("Ông!"),i("br"),i("br"),n._v("Một tiếng kêu khẽ, trên đài cao cho thấy một nhóm chữ: Tử Kỳ Cổ Thánh huyết mạch."),i("br"),i("br"),n._v("Hô!"),i("br"),i("br"),n._v("Huyết mạch được công nhận, một cái ngọc bài bay tới, rơi vào đối phương trước ngực."),i("br"),i("br"),n._v("Ngay sau đó người thứ hai đi lên phía trước."),i("br"),i("br"),n._v("“Quả nhiên không thể đầu cơ trục lợi...”"),i("br"),i("br"),n._v("Thấy cảnh này, Trương Huyền âm thầm vui mừng."),i("br"),i("br"),n._v("May mắn hắn đã sớm đoán ra, trước thời hạn đem Phàn Tiểu Húc mê đi, ngụy trang thành bộ dáng của hắn, thật nếu để cho Trịnh Dương, Ngụy Như Yên cũng giả mạo, rất nhẹ nhàng liền sẽ bị phát giác ra được, lại nghĩ trà trộn vào đi, liền khó khăn."),i("br"),i("br"),n._v("Đi theo mọi người sau lưng, chậm rãi hướng về phía trước, thời gian không dài, đi tới trước mặt, lấy ra huyết dịch, để vào đài cao, đồng dạng đạt được một cái ngọc phù."),i("br"),i("br"),n._v("Thấy hắn huyết mạch không có vấn đề, Phàn Tiểu Tinh mấy người cũng không suy nghĩ nhiều."),i("br"),i("br"),n._v("Không được nửa canh giờ, hơn hai ngàn người, liền tất cả đều lĩnh đại biểu thân phận ngọc phù."),i("br"),i("br"),n._v("“Tốt, hiện tại đi qua đi, các ngươi có ba canh giờ thời gian!”"),i("br"),i("br"),n._v("Trên không Bất Hủ cảnh đại viên mãn cường giả hừ một tiếng, vung tay lên."),i("br"),i("br"),n._v("Rầm!"),i("br"),i("br"),n._v("Dường như có cái gì bình chướng bị mở ra, mọi người đồng loạt hướng sơn phong xông tới."),i("br"),i("br"),n._v("Trước đi qua, chẳng khác nào trước chiếm cứ địa lợi chi thế, đi qua khảo hạch tỷ lệ tăng lớn không ít."),i("br"),i("br"),n._v("Phàn Tiểu Tinh mấy người hai người vừa định xông tới, liền cảm thấy trên người xiết chặt, bị Trương Huyền kéo: “Đến sơn phong, một cái gia tộc khẳng định sẽ bị chia lìa, các ngươi trước tìm địa phương trốn, ta đi tìm các ngươi!”"),i("br"),i("br"),n._v("Đi vào sơn phong thông đạo, có truyền tống trận pháp, Trương Huyền vừa rồi liền thấy, đoán không sai, hẳn là cùng một gia tộc người, sẽ bị ngẫu nhiên phân phối đến khắp nơi, tận lực không tại cùng một chỗ."),i("br"),i("br"),n._v("Nếu không, cùng một cái gia tộc, liên hợp cùng một chỗ, nhất định chiếm cứ rất lớn ưu thế."),i("br"),i("br"),n._v("“Trốn?”"),i("br"),i("br"),n._v("Phàn Tiểu Tinh hai người nhíu nhíu mày."),i("br"),i("br"),n._v("Ba người bọn họ liên hợp cùng một chỗ, thực lực cũng không tính quá mạnh, có thể hay không sống sót cũng còn khó nói, còn không bằng riêng phần mình phân tán, không để cho người chú ý."),i("br"),i("br"),n._v("“Nghe ta, bảo đảm các ngươi đều có thể đi qua khảo hạch!” Trương Huyền thản nhiên nói."),i("br"),i("br"),n._v("Đã ngụy trang thành Phàn Tiểu Húc, tự nhiên cũng phải cấp Tử Trì Cổ Thánh hậu nhân một ít chỗ tốt."),i("br"),i("br"),n._v("“Được!”"),i("br"),i("br"),n._v("Thấy hắn như thế khẳng định, Phàn Tiểu Tinh đám người dừng lại một chút, đồng thời gật gật đầu."),i("br"),i("br"),n._v("Trương Huyền cũng không giải thích, đối phương ẩn giấu đi, bản thân như thế nào tìm đạt được bọn họ, nhẹ nhàng cười một tiếng, đi đầu bay tới đằng trước."),i("br"),i("br"),n._v("Phần phật!"),i("br"),i("br"),n._v("Trận pháp chuyển một cái, đem bao phủ, sau một khắc, đã từ biến mất tại chỗ."),i("br"),i("br"),n._v("Người đăng: ThấtDạ"),i("br"),i("br"),i("br"),i("br")]),n._v(" "),i("hr"),n._v(" "),i("p",[i("a",{attrs:{href:"./chap-1883"}},[n._v("Chương trước")]),n._v("  |  "),i("a",{attrs:{href:"./chap-1885"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);