(window.webpackJsonp=window.webpackJsonp||[]).push([[998],{1353:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1873-tan-khoc-phap-tac-sinh-ton-tet-nguyen-tieu-hanh-phuc"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1873-tan-khoc-phap-tac-sinh-ton-tet-nguyen-tieu-hanh-phuc"}},[n._v("#")]),n._v(" Chương 1873: Tàn khốc pháp tắc sinh tồn (tết nguyên tiêu hạnh phúc!)")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("br"),t("br"),n._v("Xuất hiện lần nữa thời điểm, tại một cái nham thạch đằng sau, vừa mới đứng vững thân thể, liền cảm thấy một đạo hàn mang dán chặt lấy mặt đất, thẳng tắp đâm về trước ngực ngọc bài."),t("br"),t("br"),n._v("Chỉ cần đánh trúng, sẽ bị lập tức đào thải."),t("br"),t("br"),n._v("Xem ra, phía trước tiến vào, sớm đã có người mai phục tốt, chỉ chờ kẻ đến sau sa lưới."),t("br"),t("br"),n._v("“Tốc độ rất nhanh!”"),t("br"),t("br"),n._v("Nhẹ nhàng cười một tiếng, Trương Huyền ngón tay duỗi ra, tiến lên đón."),t("br"),t("br"),n._v("Ông!"),t("br"),t("br"),n._v("Hàn mang bị ngăn tại ngón tay bên ngoài, lại không cách nào tiến lên mảy may."),t("br"),t("br"),n._v("Hắn hiện tại thi triển lực lượng, cứ việc chỉ có Thánh vực cửu trọng, vậy do mượn nhãn lực cùng phản ứng, Đại Thánh cường giả đều khó mà chống lại, trước mắt vị này kẻ đánh lén, chẳng qua phổ thông Vũ Không cảnh, làm sao có thể thành công!"),t("br"),t("br"),n._v("Chặn lại đánh lén, Trương Huyền nhìn lại, là cái hắc y thanh niên, thấy hắn như thế tuỳ tiện liền chặn lại hàn mang, ánh mắt lộ ra vẻ hoảng sợ, không dám có chiêu thứ hai, xoay người bỏ chạy."),t("br"),t("br"),n._v("Xem ra, nhìn ra vị này thực lực, không trốn đi, chỉ có thể bị đào thải."),t("br"),t("br"),n._v("“Phản ứng rất nhanh, có điều, muốn đi, không còn kịp rồi...”"),t("br"),t("br"),n._v("Lộ ra khen ngợi chi sắc, Trương Huyền ngón tay lần nữa một điểm: “Định!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Tựa như mặt nước đóng băng, thanh niên mặc áo đen lập tức cứng ngắc trên không trung, không cách nào động đậy."),t("br"),t("br"),n._v("“Không gian phong cấm...” Thanh niên mặc áo đen tràn đầy kinh hãi."),t("br"),t("br"),n._v("Thánh vực cửu trọng lĩnh ngộ loại năng lực này... Bảy mươi hai Cổ Thánh gia tộc hậu nhân, tuyệt đối hàng hơn mấy, làm sao xui xẻo như vậy, trực tiếp cho hắn gặp lên?"),t("br"),t("br"),n._v("“Vị bằng hữu này, ta bị ma quỷ ám ảnh, mới đối ngươi động thủ, ngươi người lớn có đại lượng, còn xin tha ta một mạng...”"),t("br"),t("br"),n._v("Hàm răng cắn chặt, vội vàng cầu xin tha thứ."),t("br"),t("br"),n._v("Thực lực của hắn, ở gia tộc cũng là đỉnh tiêm, vốn nghĩ, chỉ cần che giấu tốt, có thể tuỳ tiện bắt giết người tham gia khảo hạch, nằm mơ đều không có nghĩ đến, lần đầu tiên ra tay, lại đụng phải như vậy kẻ khó chơi."),t("br"),t("br"),n._v("Sớm biết như vậy, nấp đi là tốt rồi, động cái gì tay ah..."),t("br"),t("br"),n._v("“Tha cho ngươi một mạng, không phải là không thể được!” Trương Huyền đạo."),t("br"),t("br"),n._v("“Có điều kiện gì, mời nói!”"),t("br"),t("br"),n._v("Thanh niên mặc áo đen cắn răng: “Ta có thể dùng bảo vật mua lệnh, có điều, ngươi hẳn là cũng biết, gia tộc mỗi người cung cấp, đều là có định số, ta cũng không có quá thật tốt đồ vật...”"),t("br"),t("br"),n._v("“Đồ vật ta không muốn, cho ngươi hai cái lựa chọn, thứ nhất, bóp nát ngươi ngọc bài, để ngươi bị đào thải! Thứ hai, nói cho ta biết, ta muốn biết sự tình...”"),t("br"),t("br"),n._v("Trương Huyền nhìn qua: “Yên tâm, ta hỏi ngươi, không dính dáng gia tộc cơ mật, cũng sẽ không để ngươi làm khó!”"),t("br"),t("br"),n._v("“Ta lựa chọn đầu thứ hai!”"),t("br"),t("br"),n._v("Không chần chờ quá lâu, thanh niên mặc áo đen mở lời."),t("br"),t("br"),n._v("Có thể đi đến nơi này, hao phí gia tộc không biết bao nhiêu tài nguyên, nếu như vào không được Đại Nho đường, tất cả tài nguyên đều sẽ ngừng cung ứng, về sau lại nghĩ tiến bộ, hầu như không có khả năng!"),t("br"),t("br"),n._v("Bất kể như thế nào, cũng không thể từ bỏ cơ hội."),t("br"),t("br"),n._v("“Rất tốt!” Hai tay chắp sau lưng, Trương Huyền vẻ mặt lạnh nhạt nhìn qua: “Ngươi từ bắt đầu tu luyện, là như thế nào từng bước một đi đến ngày hôm nay tình trạng này? Bao quát mỗi một lần thí luyện, khảo hạch! Ta muốn biết mười phần tỉ mỉ!”"),t("br"),t("br"),n._v("“Cái này...”"),t("br"),t("br"),n._v("Còn tưởng rằng biết hỏi thăm cái gì, nghe được là cái này hầu như người người đều biết sự tình, thanh niên mặc áo đen sửng sốt một chút, tuy là tràn đầy kỳ lạ, vẫn như cũ thật lòng trả lời: “Ta từ bắt đầu tu luyện đã trải qua hai mươi bảy lần khảo hạch, hầu như hàng năm đều có một lần! Mỗi một lần khảo hạch, cùng độ tuổi, đều sẽ đào thải không ít người, bị đào thải, ngừng tài nguyên cùng công pháp cung cấp, an tâm tổ chức gia tộc xí nghiệp...”"),t("br"),t("br"),n._v("Rất nhanh, thanh niên mặc áo đen đem trải qua sự tình giải thích một lần."),t("br"),t("br"),n._v("Trương Huyền vừa nghe vừa nhíu mày."),t("br"),n._v("Cùng trước đó đoán đồng dạng, Chư Tử bách gia cạnh tranh mười phần tàn khốc, khảo hạch, từ vừa mới bắt đầu tu luyện cũng đã bắt đầu, đi qua, được đến tiếp sau công pháp và tài nguyên, không thông qua, lại không có cách nào tu luyện."),t("br"),t("br"),n._v("Đây cũng không phải là bồi dưỡng hậu bối, mà là dưỡng cổ."),t("br"),t("br"),n._v("Nghe nói sẽ dưỡng cổ người, sẽ dưỡng ra hơn vạn cái cổ trùng, sau đó bảo hắn tự giết lẫn nhau, cuối cùng thắng được, mới là lợi hại nhất."),t("br"),t("br"),n._v("Hiện tại bảy mươi hai Cổ Thánh gia tộc, dường như dùng chính là như vậy, có thể đi tới nơi này tham gia Đại Nho đường khảo hạch, không có chỗ nào không đã trải qua trùng trùng điệp điệp đau khổ, mới từ trong gia tộc, vô số người trẻ tuổi bên trong trổ hết tài năng."),t("br"),t("br"),n._v("Khó trách vừa rồi liền nhìn ra những người này, từng cái ánh mắt lạnh lùng, cùng chiến sư có chút giống nhau, có loại kinh nghiệm này, nhân tính ở trong lòng, khẳng định đã sớm lạnh lùng như là một tờ giấy lộn, tùy thời cũng có thể bỏ qua."),t("br"),t("br"),n._v("Cũng khó trách, lúc trước Nhan Tiết đám người, có thể cùng Dị Linh tộc nhân liên hợp, mà không có mảy may khúc mắc... Trong lòng bọn họ, chỉ sợ chỉ có thắng bại, sớm mất danh sư đặc hữu đạo đức thước đo."),t("br"),t("br"),n._v("“Nhìn tới, Chư Tử bách gia, mới thật sự là cường giả vi tôn, kẻ phù hợp mới có thể sinh tồn...”"),t("br"),t("br"),n._v("Trong lòng cảm xúc, lần nữa nhìn về phía thanh niên trước mắt: “Ngươi mới vừa nói, tài nguyên cung cấp có hạn ngạch, có ý tứ gì?”"),t("br"),t("br"),n._v("“Thân là gia tộc tử đệ, ngươi sẽ không biết?”"),t("br"),t("br"),n._v("Thanh niên mặc áo đen lần nữa tràn đầy nghi ngờ, chẳng qua biết hiện tại ăn nhờ ở đậu, không thể làm gì khác hơn nói: “Đạt tới cấp bậc gì, đi qua loại nào khảo hạch, phân phối bao nhiêu tài nguyên, đều là nhất định lượng, theo ta được biết, mỗi cái gia tộc đều là như vậy, ai cũng không thể ngoại lệ!”"),t("br"),t("br"),n._v("Nói xong, lại giải thích một hồi."),t("br"),t("br"),n._v("Nghe xong lời nói, Trương Huyền thở hốc vì kinh ngạc, xem như rốt cục hiểu rõ, Cổ Thánh gia tộc cụ thể đưa vào hoạt động mô thức."),t("br"),t("br"),n._v("Vừa mới bắt đầu, chọn lựa tất cả hậu bối tiến hành tu luyện, trong vòng thời gian quy định, không đạt tới chỉ định cấp bậc, sẽ bị đào thải, mất đi tài nguyên cùng công pháp cung cấp, hàng năm đều là như vậy, muốn có được công pháp và tài nguyên, lại cần tiến hành đủ loại khảo hạch, thu hoạch được thành tựu, mới có thể nắm giữ."),t("br"),t("br"),n._v("Có thể nói... Thế giới này, đối tài nguyên khống chế, chính xác để cho người ta tức lộn ruột!"),t("br"),t("br"),n._v("“Chẳng lẽ không thể đi tìm kiếm một ít di tích, hoặc là tìm kiếm bảo tàng?”"),t("br"),t("br"),n._v("Cũng nhịn không được nữa, hỏi."),t("br"),t("br"),n._v("Danh sư đại lục tu luyện giả, cũng không có nhiều như vậy tài nguyên, nhưng bọn hắn sẽ tìm tìm một ít đặc thù di tích, hoặc là tìm tầm bảo giấu, coi như trải qua nguy hiểm, chỉ khi nào thành công, đủ tu luyện thật lâu."),t("br"),t("br"),n._v("Người nơi này, vì sao không làm như vậy?"),t("br"),t("br"),n._v("“Nào có di tích cùng bảo tàng? Tu luyện giả sau khi chết, tất cả tài nguyên đều sẽ bị gia tộc lần nữa thu về! Liền giống như, ngươi bây giờ giết ta, cũng không cách nào cướp đi ta tài nguyên, bởi vì, ta lúc ra cửa, trên người mang đồ vật, mỗi một kiện đều muốn đăng ký, một khi tử vong... Sẽ đuổi tới gia tộc của ngươi, hướng ngươi truy tìm!”"),t("br"),t("br"),n._v("Thanh niên mặc áo đen nói."),t("br"),t("br"),n._v("“...”"),t("br"),t("br"),n._v("Trương Huyền lông mày nhảy dựng."),t("br"),t("br"),n._v("Chư Tử bách gia, thân là Khổng sư hậu nhân, như vậy khống chế tài nguyên... Rốt cuộc muốn làm gì?"),t("br"),t("br"),n._v("Chẳng lẽ thế giới này tài nguyên, như vậy rất thiếu?"),t("br"),t("br"),n._v("Lại hỏi thăm một hồi, đem bản thân muốn biết, đối phương giải, đều biết rõ ràng, Trương Huyền lúc này mới cong ngón tay búng một cái."),t("br"),t("br"),n._v("Hô!"),t("br"),t("br"),n._v("Thanh niên mặc áo đen ngã trên mặt đất."),t("br"),t("br"),n._v("“Ta đi rồi ngươi liền sẽ tỉnh táo, tỉnh lại, vừa rồi trải qua, sẽ toàn bộ quên đi!”"),t("br"),t("br"),n._v("Cười nhạt một tiếng, Trương Huyền bàn chân một bước, thẳng tắp vọt lên phía trước đi, thời gian nháy mắt biến mất tại nguyên chỗ."),t("br"),t("br"),n._v("Tu vi đạt tới hắn cấp bậc này, đã có thể tuỳ tiện xóa đi trí nhớ của một người, vừa rồi hỏi nhiều như vậy, tuy là cũng không trọng yếu, nhưng về sau khẳng định sẽ cho Phàn Tiểu Húc mang đến phiền phức."),t("br"),t("br"),n._v("Đã như vậy, trước thời hạn miễn trừ, cũng có thể ít chút tai hoạ ngầm."),t("br"),t("br"),n._v("(Mọi người tết nguyên tiêu hạnh phúc! Lão nhai hiện tại không sung sướng, đau gió chân đau bốn ngày, đi không được đường. Ngày mai nhạc mẫu còn muốn làm giải phẫu.)"),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1884"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1886"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);