(window.webpackJsonp=window.webpackJsonp||[]).push([[999],{1355:function(n,h,t){"use strict";t.r(h);var i=t(44),g=Object(i.a)({},(function(){var n=this,h=n.$createElement,t=n._self._c||h;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"chuong-1874-phan-tieu-phong"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#chuong-1874-phan-tieu-phong"}},[n._v("#")]),n._v(" Chương 1874: Phàn Tiểu Phong")]),n._v(" "),t("hr"),n._v(" "),t("p",[n._v("“Thần thức bao phủ!”"),t("br"),t("br"),n._v("Rời đi thanh niên mặc áo đen, Trương Huyền đi về phía trước không xa, thần thức lan tràn ra, thời gian nháy mắt liền đem cả ngọn núi đều bao phủ ở bên trong."),t("br"),t("br"),n._v("Nơi này tuy là có trận pháp hạn chế tu luyện giả thần thức dò xét, có thể bày trận người, đối với trận pháp lý giải, cũng không bằng hắn, lại thêm thực lực mạnh mẽ, thần thức lại thế nào bao phủ, cũng sẽ không bị người phát giác."),t("br"),t("br"),n._v("Rất nhanh, liền bị hắn tìm tới, hai người tung tích."),t("br"),t("br"),n._v("“Phàn Tiểu Tinh vận khí không tệ, bị truyền tống đến một chỗ trong khe núi, ẩn giấu đi, người khác không phát hiện được, Phàn Tiểu Phong liền không có may mắn như vậy...”"),t("br"),t("br"),n._v("Tử Trì Cổ Thánh gia tộc tới ba người, Phàn Tiểu Húc, Phàn Tiểu Tinh cùng Phàn Tiểu Phong."),t("br"),t("br"),n._v("Cái trước thuận lợi che giấu, tạm thời không có bị những người khác phát giác, người sau giờ phút này nghiêm chỉnh lịch nguy hiểm, bị hai vị cường giả đuổi giết."),t("br"),t("br"),n._v("May mắn hắn am hiểu tốc độ, thân pháp cũng cực nhanh, nếu không, trên người ngọc bài khẳng định sớm đã bị người bóp nát, thảm tao đào thải."),t("br"),t("br"),n._v("Lần nữa thoáng một cái, Trương Huyền thẳng tắp hướng Phàn Tiểu Phong phương hướng bay lượn mà đi, biết bốn phía khẳng định có người theo dõi, tốc độ của hắn vẫn như cũ là Thánh vực cửu trọng đỉnh phong, cũng không vượt qua."),t("br"),t("br"),n._v("Ô ô ô!"),t("br"),t("br"),n._v("Từng đạo kình khí, tại bốn phía nổ tung."),t("br"),t("br"),n._v("Người khác không làm gì được hắn, nhưng vẫn là có rất lớn cạnh tranh, thỉnh thoảng có người tại một bên chiến đấu, một cái tiếp một cái cường giả bị đào thải."),t("br"),t("br"),n._v("Không thể không nói, Đại Nho đường khảo hạch, mười phần tàn khốc."),t("br"),t("br"),n._v("Tuy là không hạn chế liên minh, nhưng vì bản thân có thể đi qua, tất cả liên hợp đều là giả, làm không cẩn thận chỉ biết sau lưng đâm đao."),t("br"),t("br"),n._v("Loại sự tình này, Trương Huyền liền thấy qua, ba người liên hợp, vốn gặp được lạc đàn người có thể nhẹ nhõm đào thải, ai biết bên trong một cái, bởi vì đồng bạn bị thương, chủ động ra tay, đem hắn cũng đào thải rơi mất."),t("br"),t("br"),n._v("Bị thương, chẳng khác nào có thiếu hụt, liên hợp không những không còn uy lực, làm không cẩn thận, sẽ còn bị người vây chặt, không bằng đơn độc một người linh hoạt."),t("br"),t("br"),n._v("Bên này nhanh chóng tiến lên, Phàn Tiểu Phong bên kia, trên đầu toát mồ hôi lạnh, không ngừng run rẩy."),t("br"),t("br"),n._v("Liên tục bị ba cái cùng cấp bậc cường giả công kích, hắn đã bị thương, lực lượng cũng tiêu hao hơn phân nửa, tràn đầy mệt mỏi."),t("br"),t("br"),n._v("“Đừng vùng vẫy, ngoan ngoãn bị đào thải... Đại Nho đường không phải như ngươi loại này thực lực, có thể tiến vào!”"),t("br"),t("br"),n._v("Một thanh niên mang trên mặt thợ săn tự tin, mang theo mỉm cười nhìn qua."),t("br"),t("br"),n._v("Phía sau hắn còn có hai cái thanh niên, hiện hình quạt đem Phàn Tiểu Phong bao vây ở bên trong."),t("br"),t("br"),n._v("“Muốn đào thải ta, liền động thủ! Coi như không tiến vào được Đại Nho đường, ta cũng sẽ cho các ngươi bị thương...”"),t("br"),t("br"),n._v("Biết không có đường lui nữa, Phàn Tiểu Phong hàm răng cắn chặt, nhìn về phía trước mắt ba người, tựa như bị thương sói đói."),t("br"),t("br"),n._v("Vận khí cũng đủ cõng, vừa xuất hiện liền bị truyền tống đến nơi đây, bị ba người vây công... Có thể kiên trì đến bây giờ, đã tận lực."),t("br"),t("br"),n._v("“Để chúng ta bị thương? Ngươi cũng xứng?”"),t("br"),t("br"),n._v("Thanh niên cầm đầu hừ nhẹ: “Động thủ!”"),t("br"),t("br"),n._v("Rầm!"),t("br"),t("br"),n._v("Kèm theo mệnh lệnh, ba người đồng loạt vây công tới, lực lượng như mưa to lật đổ, đem bốn phía toàn bộ bao phủ, trốn đều không thể chạy trốn."),t("br"),t("br"),n._v("Cầm đầu thanh niên đã đạt đến nửa bước Đại Thánh, cái khác hai cái cũng đều là Thánh vực cửu trọng đỉnh phong, Phàn Tiểu Phong cùng bất kỳ một cái nào so, đều kém một đoạn, đồng thời đối mặt ba người, lập tức luống cuống tay chân."),t("br"),t("br"),n._v("Bành!"),t("br"),t("br"),n._v("Sắc mặt trắng nhợt, nhận công kích, Phàn Tiểu Phong bay ngược mà ra, sống lưng trùng trùng điệp điệp đâm vào một cái nham thạch bên trên, thân thể có chút tiêu hao, rã rời tựa như bùn nhão."),t("br"),t("br"),n._v("“Tốt, bóp nát ngọc bài, đi tìm cái kế tiếp!”"),t("br"),t("br"),n._v("Thanh niên cầm đầu thấy hắn đã không còn sức phản kháng, bàn giao một câu."),t("br"),t("br"),n._v("“Đúng!”"),t("br"),t("br"),n._v("Bên trái thanh niên cười cười, vừa tung người đi tới trước mặt, ngón tay nâng lên, thẳng tắp hướng Phàn Tiểu Phong trước ngực ngọc bài nhấn tới."),t("br"),t("br"),n._v("Nhìn lực lượng, chỉ cần đánh trúng, nhất định bị trực tiếp đào thải."),t("br"),t("br"),n._v("“Đáng tiếc, ta không hoàn thành được gia tộc trọng thác...”"),t("br"),t("br"),n._v("Lộ ra vẻ bất đắc dĩ, biết tiếp tục tranh đấu, chỉ có thể thương thế càng nặng, Phàn Tiểu Phong đành phải bất đắc dĩ nhắm mắt lại."),t("br"),t("br"),n._v("Bất quá, đợi một hồi, cũng không cảm thấy trước ngực ngọc bài vỡ vụn, vội vàng ngẩng đầu, ngay sau đó nhìn thấy một thân ảnh chẳng biết lúc nào xuất hiện ở trước mắt, ngồi tại cách đó không xa trên một tảng đá, đùi phải nhẹ nhàng đong đưa, trong ánh mắt ẩn chứa vui vẻ."),t("br"),t("br"),n._v("Ngay sau đó, một cái thanh âm nhàn nhạt ở bên tai vang lên: “Ba người vây công, có chút không quá nói đi!”"),t("br"),n._v("“Tiểu Húc...”"),t("br"),t("br"),n._v("Phàn Tiểu Phong chấn động trong lòng."),t("br"),t("br"),n._v("Không phải người khác, chính là tộc trưởng nhi tử, Phàn Tiểu Húc!"),t("br"),t("br"),n._v("Vậy mà tại nguy cơ thời khắc, chạy tới."),t("br"),t("br"),n._v("“Lại tới cái muốn chết!”"),t("br"),t("br"),n._v("Thấy lại dám tại bọn họ vây công bên dưới cứu người, thanh niên cầm đầu cười lạnh, đưa mắt liếc ra ý qua một cái, phối hợp còn lại hai người, đem Trương Huyền vây vào giữa: “Ngươi lập tức liền sẽ biết, cái gì gọi là nói!”"),t("br"),t("br"),n._v("Phần phật!"),t("br"),t("br"),n._v("Biết thời gian quý giá, không có quá nhiều nói nhảm, ba người lao đến."),t("br"),t("br"),n._v("“Tiểu Húc, chạy mau, ngươi chỉ cần có thể đi qua khảo hạch là được, không cần phải để ý đến ta...”"),t("br"),t("br"),n._v("Thấy bị vây công, Phàn Tiểu Phong tràn đầy gấp gáp."),t("br"),t("br"),n._v("Tiểu Húc thực lực hắn biết rất rõ ràng, chỉ so với hắn mạnh một tia, đối mặt ba người này, đồng dạng không có phần thắng, cùng hắn bị đào thải, còn không bằng chạy trốn, vì Tử Trì Cổ Thánh gia tộc, giữ lại một tia hi vọng."),t("br"),t("br"),n._v("Thình thịch thình thịch!"),t("br"),t("br"),n._v("Tiếng nói còn không có kết thúc, liền nghe đến quyền cước vào thịt thanh âm vang lên, lo lắng tiếng gọi ầm ĩ, câm tại cổ họng, cũng lại không nói ra được."),t("br"),t("br"),n._v("Chỉ thấy mới vừa rồi còn khí thế hung hăng ba người, giờ phút này toàn bộ nằm trên mặt đất, trong mắt tràn đầy kinh hoàng."),t("br"),t("br"),n._v("Ngồi tại nham thạch bên trên Phàn Tiểu Húc, hình như không nhúc nhích qua đồng dạng, cười khanh khách nhìn qua, ánh mắt bên trong mang theo cổ vũ: “Tự thân đem bọn hắn đào thải đi!”"),t("br"),t("br"),n._v("“Ta?”"),t("br"),t("br"),n._v("Phàn Tiểu Phong đầu óc choáng váng."),t("br"),t("br"),n._v("Đến thời khắc này, đều không có hiểu rõ, bản thân quen thuộc vị này Tiểu Húc, là như thế nào lấy một địch ba, nhẹ nhõm đem hắn đánh bại."),t("br"),t("br"),n._v("“Bọn họ không phải muốn đào thải ngươi sao? Đã như vậy, đào thải bọn họ sự tình, tự nhiên cho ngươi...”"),t("br"),t("br"),n._v("Trương Huyền nói."),t("br"),t("br"),n._v("Đối loại thực lực này người ra tay, đã là ỷ lớn hiếp nhỏ, lại đào thải đối phương, thực tế xấu hổ."),t("br"),t("br"),n._v("Bất kể nói thế nào, cũng là muốn mặt người."),t("br"),t("br"),n._v("“Ta... Không phải là đối thủ!”"),t("br"),t("br"),n._v("Hơi đỏ mặt, Phàn Tiểu Phong cắn răng."),t("br"),t("br"),n._v("Tuy là trước mắt ba người bị thương, nhưng hắn thương thế cũng không nhẹ, cứ việc rất muốn đào thải đối phương, có thể dựa vào thực lực bản thân, vẫn là không cách nào hoàn thành."),t("br"),t("br"),n._v("“Yên tâm, ngươi dựa theo ta nói chiến đấu, đào thải ba người mà thôi, mười phần đơn giản!”"),t("br"),t("br"),n._v("Mỉm cười, Trương Huyền ánh mắt bên trong mang theo cổ vũ."),t("br"),t("br"),n._v("“Đơn giản?”"),t("br"),t("br"),n._v("Không biết đối phương tại sao lại nói như vậy, dừng lại một chút, Phàn Tiểu Phong cắn răng: “Tốt, ta thử một chút!”"),t("br"),t("br"),n._v("Dù sao hắn ở bên cạnh, nhìn thực lực, hẳn là sẽ không bị đào thải, đã như vậy, cũng liền không có gì có thể lo lắng."),t("br"),t("br"),n._v("“Ngươi không ra tay?”"),t("br"),t("br"),n._v("Cầm đầu thanh niên, đứng dậy, trong mắt mang theo kinh hoàng."),t("br"),t("br"),n._v("Đối phương một chiêu liền đem ba người bọn họ đánh bại, thực lực mạnh mẽ, thế hệ trẻ tuổi, trước đây chưa từng gặp, cùng người này đối chiến, khẳng định sẽ bị đào thải, nhưng cùng cái này bị hắn bọn họ đuổi sắp mệt lả gia hỏa chiến đấu, mười phần đơn giản."),t("br"),t("br"),n._v("“Ta sẽ không xuất thủ, các ngươi dùng hết toàn lực, nếu như có thể đem hắn đào thải, ta bỏ qua các ngươi, không làm được, vậy thì chờ lấy bị đào thải!”"),t("br"),t("br"),n._v("Trương Huyền mỉm cười."),t("br"),t("br"),n._v("“Tiểu Húc, ngươi... Điên rồi sao?”"),t("br"),t("br"),n._v("Nghe nói như thế, Phàn Tiểu Phong thân thể thoáng một cái, thiếu chút nữa ngất đi."),t("br"),t("br"),n._v("Người đăng: ThấtDạ"),t("br"),t("br"),t("br"),t("br")]),n._v(" "),t("hr"),n._v(" "),t("p",[t("a",{attrs:{href:"./chap-1885"}},[n._v("Chương trước")]),n._v("  |  "),t("a",{attrs:{href:"./chap-1887"}},[n._v("Chương sau")])])])}),[],!1,null,null,null);h.default=g.exports}}]);